import { Button } from "lively.components/buttons.js";
import { Color } from "lively.graphics/index.js";

export default class StripeButton extends Button {

  static get properties() {
    return {
      haloShadowEnabled: {
        defaultValue: true
      },
      toggleFill: {},
    }
  }

  onDrop(evt) {
    // handy shortcut to quickly assign icons
     const [m] = evt.hand.grabbedMorphs; // pick the first of the grabbed submorphs  
     if (m && m.isLabel) {
       this.label = m.textAndAttributes;
       m.remove();
     }
  }

  onHoverIn(evt) {
    super.onHoverIn(evt);
    if (this.haloShadowEnabled === false) return;
    let ss = this.dropShadow.toJson();
    ss.blur = 10;
    ss.distance = 3;
    this.animate({
      dropShadow: ss, duration: 200
    });
  }

  onHoverOut(evt) {
    super.onHoverOut(evt);
    if (this.haloShadowEnabled === false) return;
    let ss = this.dropShadow.toJson();
    ss.blur = 6;
    ss.distance = 1;
    this.animate({
      dropShadow: ss, duration: 200
    });
  }
}

