import { MorphicDBPrompt, interactivelyChosenCurrentMorphicDB, interactivelyChooseMorphicDB } from "lively.morphic/morphicdb/tools.js";

export default class PublishPartDialog extends AbstractPrompt {

  static get properties() {

    return {

      morphicDBName: {}, serverURL: {}, snapshotLocation: {},

      epiMorph: {defaultValue: true},

      commit: {},

      styleSheets: {
        initialize() {}
      },

      ui: {
        derived: true, readOnly: true, after: ["submorphs"],
        get() {
          return {
            label: this.getSubmorphNamed("save-dialog-label"),
            cancelButton: this.getSubmorphNamed("cancelButton"),
            okButton: this.getSubmorphNamed("okButton"),
            nameInput: this.getSubmorphNamed("name input"),
            nameLabel: this.getSubmorphNamed("name label"),
            tagInput: this.getSubmorphNamed("tag input"),
            description: this.getSubmorphNamed("description"),
            commitMessage: this.getSubmorphNamed("commit message"),
          }
        }
      },
    }

  }

  alignInWorld(world = this.world()) {
    world.addMorph(this);
    let {width, height} = world.visibleBounds();
    this.width = (width*1/2) - 5;
    this.height = (height*1/2) - 10
    this.center = world.visibleBounds().center();
    return this;
  }

  reset() {
    // this.reset();
    // this.openInWorld()
    this.morphicDBName = null;
    this.snapshotLocation = null;
    this.serverURL = null;

    this.commit = null;

    this.ui.nameInput.input = "";
    this.ui.tagInput.input = "";
    this.ui.description.textString = "";
    this.ui.commitMessage.textString = "";
    this.clipMode = "hidden";

    this.ui.nameInput.historyId = "lively.morphic-publish-part-names";
    connect(this.ui.cancelButton, 'fire', this, 'reject');
    connect(this.ui.okButton, 'fire', this, 'resolve');
  }

  async activate(opts) {
    // nameInput.showHistItem("prev"); // FIXME, last saved world name

    if (opts && opts.part) {
      let commit = await ensureCommitInfo(Path("metadata.commit").get(opts.part));
      this.commit = commit;
      let {name, tags, description} = commit || {};
      if (!name) name = opts.part.name;
      this.ui.nameInput.input = name;
      this.ui.tagInput.value = (tags || []).join(" ");
      this.ui.description.textString = description || "";
      this.ui.commitMessage = "";
    }

    let {name: morphicDBName, serverURL, snapshotLocation} = await interactivelyChosenCurrentMorphicDB();
    this.morphicDBName = morphicDBName;
    this.serverURL = serverURL;
    this.snapshotLocation = snapshotLocation;

    return super.activate();
  }

  reject() {
    super.reject({db: null, commit: null});
  }

  resolve() {
    let name = this.ui.nameInput.input;
    if (!name) return this.reject();
    this.remove(); // hard remove to not have save dialog in serialization
    this.ui.nameInput.acceptInput();
    let tags = this.ui.tagInput.input.split(" ").map(ea => ea.trim()).filter(Boolean),
        description = this.ui.description.textString || "no description yet",
        message = this.ui.commitMessage.textString || "no message",
        db = this.morphicDB;
    return super.resolve({db, commit: {...this.commit, name, tags, description}});
  }

  focus() {
    this.ui.nameInput.focus();
  }

  beforePublish() {
    // disconnectAll(this);
    this.reset();
  }

  onKeyDown(evt) {}

  get morphicDB() {
    let {morphicDBName, serverURL} = this;
    if (!morphicDBName) return MorphicDB.default;
    if (!serverURL) serverURL = MorphicDB.default.serverURL;
    return MorphicDB.named(morphicDBName, {serverURL})
  }

  async changeDB() {
    let {db, dbs} = await interactivelyChooseMorphicDB();
    if (!db) return;
    let {name: morphicDBName, serverURL, snapshotLocation} = db;
    this.morphicDBName = morphicDBName;
    this.serverURL = serverURL;
    this.snapshotLocation = snapshotLocation;
  }
}

