/*global fetch*/
import { Morph, easings, MorphicDB, morph } from 'lively.morphic';
import { MorphList } from "lively.components/list.js";
import { arr, promise, fun } from "lively.lang";
import { resource } from "lively.resources/index.js";

export class MasterComponentList extends MorphList {

  // TODO: change this to a list morph
// this.listItemProto.openInWorld()
  static get properties() {
    return {
      todayMarker: {},
      listItemProto: {},
    }
  }

  beforePublish() {
    this.items = [];
  }

  // async onLoad() {
  //   super.onLoad();
  //   this.clipMode = this.touchInput ? 'auto' : 'hidden';
  //   //await this.loadConcerts();
  //   //this.todayMarker.get('date').textString = date.format(new Date(), 'dd.mm.yy');
  //   this.indicatePastAndUpcoming();
  //   this.relayout();
  // }

  relayout() {
    this.items.forEach(m => m.morph.width = this.width);
    if (this.scroller) this.scroller.width = this.width;
  }

  onChange(change) {
    super.onChange(change);
    if (change.prop === 'extent') {
      this.relayout();
    }
  }

  // this.loadConcerts()
  async loadConcerts() {
    let res = await fetch('https://typeshift.fra1.digitaloceanspaces.com/hornung/konzerte.csv');
    let concertString = await res.text();
    let concerts = concertString.split('\n').slice(1).map(l => {
      return l.split(';')
    })

    let items = concerts.map(([date, city, team, place]) => {
      let c = this.listItemProto.copy();
      c.visible = true;
      c.date = date;
      c.city = city;
      c.team = team;
      c.place = place;
      c.width = this.width;
      //c.layout.disable();
      c.layout.forceLayout();
      c.reactsToPointer = false;
      return {
        isListItem: true,
        morph: c
      };
    });

    // insert today marker at correct position
    const dayMicroSeconds = 24 * 7 * 60 * 10000;
    let now = Date.now();
    let upcoming = items.filter(i => -this.getEntryPriority(i.morph) >= now - dayMicroSeconds)
    upcoming = arr.sortBy(upcoming, i => -this.getEntryPriority(i.morph));
    this.items = [
      { isListItem: true, morph: morph({ visible: false })},
      { isListItem: true, morph: morph({ visible: false })},
      ...upcoming, 
      { isListItem: true, morph: this.todayMarker },
      { isListItem: true, morph: morph({ visible: false })},
      { isListItem: true, morph: morph({ visible: false })}, 
      ...arr.withoutAll(items, upcoming)
    ];
  }

  indicatePastAndUpcoming() {
    const dayMicroSeconds = 24 * 7 * 60 * 10000;
    let minTeamWidth = arr.max(this.items.map(m => {
        let l = m.morph.submorphs[3];
        if (!l) return;
        l._cachedTextBounds = null;
        return l.textBounds().width;
      }));
    minTeamWidth = Math.min(minTeamWidth, this.width / 3);
    for (let { morph: m} of this.items) {
      if (m == this.todayMarker) continue;
      if (!m.layout) continue;
      if (this.getEntryPriority(m) > -(Date.now() - dayMicroSeconds))
        m.opacity = .6;
      else
        m.opacity = 1;
    }
  }

  getEntryPriority(entry) {
    let [dd, mm, yy] = entry.getSubmorphNamed("date").textString.split(".");
    return -new Date(
      Date.UTC(Number.parseInt(20 + yy), Number.parseInt(mm) - 1, Number.parseInt(dd))
    ).valueOf();
    Date(20, 1, 1)
  }

}

export class ExportedComponent extends Morph {

  static get properties() {
    return {
      project: {},
      fetchUrl: {
        after: ['submorphs'],
        set(url) {
          this.setProperty('fetchUrl', url);
          this.updateLabel();
        }
      },
      isSelected: {},
      preview: {
        derived: true,
        set(url) {
          this.getSubmorphNamed('component preview').imageUrl = url;
          this.fitPreview();
        }
      },
      master: {
        initialize() {
          this.master = {
            auto: 'styleguide://SystemDialogs/exported component preview/deselected'
          }
        }
      }
    }
  }

  async fitPreview() {
    const img = this.getSubmorphNamed('component preview');
    if (!this.world()) img.opacity = 0;
    await this.master.whenApplied();
    const naturalExtent = await img.determineNaturalExtent();
    // scale the preview down to fit into width and height;
    const maxWidth = img.owner.width;
    const maxHeight = img.owner.height;
    const scaleFactor = Math.min(maxWidth / naturalExtent.x, maxHeight / naturalExtent.y);
    img.extent = naturalExtent.scaleBy(scaleFactor);
    img.opacity = 1;
  }

  updateLabel() {
    const nameLabel = this.getSubmorphNamed('component name');
    nameLabel.value = resource(this.fetchUrl).name();
    this.tooltip = this.fetchUrl;
  }

  onMouseDown(evt) {
    super.onMouseDown(evt);
    // this is pretty bad style
    if (this.project)
      this.project.selectComponent(this);
  }

  select(active) {
    this.isSelected = active;
    if (active) {
      this.master = {
        auto: 'styleguide://SystemDialogs/exported component preview/selected',
      }
    } else {
      this.master = {
        auto: 'styleguide://SystemDialogs/exported component preview/deselected'
      }
    }
  }

}

export class ProjectEntry extends Morph {

  static get properties() {
    return {
      worldName: {
        derived: true,
        set(name) {
          this.getSubmorphNamed('project title').value = name;
        },
        get() {
          return this.getSubmorphNamed('project title').textString;
        }
      }
    }
  }

  async withExportedComponents(components) {
    const previewContainer = this.getSubmorphNamed('component previews');
    const previewProto = await resource('part://SystemDialogs/exported component preview/deselected').read();
    previewContainer.submorphs = components.map(info => {
      let preview = previewContainer.submorphs.find(p => p.fetchUrl == info.identifier);
      if (!preview) {
        preview = previewProto.copy();
        preview.project = this;
        preview.fetchUrl = info.identifier;
      }
      preview.preview = info.preview;
      return preview;
    });
    return this;
  }

  selectComponent(component) {
    let importButton = this.get('import button');
    if (importButton) importButton.deactivated = false;
    this.owner.getSubmorphsByStyleClassName('ExportedComponent').forEach(m => m.select(false));
    component.select(true);
  }

}

export class ComponentBrowser extends Morph {

  static get properties() {
    return {
      db: {
        get() { return MorphicDB.default; }
      },
      ui: {
        derived: true,
        get() {
          return {
            spinner: this.getSubmorphNamed("spinner"),
            importButton: this.getSubmorphNamed('import button'),
            searchInput: this.getSubmorphNamed("search input"),
            masterComponentList: this.getSubmorphNamed("master component list"),
          }
        }
      }  
    }
  }

  onKeyDown(evt) {
    if (evt.key == 'Escape') this.reject();
  }

  reset() {
    this.ui.masterComponentList.submorphs = [];
  }

  async activate() {
    await this.fetchInfo();
    this._promise = promise.deferred();
    this.openInWorld();
    this.ui.searchInput.focus();
    this.center = this.world().visibleBounds().center();
    return this._promise.promise;
  }

  reject() {
    this._promise.resolve(false);
    if (!this.isComponent) this.fadeOut(300);
  }

  async importSelectedComponent() {
    let part = await resource(this.getSelectedComponent().fetchUrl).read();
    this._promise.resolve(part);
    if (!this.isComponent) this.fadeOut(300);
  }

  toggleBusyState(active) {
    this.ui.spinner.visible = active;
  }

  toggleComponentList(active) {
    const {masterComponentList} = this.ui;
    if (masterComponentList.isLayoutable == active) return;
    const center = this.world().visibleBounds().center();
    this.withAnimationDo(() => {
      masterComponentList.height = active ? 300 : 0;
      masterComponentList.isLayoutable = active;
    }, {
      duration: 300,
      easing: easings.inOutExpo,
    });
    this.layout.forceLayout();
    this.animate({ center, duration: 300, easing: easings.inOutExpo, });
  }

  filterList() {
    const { searchInput, masterComponentList, importButton } = this.ui;
    this.toggleComponentList(!!searchInput.input);
    fun.debounceNamed('updateList', 100, () => {
      this.updateList(this.filteredIndex(searchInput.input));
      importButton.deactivated = !this.getSelectedComponent();
    })();
  }

  getSelectedComponent() {
    return this.getSubmorphsByStyleClassName('ExportedComponent').find(component => component.isSelected)
  }

  filteredIndex(term) {
    const filteredIndex = {};
    for (let worldName in this._componentIndex) {
      if (worldName == this.world().name) continue; // skip local components
      let matches = this._componentIndex[worldName].filter(component => component.identifier.includes(term));
      if (matches.length > 0) filteredIndex[worldName] = matches;
    }
    return filteredIndex;
  }

  // this.toggleComponentList(true)
  // this.ui.masterComponentList.submorphs = []
  // await this.fetchInfo()

  async fetchInfo() {
    const maxSubCategories = 10;
    let componentsCache = resource(System.baseURL).join('components_cache/');
    const index = (await componentsCache.dirList(maxSubCategories))
           .filter(res => res.isFile())
           .map(res => {
             const url = decodeURIComponent(res.url.replace(componentsCache.url, '').replace('.png', ''));
             return {
               worldName: url.split('/')[0],
               preview: res.url,
               identifier: `part://${url}`
             }
           });
    this._componentIndex = arr.groupBy(index, m => m.worldName);
  }

  async updateList(componentsByWorlds) {
    const { masterComponentList } = this.ui;
    // do some smart updating of the list
    const newList = [];
    const currentList = masterComponentList.submorphs;
    const projectEntry = await resource('part://SystemDialogs/project section').read();
    // remove all empty lists
    for (let worldName in componentsByWorlds) {
      let item = currentList.find(item => item.worldName == worldName) || projectEntry.copy()
      item.worldName = worldName;
      item.withExportedComponents(componentsByWorlds[worldName]);
      newList.push(item);
    }

    masterComponentList.submorphs = newList;
  }

}
