MorphicSideBar.index.js:
import { Morph, touchInputDevice, easings } from "lively.morphic";
import { pt } from "lively.graphics/index.js";
import { fun } from "lively.lang/index.js";
import { connect } from "lively.bindings/index.js";

export default class MorphicSideBar extends Morph {

  static get properties() {
    return {
      isHaloItem: {
        get() {
          return false;
        }
      }
    }
  }

  onWorldResize(align = true) {
     if (!this.respondsToVisibleWindow) return;
     const bounds = $world.visibleBounds();
     const offsetTop = navigator.standalone && touchInputDevice ? 25 : 0;
     this.height = bounds.height - offsetTop;
     this.top = offsetTop + bounds.top(); 
     const hr = this.get('horizontal resizer'); 
     hr.height = this.height;
     hr.top = 0;
     if (!align) return;
     if (this.visible) {
       this.topLeft = bounds.topLeft();
     } else this.topRight = bounds.topLeft();

  }

  switchMode(mode) {
    if (mode == 'inspector') this.showInspector();
    if (mode == 'connections') this.showConnections();
  }

  showInspector() {
    this.get('inspector').visible = true;
    this.get('connections tree').visible = false;
  }

  showConnections() {
    this.get('inspector').visible = false;
    this.get('connections tree').visible = true;
  }

  async toggle(active) {
    const bounds = $world.visibleBounds();
    const offsetTop = navigator.standalone && touchInputDevice ? 25 : 0;
    this.height = bounds.height - offsetTop;
    this.top = offsetTop + bounds.top(); 
    if (active) {
      $world.addMorph(this, $world.get('lively top bar'));
      this.right = 0;
      this.attachToWorld($world);
      this.visible = true;
      await this.whenRendered();
      this.onWorldResize(false);
      this.get('scene graph').adjustProportions()
      await this.animate({
        opacity: 1, easing: easings.outCirc,
        left: 1, duration: 300
      });
      this.get('scene graph').refresh();
    } else {
      this.detachFromWorld($world);
      await this.animate({
        opacity: 0,
        right: 0, duration: 300
      });
      this.visible = false;
      this.remove();
    } 
  }

  attachToWorld(world) {
    connect(world, 'onChange', this, 'onHierarchyChange', {
      garbageCollect: true
    });
    connect(world, 'onSubmorphChange', this, 'onHierarchyChange', {
      garbageCollect: true
    });
    connect(world, 'showHaloFor', this, 'selectNode', {
      garbageCollect: true
    });
  }

  detachFromWorld(world) {
    world.attributeConnections.forEach(conn => {
      if (conn.targetObj === this) conn.disconnect();
    });
  }

  selectNode(target) {
     this.getSubmorphNamed('scene graph').selectMorphInTarget(target);
     this.getSubmorphNamed('connections tree').inspectConnectionsOf(target);
  }

  async onHierarchyChange(change) {
    if (change.selector === 'addMorphAt' ||
        change.selector === 'removeMorph' ||
        change.prop === 'name') {
       let sceneGraph = this._sceneGraph || (this._sceneGraph = this.getSubmorphNamed('scene graph'));
       if (!sceneGraph) return;
       if (!sceneGraph.owner.visible) return;
       if (change.target && change.target.isHand) return;
       if (change.target && sceneGraph.ignoreMorph(change.target)) return;
       if (change.args && !change.args[0].isHalo && sceneGraph.ignoreMorph(change.args[0])) return;
       if (change.target.ownerChain().find(m => sceneGraph.ignoreMorph(m))) return;
       fun.debounceNamed('scene-graph-update', 50, () => sceneGraph.refresh())(); 
    }
  }

  // this.toggle(true)

}

InteractiveTree.index.js:
import { Tree, TreeData } from "lively.components/tree.js";
import { morph, Icon, HorizontalLayout, easings, Morph } from "lively.morphic/index.js";
import { Color, Rectangle, rect, pt } from "lively.graphics/index.js";
import { connect, disconnect, disconnectAll, once } from "lively.bindings/index.js";
import { arr, num, fun, tree, promise } from "lively.lang/index.js";
import { ConnectionHalo } from "lively.ide/fabrik.js";

export class InteractiveTreeContainer extends Morph {

  static get properties() {
    return {
      fill: {
        defaultValue: Color.transparent
      },
      isContainer: {
        get() {
          return true;
        }
      },
      tree: {

      },
      node: {
        readOnly: true,
        get() {
          let treeData = this.tree.treeData;
          return treeData && treeData.asList().find(m => m.container === this)
        }
      }
    }
  }

  onChildRemoved(child) {

  }

  onChildAdded(child) {

  }

  toggleSelected(active) {
    if (!this.tree) return;
    let { selectionFontColor, nonSelectionFontColor } = this.tree;
     this.submorphs.filter(m => !m._isControlElement && m.styleClasses.includes('Label')).forEach(m => {
        m.fontColor = active ? selectionFontColor : nonSelectionFontColor
     })
  }

  onGrab(evt) {
    let tree = this.tree, node = this.node;
    this.fill = Color.gray.withA(.5)
    this.fontColor = tree.selectionFontColor;
    this.opacity = .8;
    super.onGrab(evt);
    if (!node) return;
    this.toggleSelected(false);
    tree.collapse(node);
    tree.treeData.parentNode(node).container.onChildRemoved(this); 
    this._data = tree.treeData.remove(node);
    node.container.target.remove();
    tree.update(true);
  }

  wantsToBeDroppedOn(dropTarget) {
     this.showPreviewOn(dropTarget);
     return true;    
  }

  showPreviewOn(target) {
    let tree;
    if (target.isContainer) {
      tree = target.tree;
    }
    if (target.isTree) {
      tree = target;    
    }
    if (tree && tree.showDropPreviewFor) {
      tree.showDropPreviewFor(this);
    }
  }

  async insertAsChild(node) {
    this.tree.treeData.add(node._data, this.node, this.node.children[0]); // add as child of node
    this.tree.uncollapse(this.node);
    this.tree.treeData.remove(this.tree._previewNode);
    await this.tree.whenRendered();
    this.tree.update(true);
    this.onChildAdded(node);
  }

  onBeingDroppedOn(hand, recipient) {
    debugger;
    this.tree._dropInProgress = true;
    super.onBeingDroppedOn(hand, recipient);
    let after = () => {
      this.fill = Color.gray.withA(.5);
      this.opacity = 1;
      this.tree._dropInProgress = false;
      this.tree.refresh();
    }

    if (recipient.isTree) {
      recipient.insertAtPlaceholder(this).then(after);
    } else if (recipient.isContainer) {
      recipient.insertAsChild(this).then(after);
    } else {
      after();
    }
  }

}

export class InteractiveTreeData extends TreeData {

  display(node) { 
    // display the morph container of that node by default

    // in case it provides a "fast render" (i.e. text based) version
    // render that in case we are not about to interact with that node
    return [node.container || '<NO CONTAINER>', {}];
  }
  isCollapsed(node) { return node.isCollapsed; }
  collapse(node, bool) { node.isCollapsed = bool; }
  getChildren(node) {
    return node.isLeaf ? null : 
       node.children.filter(n => n && (this.filterActive ? n.visible : true));
  }
  isLeaf(node) { return !node.children || node.children.filter(n => n && (this.filterActive ? n.visible : true)).length == 0; }

  add(node, parent = this.root, before) {
    if (!parent.children) parent.children = [];
    if (before)
      arr.pushAt(parent.children, node, parent.children.indexOf(before));
    else parent.children.push(node);
    parent.isLeaf = false;
  }

  replace(nodeA, nodeB) {
    let parent = this.parentNode(nodeA);
    let idx = parent.children.indexOf(nodeA);
    parent.children[idx] = nodeB;
  }

  next(node) {
    let parent = this.parentNode(node);
    return parent && parent.children[parent.children.indexOf(node) + 1];
  }

  isBefore(nodeA, nodeB) {
    this.next(nodeA) === nodeB;
  }

  isAfter(nodeA, nodeB) {
    this.next(nodeB) === nodeA;
  }

  addBefore(node, next) {
    this.add(node, this.parentNode(next), next);
  }

  addAfter(node, prev) {
    let parent = this.parentNode(prev);
    let nextIndex = parent.children.indexOf(prev) + 1;
    this.add(node, parent, parent.children[nextIndex]);
  }

  remove(node) {
    let parent = this.parentNode(node);
    if (!parent) return;
    arr.remove(parent.children, node);
    if (parent.children == []) {
      parent.children = null;
    }
    return node;
  }

  async filter(iterator, maxDepth = 1) {
    await this.uncollapseAll((node, depth) => depth < maxDepth + 1);
    let active = this.filterActive;
    this.filterActive = false;
    this.asListWithIndexAndDepth(false).reverse().forEach(({node, depth}) => {
      if (depth == 0) return (node.visible = true);
      node.visible = iterator(node);
      if (!node.visible && !node.isCollapsed && node.children.find(n => n.visible)) {
        node.visible = true;
        node.container.opacity = .5;
      } else node.container.opacity = 1;
      return node.visible;
    });
    this.filterActive = active;
  }

}

export class InteractiveTree extends Tree {

  update(force) {
    super.update(force);
    this.treeData && this.relayoutContainers();
    if (force) this.embeddedMorphs.forEach(m => {
      if (m.isContainer) {
        m.fill = Color.transparent
        m.fontFamily = this.fontFamily;
        m.fontSize = this.fontSize;
      }
    });
  }

  onDrop(evt) {
    super.onDrop(evt);
    this.treeData.remove(this._previewNode);
    this.update(true);
  }

  onDropHoverIn(evt) {
    // prevents the default text behavior
  }

  onDropHoverUpdate(evt) {
    // prevent default
  }

  onDropHoverOut(evt) {
    // prevent default
  }

  async onHoverOut(evt) {
    super.onHoverOut(evt);
    if (this._previewNode && this._previewNode.container.world()) {
       this.treeData.remove(this._previewNode); 
    }
    this.resetContainerSelection();
    this.update(true);
  }

  async insertAtPlaceholder(node) {
    let newParent = this.treeData.parentNode(this._previewNode);
    this.treeData.replace(this._previewNode, node._data);
    this.treeData.remove(this._previewNode);
    await this.whenRendered();
    this.update(true);
    newParent.container.onChildAdded(node);
  }

  scrollUpSlowly() {
    if (this._scrollingUp) return;
    this._scrollingUp = true;
    let scrollUp = async () => {
      if (this._scrollingUp && this.scroll.y > 0) {
        await this.animate({
          scroll: this.scroll.subXY(0, 30),
          duration: 300,
          easing: easings.linear
        })
        scrollUp();
      } else {
        this._scrollingUp = false;
      }
    };
    scrollUp();
  }

  scrollDownSlowly() {
    if (this._scrollingDown) return;
    this._scrollingDown = true;
    let scrollDown = async () => {
      if (this._scrollingDown && this.scroll.y < this.scrollExtent.y - this.height) {
        await this.animate({
          scroll: this.scroll.addXY(0, 30),
          duration: 300,
          easing: easings.linear
        })
        scrollDown();
      } else {
        this._scrollingDown = false;
      }
    };
    scrollDown();
  }

  stopScrollingSlowly() {
    this._scrollingUp = this._scrollingDown = false;
  }

  resetContainerSelection() {
    this.embeddedMorphs.forEach(m => {
      if (m.isContainer) m.fill = Color.transparent
    });
  }

  showDropPreviewFor(target) {
    let borderDist = 4;
    let pos = this.localize(target.globalBounds().center());
    let docPos = this.textPositionFromPoint(pos);
    let nodes = this.treeData.asListWithIndexAndDepth(({node}) => 
                  node != this._previewNode && node.container.world());
    let {node} = nodes.find(node => node.i - 1 === docPos.row) || {};
    let hoveredContainer = node && node.container;
    let placeholder = this.ensurePlaceholder();
    let previewContainer = placeholder.container;
    let previewIsShown = !!previewContainer.world();

    let positionInFrame = pos.subPt(this.scroll).subPt(this.scrollbarOffset);

    if (positionInFrame.y < 10) {
      this.scrollUpSlowly();
    } else if (positionInFrame.y > this.height - 50) {
      this.scrollDownSlowly();
    } else {
      this.stopScrollingSlowly();
    }

    this.resetContainerSelection();

    if (!hoveredContainer) {
       if (nodes.length > docPos.row) {
         return
       }
       if (arr.last(this.treeData.root.children) === placeholder) return;
       this.treeData.remove(placeholder);;
       this.treeData.add(placeholder);
       this.update(true);
    }
    if (hoveredContainer) {
      let charBounds = this.textLayout.boundsFor(this, docPos)
      if (Math.abs(charBounds.top() - pos.y) < borderDist) {
        if (this.treeData.isBefore(placeholder, node)) return;
        this.treeData.remove(placeholder);
        this.treeData.addBefore(placeholder, node);
        this.update(true);
      } else if (Math.abs(charBounds.bottom() - pos.y) < borderDist 
                 && (node.isCollapsed || this.treeData.isLeaf(node))) {
        if (this.treeData.isAfter(placeholder, node)) return;
        this.treeData.remove(placeholder);
        this.treeData.addAfter(placeholder, node);
        this.update(true);
      } else {
        hoveredContainer.fill = Color.gray;
        if (!previewIsShown) return;
        this.treeData.remove(placeholder);
        this.update(true);
      }
    }
  }

  ensurePlaceholder() {
    let basicLineExtent = pt(this.width - 100, this.document.getLine(0).height);
    if (!this._previewNode) {
       this._previewNode = {
         container: morph({
           acceptsDrops: false, reactsToPointer: false,
           fill: Color.gray, borderRadius: 5
         }),
         isCollapsed: true,
         isLeaf: true,
       }
     }
     this._previewNode.container.extent = basicLineExtent;
     return this._previewNode;
  }

   relayoutContainers() {
     let lineHeight = this.charBoundsFromTextPosition({
       row: 0, column: 6
     }).height;
     this.textLayout.charBoundsOfRow(this, 0).map(m => m.height)
     this.treeData.asListWithIndexAndDepth().forEach(({node, i}) => {
       node.container.nativeCursor = 'grab';
       node.container.width = this.width - 25 - node.container.left;
       if (node.container.toggleSelected)
          node.container.toggleSelected(i == this.selectedIndex);
     });
     this.anchors.forEach(a => a.position = a.position)
   }

}

export class SceneGraphTree extends InteractiveTree {

  static get properties() {
    return {
      target: {
        serialize: false
      }
    }
  }

  // this.reset()
  //this.onLoad()

  beforePublish() {
    this.submorphs = [];
    this.reset();
  }

  selectMorphInTarget(morph) {
    let node = this.treeData.asList().find(n => n.container.target === morph);
    if (node && this.selectedNode != node) this.selectPath(this.treeData.pathOf(node));
  }

  async onLoad() {
    this.submorphs = []
    await this.whenRendered();
    this.get('inspector').makeEditorVisible(true);
    this.get('inspector').makeEditorVisible(false);
    this.setTarget($world);
  }

  adjustProportions(evt) {
    let {y} = evt ? evt.state.dragDelta : {y: 0};
    let inspector = this.get('inspector');
    let resizer = this.get('scene graph resizer');
    let selector = this.get('mode selector');
    let connectionTree = this.get('connections tree');

    this.height += y;
    inspector.height -= y;
    connectionTree.height -= y;
    resizer.top = this.bottom;
    resizer.left = 0;
    selector.top = resizer.bottom;
    connectionTree.top = inspector.top = selector.bottom + 5;
  }

  reset() {
    this.treeData = new InteractiveTreeData({
      children: [],
      container: morph()
    });
    this.get('inspector').targetObject = {};
    this.selectedNode = null;
    this.anchors.filter(a => a.id == null).forEach(a => this.removeAnchor(a));
  }

  // this.setTarget($world)
  // this.treeData

  setTarget(morph) {
    // scan the morph hierarchy, collapse all initially, create all nodes
    this.target = morph;
    this.treeData = this.getTreeFromSubmorphHierarchy(morph);
    this.uncollapse(this.treeData.root)
  }

  isNodeOutOfSync(node) {
    if (node === this._previewNode) return false; // preview does not represent a real morph
    if (node.container.nameNeedsUpdate) return true;
    let actualSubmorphs = node.container.target.submorphs.filter(m => !this.ignoreMorph(m));
    if (actualSubmorphs.length != node.children.length) return true;
    if (actualSubmorphs.map(m => m.name).join('') != node.children.map(m => m.target ? m.target.name : "").join('')) return true;
    return false;
  }

  getNewChildren(node) {
    let nodeSubmorphs = node.container.target.submorphs.map(m =>
       m.__scene_graph_target__ ? m.__scene_graph_target__() : m
     ).filter(m => !this.ignoreMorph(m));
    let newChildren = []
    for (let i in nodeSubmorphs) {
      let m = nodeSubmorphs[i];
      let children = arr.without(node.children, this._previewNode);
      let existingChild = children.find(n => n.container.target.id == m.id);
      if (existingChild) newChildren[i] = existingChild;
      else newChildren[i] = this.readMorphHierarchy(m)
    }
    return newChildren;
  }

  refresh() {
    // update the treeData in place and rerender the tree
    if (this._updating || this._dropInProgress) return;
    let changes = false;
    tree.prewalk(this.treeData.root, (node) => {
      if (this.isNodeOutOfSync(node)) {
        changes = true;        
        node.name = node.container.target.name;
        node.container.refresh(); // because the tree will only update structure
        node.children = this.getNewChildren(node);
      }
    } , node => node.children);
    this.update(changes);
  }

  // this.refresh()

  ignoreMorph(m) {
    if (!m.isMorph) return false;
    return m.isContainer || m.isWindow || m.getWindow() || m.isEpiMorph || m.isHand ||
      ['lively.halos'].includes(m.constructor[Symbol.for("lively-module-meta")].package.name) ||
      ['MorphicSideBar', 'SideBarToggler', 'UserFlap', 'Menu'].includes(m.constructor.name);
  }

  onDropHoverOut(evt) {
    let container = evt.hand.grabbedMorphs[0];
    let target = evt.hand.findDropTarget();
    if (target === this) return;
    if (target && target.ownerChain().includes(this)) return;
    if (container && container.isContainer) container.onDragOutside();
  }

  onDropHoverIn(evt) {
    let morph = evt.hand.grabbedMorphs[0];
    if (morph && !morph.isContainer) {
      $world.halos().forEach(h => h.remove());
      morph.remove();
      Object.assign(morph, evt.hand._grabbedMorphProperties.get(morph).pointerAndShadow);
      let container = this.renderContainerFor(morph, false);
      evt.hand.grab(container);
      container.leftCenter = pt(-20,0);
    }
  }

  getTreeFromSubmorphHierarchy(morph) {
    return new InteractiveTreeData(this.readMorphHierarchy(morph));
  }

  readMorphHierarchy(morph) {
    return tree.mapTree(morph, (m, children) => {
      return {
        name: m.name,
        isCollapsed: true,
        container: this.renderContainerFor(m),
        visible: true,
        children
      }
    }, m => {
      return m.submorphs.filter(sub => 
        m.isWorld ? !this.ignoreMorph(sub) : true
      );
    });
  }

  // this.refresh()

  renderContainerFor(submorph = morph({name: 'root'}), embedded = true) {
    let container = new MorphContainer({
      tree: this,
      fill: embedded ? Color.transparent : Color.gray.withA(.5),
      fontColor: this.nonSelectionFontColor,
      fontFamily: this.fontFamily,
      target: submorph,
      opacity: submorph.visible ? 1 : .5,
      //layout: new HorizontalLayout({ spacing: 2, align: 'center' }),
    });
    if (submorph._data) {
      container._data = submorph._data;
      submorph._data.container = container;
    } else {
      container._data = {
        name: submorph.name,
        isCollapsed: true,
        container,
        children: []
      };
    }
    return container;
  }

  collapseAll() {
    this.treeData.asList().forEach(node =>
      node != this.treeData.root ? (node.isCollapsed = true) : (node.isCollapsed = false));
  }

  async filterMorphs(term) {
    let searchTerm = term.tokens[0];

    if (!this.treeData.filterActive) {
      this._collapseState = new WeakMap(this.treeData.asList().map(n => [n, n.isCollapsed])) 
    }

    this.treeData.filterActive = !!searchTerm;
    await this.treeData.filter(node => {
      let matches = !searchTerm || node.container.target.name.includes(searchTerm);
      node.container.highlightTerm(searchTerm);
      return matches;
    }, 3);
    if (!this.treeData.filterActive) {
      this.treeData.asList().forEach(node =>
          node.isCollapsed = this._collapseState.has(node) ? this._collapseState.get(node) : true);
    }
    this.refresh();
  }

  // this.setTarget(this.get('Rich Text Control'))

}

export class MorphContainer extends InteractiveTreeContainer {

  static get properties() {
    return {
      fontFamily: {
        after: ['submorphs'],
        derived: true,
        set(family) {
          this.submorphs[0].fontFamily = family;
        }
      },
      fontColor: {
        after: ['submorphs'],
        derived: true,
        set(color) {
          this.submorphs[0].fontColor = color;
        }
      },
      fontSize: {
        derived: true,
        set(size) {
          this.submorphs[0].fontSize = size;
        }
      },
      target: {
        serialize: false,
      },
      dragTriggerDistance: {
        defaultValue: 15
      },
      borderRadius: { defaultValue: 4 },
      nameNeedsUpdate: {
        derived: true,
        get() {
          if (this.target) {
            if (this.submorphs[0].textString.slice(2, -1) != this.target.name) return true;
            if (this.connectionCount != (this.target.attributeConnections || []).length) return true;
          }
          return false;
        }
      },
      grabbable: { defaultValue: true },
      draggable: { defaultValue: true },
      submorphs: {
        after: ['target', 'tree'],
        initialize() {
          this.refresh();
        }
      }
    }
  }

  async refresh() {
    let target = this.target;
    if (!target) return;
    this.reactsToPointer = true;
    this.dropShadow = false;
    let last;
    this.submorphs = [
      this.getLabel(),
      target.layout && this.getLayoutControls(),
      this.getConnectionControls()
    ].filter(Boolean).map((m, i) => {
      m.fitIfNeeded();
      m.left = last ? last.right + 5 : 0;
      m.top = 2;
      last = m;
      return m;
    });
    this.height = 20;
    this.opacity = target.visible ? 1 : .5;
  }

  getLabel() {
    let l = this.getSubmorphNamed('name label') || morph({
      type: 'label', name: 'name label',
      reactsToPointer: false, padding: rect(0,0,0,0),
      acceptsDrops: false, fontSize: this.tree.fontSize
    });

    const displayedName = l.textString.slice(2, -1);

    if (!displayedName || displayedName != this.target.name)
      l.value = [...this.getIcon(this.target), " " + this.target.name + " "];
    return l;
  }

  getIcon(target) {
    const klassIconMapping = {
      "Morph": "square",
      "Ellipse": "circle",
      "Text": "font",
      "Label": "tag",
      "Polygon": "draw-polygon",
      "Path": "bezier-curve",
      "Image": "image",
      "Canvas": "chess-board",
      "HTMLMorph": "code"
    }
    return Icon.textAttribute(klassIconMapping[target.constructor.className] || "codepen", {
      paddingTop: '4px',
      fontSize: this.tree.fontSize - 2,
      opacity: .6
    });
  }

  getLayoutControls() {
    let layoutLabel = this.getSubmorphNamed('layout label') || morph({
      type: "label", name: 'layout label', borderRadius: 3,
      acceptsDrops: false, padding: Rectangle.inset(4, 2, 4, 2),
      fill: Color.orange, fontColor: Color.white
    });

    layoutLabel._isControlElement = true;
    if (layoutLabel.textString != this.target.layout.name())
      layoutLabel.value = this.target.layout.name();
    return layoutLabel;
  }

  async onMouseDown(evt) {
    super.onMouseDown(evt);
    if (evt.targetMorph == this.get('connections label')) {
      await this.whenRendered();
      for (let halo of this.world().halos()) {
        halo.remove();
      }
      for (let halo of this.world().getSubmorphsByStyleClassName('ConnectionHalo')) {
        halo.remove();
      }
      this.owner.animate({ opacity: .5, duration: 300 });
      let connectionHalo = new ConnectionHalo({target: this.target});
      connectionHalo.openInWorld();
      connectionHalo.topLeft = this.getSubmorphNamed('connections label').globalPosition.addXY(0, -2);
      let conn = connect(this, 'position', () => {
        connectionHalo.topLeft = this.getSubmorphNamed('connections label').globalPosition.addXY(0, -2);
      });
      let self = this, tree = this.owner, sideBar = this.get('side bar');
      once(connectionHalo, 'remove', this, 'toggleConnectionControl', {
        varMapping: { self, disconnectAll, disconnect, connectionHalo, tree, sideBar },
        updater: ($upd) => {
          self.connectionHalo = null;
          tree.animate({ opacity: 1, duration: 300 });
          disconnect(sideBar, 'toggle', connectionHalo, 'remove');
          disconnectAll(self);
          $upd(true);
        } // if connections are > 0
      });
      once(this, 'remove', connectionHalo, 'remove');
      once(this.get('side bar'), 'toggle', connectionHalo, 'remove');
      this.toggleConnectionControl(false);
      this.connectionHalo = connectionHalo;
    }
  }

  getConnectionControls() {
    this.connectionCount = this.target.attributeConnections ?
      this.target.attributeConnections.filter(m => !m.targetObj.isHalo).length : 0;
    let connectionsLabel = this.getSubmorphNamed('connections label') || morph({
      acceptsDrops: false, borderRadius: 3,
      type: "label", name: 'connections label', 
      padding: Rectangle.inset(4,2,4,2), nativeCursor: 'pointer',
      fill: Color.red, fontColor: Color.white 
    });
    connectionsLabel._isControlElement = true;
    connectionsLabel.visible = !this.connectionHalo && !!this.connectionCount;
    const connectionState = this.connectionCount ? 
       `${this.connectionCount} connection${this.connectionCount > 1 ? 's' : ''}` 
       : 'Manage Connections';
    if (connectionsLabel.textString != connectionState)
      connectionsLabel.value = connectionState
    return connectionsLabel;
  }

  highlightTerm(term) {
    const label = this.submorphs[0];
    const iconPart = label.textAndAttributes.slice(0, 2);
    const termToHighlight = label.textString.slice(1); // just remove the icon
    let inBetween = termToHighlight.split(term);
    if (inBetween.length === 1) {
      label.textAndAttributes = [...iconPart, inBetween[0]];
      return;
    }

    label.textAndAttributes = [...iconPart, ...arr.flatten(
      arr.interpose(
         inBetween.map(x => [x, {}]), [term, { fontWeight: '900'}]
      )
    )];
  }

  getClassControls() {

  }

  wantsToBeDroppedOn(target) {
    let res = super.wantsToBeDroppedOn(target);
    if (![target, ...target.ownerChain()].includes(this.tree)) this.onDragOutside();
    return res;
  }

  async onGrab(evt) {
    this.tree._dropInProgress = true;
    let globalTargetPosition = this.target.globalPosition;
    super.onGrab(evt);
    this._data.globalTargetPosition = globalTargetPosition;
    await this.whenRendered();
    if (!this.tree.fullContainsWorldPoint(this.globalPosition))
      this.onDragOutside();
    else this.leftCenter = pt(-20,0);
    this.tree._dropInProgress = false;
  }

  // fixme: prevent concurrent calls of this method
  onDragOutside() {
    if (this._draggedOutside) return;
    this._draggedOutside = true;
    if (!this.queue) {
      this.queue = fun.createQueue('container-hover-queue', async (self, thenDo) => {
        let { 
          opacity: originalOpacity, 
          scale: originalScale
        } = self.target;
        self.target.opacity = 0;
        self.target.scale = .5;
        $world.halos().forEach(h => h.remove());
        $world.firstHand.grab(self.target);
        self.target._data = self._data;
        self.remove();
        self.target.position = pt(0,0);
        await self.target.animate({
          opacity: originalOpacity,
          scale: originalScale,
          duration: 200
        });
        thenDo();
      })
    }
    this.queue.push(this);
  }

  onChildAdded(child) {
    let nextIndex = this.node.children.indexOf(child.node || child._data) + 1;
    let neighbor = this.node.children[nextIndex];
    child.target.remove();
    if (neighbor) {
      let actualIndex = this.target.submorphs.indexOf(neighbor.container.target);
      this.target.addMorphAt(child.target, actualIndex);
    } else
      this.target.addMorph(child.target);
    if (child._data.globalTargetPosition)
      child.target.position = this.target.localize(child._data.globalTargetPosition);
  }

  onChildRemoved(child) {
    //child.show()
  }

  toggleConnectionControl(active) {
    this.getSubmorphNamed('connections label').visible = active;
  }

  async toggleSelected(active) {
    super.toggleSelected(active);
    if (this._active === active) return;
    this._active = active;
    if (active && this.target && this.target.isMorph && this.world()) {
      await this.target.whenRendered();
      this.get('inspector').targetObject = this.target;
      if (this.connectionHalo) return;
      for (let halo of this.world().halos()) {
        if (halo.target === this.target) return;
        halo.remove();
      }
      this.world().showHaloFor(this.target);
    }
  }

}

FrameResizer.index.js:
import { Morph } from "lively.morphic";

export default class FrameResizer extends Morph {

  static get properties() {
    return {
      direction: {
        type: 'Enum',
        values: ["left", "right"]
      }
    }
  }

  onDrag(evt) {
    const prevLeft = this.owner.left;
    const minWidth = 350;
    // compute the delta relative to own position
    const dragOffset = evt.positionIn(this).x;
    let delta = (this.direction == "left" ? -1 : 1) * dragOffset;
    this.owner.width += delta;
    if (this.owner.width < minWidth) {
      // get the distance that needs to be corrected
      let overflow = minWidth - this.owner.width;
      delta = delta + overflow; // remove the overflow from the delta;
      this.owner.width = minWidth;
    }
    this.owner.left = this.direction == "left" ? prevLeft - delta : prevLeft;
  }
}

ConnectionsTree.index.js:
import { Tree } from "lively.components/tree.js";
import { TreeData } from "lively.components";
import { Icon, morph } from "lively.morphic";
import { pt, Rectangle, rect, Color } from "lively.graphics";
import { arr } from "lively.lang";
import { connect, signal } from "lively.bindings";
import { Leash } from "lively.components/widgets.js";
import { once } from "lively.lang/function.js";
import { interactivelyReEvaluateConnection, interactiveConnectGivenSource, interactiveConnectGivenSourceAndTarget } from "lively.ide/fabrik.js";

function visualizeConnection(m1, m2, existingLeash, leashStyle = {}, orientation = 'left') {
  // if m2 is not a morph, then render a data pointer (to open inspector)
  let sides = rect(0).sides.concat(rect(0).corners),
      leash = existingLeash || new Leash({
        isSmooth: true,
        styleClasses: ['Halo'],
        borderColor: Color.orange,
        epiMorph: true,
        endpointStyle: {
          start: {fill: Color.transparent, nativeCursor: "auto"},
          end: {fill: Color.orange}
        },
        ...leashStyle,
        hasFixedPosition: true
      });
  // fixme: the attachment points of the leashes should be parametrized...
  leash.startPoint.attachTo(m1, 'rightCenter');
  if (m2.isMorph) {
     var nearestPart = m2.globalBounds().partNameNearest(sides, m1.globalPosition);
     if (m1.globalPosition.equals(m2.globalBounds().partNamed(nearestPart))) {
       // pick another part, that is not exactly the same
       nearestPart = m2.globalBounds().partNameNearest(arr.without(sides, nearestPart), m1.globalPosition);
     }
     leash.endPoint.attachTo(m2, 'leftCenter');
  } else {
     let virtualNodePos = m1.globalBounds().topRight().addPt(pt(100,0)),
          visualPointer = morph({
           type: 'label', value: m2.toString(),
           styleClasses: ['Tooltip'] , padding: rect(8,4)
         }).openInWorld(virtualNodePos);
     visualPointer.position = m1.world().bounds().translateForInclusion(visualPointer.bounds()).topLeft();
     once(leash, 'remove', visualPointer, 'remove');
     leash.endPoint.attachTo(visualPointer, 'leftCenter');
  }
  return leash;
}

function startConnecting(connectorLeash, connectorPin) {
  connectorLeash.animate({opacity: .8, duration: 300});
  connectorLeash.startPoint.attachTo(connectorPin, 'center');
  connectorLeash.vertices[0].position = pt(2,2);
}

async function stopConnecting(connectorLeash, connectorPin, sourceObj, sourceAttr = "sourceAttrName") {
  let {startPoint, endPoint} = connectorLeash,
       targetObj = endPoint.connectedMorph;
  if (targetObj && targetObj.constructor.className == 'MorphContainer') {
    targetObj = targetObj.target;
  }
  if (targetObj)
    await interactiveConnectGivenSourceAndTarget(sourceObj, sourceAttr, targetObj);

  await cleanup();

  async function cleanup() {
    await connectorLeash.animate({opacity: 0, duration: 300});
    connectorLeash.startPoint.clearConnection();
    connectorLeash.endPoint.clearConnection();
    connectorLeash.remove();
  }
}

async function editConnection({connection, connectionPoint, description}) {
  //this.hideConnection({connection, connectionPoint, description});
  let leash = visualizeConnection(connectionPoint, connection.targetObj, false, {
    borderColor: Color.gray.darker(),
    endpointStyle: {
      start: {fill: Color.transparent, nativeCursor: "auto"},
      end: {fill: Color.gray.darker()}
    }
  });
  await interactivelyReEvaluateConnection(connection, 'Edit Connection', false);
  leash.remove();
}

class ConnectionsTreeData extends TreeData {
  static for(target) {
    const treeData = new this();
    treeData.root = treeData.parseMorphConnections(target);
    return treeData;
  }

  parseMorphConnections(target) {
    const { attributeConnections = [] } = target;
    const data = {};
    for (let conn of attributeConnections)  {
      if (conn.targetObj.isHalo) continue;
      data[conn.sourceAttrName] = data[conn.sourceAttrName] || [{
        name: [
          `      New Connection  `, { fontColor: Color.white.withA(.5), fontStyle: 'italic'},
          this.renderPin(target, conn.sourceAttrName), {}
        ]
      }];
      arr.pushAt(data[conn.sourceAttrName], {
        name: [
          ...this.renderExistingConnection(conn)
        ],
      }, 0);
    }
    return {
      children: Object.entries(data).map(([sourceAttrName, children]) => {
        return {
          name: [sourceAttrName, { fontWeight: 'bold' }, " ", {}, morph({
            fill: Color.white,
            opacity: .7,
            value: `${children.length - 1}`,
            borderRadius: 15,
            fontWeight: 'bold',
            padding: Rectangle.inset(5,0,5,0),
            type: "label"
          }), {paddingTop: '4px'}],
          children
        }
      }).concat([{
        name: [
          `New Connection  `, { fontColor: Color.white.withA(.5), fontStyle: 'italic'},
          this.renderPin(target), {}
        ]
      }])
    }
  }

  renderExistingConnection(conn) {
    const nameLabel = morph({
      type: 'label',
      fontColor: Color.gray,
      fontSize: 16,
      nativeCursor: 'pointer',
      value: [...Icon.textAttribute('long-arrow-alt-right', { paddingTop: '4px' }),
      "  ", {}, conn.targetMethodName, {}]
    });
    const connectionPin = this.renderDeadPin();
    const bubble = connectionPin.submorphs[0];
    connect(nameLabel, 'onHoverIn', () =>    {
      nameLabel.fontColor = Color.orange;
      nameLabel._leash = visualizeConnection(bubble, conn.targetObj, nameLabel._leash);
      bubble.fill = Color.orange;
      bubble.border = { color: Color.orange, width: 3}
    });
    connect(nameLabel, "onMouseDown", async (evt) => {
      evt.stop();
      await interactivelyReEvaluateConnection(conn);
      signal(this, 'refresh');
    })
    connect(nameLabel, 'onHoverOut', () => {
      nameLabel.fontColor = Color.gray;
      bubble.fill = Color.gray;
      bubble.border = { color: Color.white, width: 1};
      nameLabel._leash.remove();
      nameLabel._leash = undefined;
    });
    return [
      nameLabel, {},
      "  ", {},
      ...Icon.textAttribute('times', { nativeCursor: "pointer ", onMouseDown: () => {
        conn.disconnect();
        signal(this, 'refresh');
      }}),
      "  ", {},
      connectionPin, {}
    ]
  }

  renderDeadPin() {
    const pin = morph({
      type: 'ellipse',
      fill: Color.gray,
      extent: pt(10,10),
      center: pt(8,12),
      borderWidth: 1,
    });
    return morph({
      extent: pt(20,20),
      fill: Color.transparent,
      reactsToPointer: false,
      submorphs: [ pin ]
    })
  }

  renderPin(sourceObj, sourceAttrName) {
    const pin = morph({
      type: 'ellipse',
      fill: Color.gray.withA(.5),
      extent: pt(10,10),
      center: pt(8,12),
      draggable: false,
      borderWidth: 1,
    });
    const connector = new Leash({
      opacity: 0,
      styleClasses: ['Halo'],
      isSmooth: true,
      epiMorph: true,
      hasFixedPosition: true,
      borderColor: Color.gray.darker(),
      endpointStyle: {fill: Color.gray.darker()},
      position: pt(1, 1),
      vertices: [pt(0, 0), pt(0, 0)],
      canConnectTo: m => m.constructor.className == "MorphContainer" || arr.intersect(
        [m, ...m.ownerChain()],
        [pin, ...pin.ownerChain()]
      ).length < 2
    });
    pin.addMorph(connector);
    connect(connector.endPoint, "onDragStart", () => {
      startConnecting(connector, pin);
    });
    connect(connector.endPoint, "onDragEnd", async (evt) => {
      pin.center = pt(8,12);
      await stopConnecting(connector, pin, sourceObj, sourceAttrName);
      signal(this, 'refresh');
    });
    return morph({
      extent: pt(20,20),
      fill: Color.transparent,
      reactsToPointer: false,
      submorphs: [ pin ]
    })
  }

  display(node) {
    return node.name;
  }

  collapse(node, bool) {
    node.isCollapsed = bool;
  }

  isCollapsed(node) {
    return node.isCollapsed;
  }

  isLeaf(node) {
    return !node.children;
  }

  getChildren(node) {
    return node.children;
  }
}

export default class ConnectionsTree extends Tree {
  static get properties() {
    return {

    }
  }

  inspectConnectionsOf(target) {
    // target = this.get("email input")
    // target = this.get("save button")
    this.targetMorph = target;
    if (target) {
      this.treeData = ConnectionsTreeData.for(target);
      connect(this.treeData, 'refresh', this, 'refresh')
    }
  }

  refresh() {
    if (!this.targetMorph) return;
    this.treeData = ConnectionsTreeData.for(this.targetMorph);
    connect(this.treeData, 'refresh', this, 'refresh')
  }
}

StripeButton.index.js:
import { Button } from "lively.components/buttons.js";
import { Color } from "lively.graphics/index.js";

export default class StripeButton extends Button {

  static get properties() {
    return {
      haloShadowEnabled: {
        defaultValue: true
      },
      toggleFill: {},
    }
  }

  onDrop(evt) {
    // handy shortcut to quickly assign icons
     const [m] = evt.hand.grabbedMorphs; // pick the first of the grabbed submorphs  
     if (m && m.isLabel) {
       this.label = m.textAndAttributes;
       m.remove();
     }
  }

  onHoverIn(evt) {
    super.onHoverIn(evt);
    if (this.haloShadowEnabled === false) return;
    let ss = this.dropShadow.toJson();
    ss.blur = 10;
    ss.distance = 3;
    this.animate({
      dropShadow: ss, duration: 200
    });
  }

  onHoverOut(evt) {
    super.onHoverOut(evt);
    if (this.haloShadowEnabled === false) return;
    let ss = this.dropShadow.toJson();
    ss.blur = 6;
    ss.distance = 1;
    this.animate({
      dropShadow: ss, duration: 200
    });
  }
}

ShapeLayoutControl.index.js:
import { Morph, morph } from "lively.morphic";
import * as layouts from 'lively.morphic/layout.js';
import { string } from "lively.lang/index.js";
import { connect, noUpdate } from "lively.bindings/index.js";
import { InteractiveMorphSelector } from "lively.halos/morph.js";
import { Color, pt } from "lively.graphics/index.js";

export default class ShapeLayoutControl extends Morph {

  static get properties() {
    return {
      managedProps: {
        get() {
          return [
            ['type', 'selection', true], 
            ['align', 'selection'],
            ['direction', 'selection'],
            ['axis', 'selection'],
            ['spacing', 'number'],
            ['auto resize', 'checked'], 
            ['resize submorphs', 'checked'],
            ['x axis policy', 'selection', true],
            ['y axis policy', 'selection', true],
            ['react to submorph animations', 'checked'],
            ['submorph settings', false, true],
            ['order by index', 'checked'],
            ['dragme', false, true],
            ['configure grid layout', false, true]
          ];
        }
      },
      ui: {
        readOnly: true,
        get() {
          let ui = {
            labelContainer: this.getSubmorphNamed('label container'),
            controlContainer: this.getSubmorphNamed('control container')
          };
          for (let [prop] of this.managedProps) {
            let labelName = prop + ' label';
            let controlName = prop + ' control';
            ui[this.join(labelName)] = this.getSubmorphNamed(labelName);
            ui[this.join(controlName)] = this.getSubmorphNamed(controlName);
          }
          return ui;
        }
      }
    }
  }

  // this.focusOn(this.get('tester'))

  focusOn(target) {
    this.target = target;
    this.update(false);
  }

  setupConnections() {
    let ui = this.ui;
    connect(ui.submorphSettingsControl, 'fire', this, 'chooseSubmorphToChangeLayoutSettings');
    connect(ui.dragmeControl, 'onDragStart', this, 'onSubmorphSettingsDragStart');
    connect(ui.xAxisPolicyControl, 'selection', this, 'updateSubmorphProportionalLayoutSettings', {
      converter: `policy => ({
        policy,
        axis: "x",
        submorph: self.selectedSubmorph,
      })`,
      varMapping: {
        self: this
      }
    });
    connect(ui.yAxisPolicyControl, 'selection', this, 'updateSubmorphProportionalLayoutSettings', {
      converter: `policy => ({
        policy,
        axis: "y",
        submorph: self.selectedSubmorph,
      })`,
      varMapping: {
        self: this
      }
    });

    for (let [propName, accessor, customConnect] of this.managedProps) {
      if (customConnect)
        continue;
      let prop = this.join(propName),
          control = ui[this.join(propName, 'control')];
      connect(control, accessor, this, 'updateLayout', {
        updater: ($upd, val) => {
          $upd(prop, val);
        }, varMapping: { prop }
      });
    }
  }

  lower(s) { return s.charAt(0).toLowerCase() + s.slice(1); }

  join(fieldName, sub) {
    return this.lower(string.camelCaseString(fieldName + (sub ? ` ${sub}` : '')))
  }

  updateLayoutOfTarget(layout) {
    let layoutClass = layouts[string.camelCaseString(layout)];
    this.target.layout = layoutClass ? new layoutClass({ autoResize: false }) : null;
    this.update();
  }

  updateLayout(prop, value) {
    this.target.layout[prop] = value;
  }

  update(refresh = true) {
    if (!this.target) return;
    noUpdate(() => {
      let ui = this.ui;
      let layout = this.target.layout || { name: () => 'No'};
      let i = 0;
      for (let [prop, accessor, updater] of this.managedProps) {
        i += 5;
        let control = ui[this.join(prop, 'control')];
        let label = ui[this.join(prop, 'label')];

        if (!accessor) continue;
        if (prop === 'type') {
          control.selection = layout.name() + " Layout";
          continue;
        }

        let value = layout[this.join(prop)];
        let propApplicable = typeof value !== 'undefined';
        if (propApplicable) {
          switch (prop) {
            case 'align':
              control.items = layout.possibleAlignValues; break;
            case 'direction':
              control.items = layout.possibleDirectionValues; break;
            case 'axis':
              control.items = layout.possibleAxisValues; break;            
          }
          control[accessor] = value;
        }
        label.isLayoutable = label.visible = propApplicable;
      }
      this.showGridLayoutControl(layout.name() === 'Grid');
      this.showProportionalControl(layout.name() === 'Proportional');
    });
  }

  // proportional layout stuff

  updateSubmorphProportionalLayoutSettings({ policy, axis, submorph }) {
    this.target.layout.changeSettingsFor(submorph, {
      [axis]: policy
    }, true)
  }

  onSubmorphSettingsDragStart(evt) {
    evt.stop();
    let layout = this.target.layout;
    let settings = layout.settingsFor(this.selectedSubmorph), descr = [];
    for (let name in settings) descr = [...descr, name + ': ', { fontWeight: 'bold'}, settings[name] + ' ', {}];
    let grabme = morph({
      type: "label", fontColor: Color.white,
      value: descr, fill: Color.black.withA(.7),
      padding: 5, borderRadius: 10,
      isLayoutable: false
    });
    grabme.wantsToBeDroppedOn = (dropTarget) => layout.layoutableSubmorphs.includes(dropTarget);
    grabme.onBeingDroppedOn = (hand, dropTarget) => {
      grabme.remove();
      let target = layout.layoutableSubmorphs.includes(dropTarget) ? dropTarget :
        evt.world.morphsContainingPoint(evt.hand.position).find(ea =>
          layout.layoutableSubmorphs.includes(ea));
      if (target) {
        this.updateSubmorphProportionalLayoutSettings({policy: settings.x, axis: "x", submorph: target});
        this.updateSubmorphProportionalLayoutSettings({policy: settings.y, axis: "y", submorph: target});
        target.show();
        $world.setStatusMessage("layout settings applied");
      }
    };
    evt.hand.grab(grabme);
    grabme.moveBy(pt(10,10));
  }

  async chooseSubmorphToChangeLayoutSettings() {
    let morphs = this.target.layout.layoutableSubmorphs, submorph;
    // prevent drawer from loosing focus
    this.manageFocus = true;
    if (this.env.eventDispatcher.isKeyPressed("Shift")) {
      let items = morphs.map(m => ({isListItem: true, string: String(m), value: m}));
      ({selected: [submorph]} = await $world.listPrompt(
        "Select morph", items, {onSelection: ea => ea.show()}));
    } else {
      submorph = await InteractiveMorphSelector.selectMorph(
        this.world(), this, target => morphs.includes(target));
    }
    this.manageFocus = false;
    // stop prevention
    this.selectedSubmorph = submorph;
    if (!submorph) return;

    let { xAxisPolicyControl, yAxisPolicyControl } = this.ui;
    let submorphSettings = this.target.layout.settingsFor(submorph);
    noUpdate(() => {
      xAxisPolicyControl.selection = submorphSettings.x;
      yAxisPolicyControl.selection = submorphSettings.y;
    });
  }

  showProportionalControl(active) {
    let { submorphSettingsControl, dragmeControl,
          xAxisPolicyControl, xAxisPolicyLabel,
          yAxisPolicyControl, yAxisPolicyLabel
        } = this.ui;
    [
     submorphSettingsControl, dragmeControl,
     xAxisPolicyControl, xAxisPolicyLabel,
     yAxisPolicyControl, yAxisPolicyLabel
    ].forEach(elem => {
        elem.isLayoutable = active;
        elem.visible = active;
    });
  }

  showGridLayoutHalo() {
    $world.showLayoutHaloFor(this.target);
  }

  showGridLayoutControl(active) {
    let button = this.ui.configureGridLayoutControl;
    button.visible = button.isLayoutable = active;
  }
}

StylingSideBar.index.js:
import { Morph, touchInputDevice, morph, Icon, easings } from "lively.morphic";
import { pt, Color } from "lively.graphics/index.js";
import { fun } from "lively.lang/index.js";
import { connect } from "lively.bindings/index.js";
import { TreeData } from "lively.components";

export class SettingsTree extends TreeData {

  static default() {
    return new this({
      isCollapsed: false,
      children: [
        {
          isCollapsed: true,
          name: [ " ", {}, ...Icon.textAttribute('font'), ' Rich Text', { fontWeight: 'bold'}],
          children: [{
            panel: morph({}) 
          }]
        },
        {
          isCollapsed: false,
          name: [" ", {}, ...Icon.textAttribute('image'), ' Shape', { fontWeight: 'bold'}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [" ", {}, ...Icon.textAttribute('border-style'), ' Border', { paddingLeft: '2px', fontWeight: 'bold'}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [" ", {}, ...Icon.textAttribute('grip-vertical'), ' Layout', { paddingLeft: '4px', fontWeight: 'bold'}],
          children: [{
            panel: morph({})
          }]
        },
      ]
    })
  }

  static border() {
    return new this({
      isCollapsed: false,
      children: [
        {
          isCollapsed: true,
          name: [' Type    ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({}) 
          }]
        },
        {
          isCollapsed: false,
          name: [' Color   ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Width  ', {}, morph({ height: 20 }), {}],
          children: [{
