MasterComponentControl.index.js:
import { Morph } from "lively.morphic/morph.js";
import { resource } from "lively.resources/index.js";
import { arr, obj, string, Path } from "lively.lang/index.js";
import { connect } from "lively.bindings/index.js";
import { getMastersMenu, resolvedMasters } from "lively.morphic/style-guide.js";

export default class MasterComponentControl extends Morph {

  focusOn(target) {
    this.target = target;
    this.update();
  }

  async onMouseUp(evt) {
    let stateToModify;
    if (evt.targetMorph.name == 'auto master selection') {
      stateToModify = 'auto';
    }
    if (evt.targetMorph.name == 'hover master selection') {
      stateToModify = 'hover';
    }
    if (evt.targetMorph.name == 'click master selection') {
      stateToModify = 'click';
    }

    this.refreshButtons()

    if (!this.target) return;

    for (let state of ['auto', 'hover', 'click']) {
      if (this.target.master && 
          this.target.master[state] && 
          !this.get(state + ' master checkbox').checked) {
        this.adjustMasterComponent(state, null);
      }
    }

    if (stateToModify) {
      let menu = this.openMenu(await this.getMastersMenu(stateToModify), evt);
    }
  }

  async getMastersMenu(stateToModify) {
  return await Promise.all([...Object.keys(resolvedMasters), this.world().name].map(async worldName => {
    return [worldName, this.componentsToMenu(await resource(`styleguide://${worldName}/`).dirList(), stateToModify)];
  }));
}

  componentsToMenu(components, state, items = null, depth = 1) {
    if (!items) {
      items = components.map(component => ({
        name: arr.last(component.name.split('/')),
        path: component.name.split('/'),
        value: component,
      }));
    }

    if (items.length == 0) return [];
    if (depth > 100) return [];
    const self = this;
    return arr.flatten(Object.entries(arr.groupBy(items, c => c.path[depth - 1])).map(([name, entries]) => {
      let [components, categories] = arr.partition(entries, entry => entry.path.length <= depth);
      let subMenu = [
          name,
          this.componentsToMenu(null, state, categories, depth + 1)
      ];
      return [
        ...components.map(entry => {
            return [entry.name, () => { self.adjustMasterComponent(state, entry.value) }]
          }),
        ...subMenu[1].length ? [subMenu] : []
      ]
    }), 1);
  }

  async getComponentSelectionMenu(state) {
    let worldName = this.world().metadata.commit.name;
    let res = resource(`styleguide://${worldName}/`);
    let components = await res.dirList();
    let systemComponents = await resource('styleguide://System/').dirList();
    let localMenu = this.componentsToMenu(components, state);
    let remoteMenu = this.componentsToMenu(systemComponents, state);
    return [
       ...localMenu.length > 0 ? 
        [
          ...localMenu,
          { isDivider: true }
        ] : [],
      ...remoteMenu,
    ]
  }

  adjustMasterComponent(state, component) {
    if (this.target.master)
      this.target.master[state] = component;
    else if (component) {
      this.target.master = {
        [state]: component
      }
    }
    let { auto, hover, click } = this.target.master;
    if (!auto && !hover && !click) this.target.master = null;
    this.update();
    this.target.withAllSubmorphsDo(m => delete m._parametrizedProps); // clear them all
    this.target.requestMasterStyling();
  }

  componentsToMenu(components, state, items = null, depth = 1,) {
    if (!items) {
      items = components.map(component => ({
        name: arr.last(component.name.split('/')),
        path: component.name.split('/'),
        value: component,
      }));
    }

    if (items.length == 0) return [];
    if (depth > 100) return [];
    let self = this;
    return arr.flatten(Object.entries(arr.groupBy(items, c => c.path[depth - 1])).map(([name, entries]) => {
      let [components, categories] = arr.partition(entries, entry => entry.path.length <= depth);
      let subMenu = [
          name,
          this.componentsToMenu(null, state, categories, depth + 1)
      ];
      return [
        ...components.map(entry => {
            return [entry.name, () => { self.adjustMasterComponent(state, entry.value) }]
          }),
        ...subMenu[1].length ? [subMenu] : []
      ]
    }), 1);
  }

  update() {
    if (!this.target) return;
    for (let state of ['auto', 'hover', 'click']) {
      let btn = this.getSubmorphNamed(state + ' master selection');
      let checkBox = this.getSubmorphNamed(state + ' master checkbox');
      if (Path(['master', state]).get(this.target)) checkBox.checked = true;
      else checkBox.checked = false;
      btn.label = string.truncateLeft(Path(['master', state, 'name']).get(this.target) || "select master", 15, '...');
    }
    this.refreshButtons();
  }

  refreshButtons() {
    for (let state of ['auto', 'hover', 'click']) {
      let btn = this.getSubmorphNamed(state + ' master selection');
      let checkBox = this.getSubmorphNamed(state + ' master checkbox');
      let disabled = btn.deactivated = !checkBox.checked;

      this.getSubmorphNamed(state + ' master selection').opacity = disabled ? .5 : 1;
    }
    // display overridden properties
    const overriddenPropsList = this.getSubmorphNamed('overridden props list');
    overriddenPropsList.items = this.getOverriddenProps();
    this.getSubmorphNamed('clear overridden prop button').deactivated = !overriddenPropsList.selection;

  }

  getMasterForTarget() {
    if (!this.target.isMorph) return;
    return [this.target, ...this.target.ownerChain()].map(m => m.master).find(Boolean);
  }

  clearSelectedProperty() {
    let prop = this.getSubmorphNamed('overridden props list').selection;
    this.getMasterForTarget().clearOverriddenPropertiesFor(this.target, [prop]);
    this.refreshButtons();
  }

  getOverriddenProps() {
    let master = this.getMasterForTarget();
    if (!master || !master._overriddenProps) return [];
    let props = master._overriddenProps.get(this.target);
    if (!props) return [];
    return Object.keys(props);
  }

}

LivelyTopBar.index.js:
import { Morph } from "lively.morphic/morph.js";
import { Icon } from "lively.morphic/index.js";
import { pt, Color } from "lively.graphics/index.js";
import { arr } from "lively.lang/index.js";
import { LoadingIndicator } from "lively.components/index.js";
import { resource } from "lively.resources/index.js";

export default class LivelyTopBar extends Morph {

  static get properties() {
    return {
      activeSideBars: {},
      currentShapeMode: {
        after: ['submorphs'],
        set(shapeName) {
          this.setProperty('currentShapeMode', shapeName);
          this.get('shape status icon').textAndAttributes = Icon.textAttribute(this.shapeToIcon[shapeName].args[0]);
        }
      },
      sideBarToIcon: {
        readOnly: true,
        get() {
          return {
            "Scene Graph": { args: ["sitemap", { textStyleClasses: ['fas']}]},
            "Styling Palette": { args: ["palette", { textStyleClasses: ['fas']}]},  
          }
        }
      },
      shapeToIcon: {
        readOnly: true,
        get() {
          return {
            "Rectangle": { shortcut: "R", args: ["square", { textStyleClasses: ['fas']}]},
            "Ellipse": { shortcut: "E", args: ["circle", { textStyleClasses: ['fas']}]},
            "Image": { shortcut: "I", args: ["image", { textStyleClasses: ['fas'], paddingTop: '1px'}]},
            "Path": { shortcut: "P", args: ["bezier-curve", { fontSize: 13, paddingTop: '3px' }]},
            "Polygon": { shortcut: "Q", args: ["draw-polygon", { fontSize: 17 }]},
            "Label": { shortcut: "L", args: ["tag", { paddingTop: '1px'}]}, 
            "Canvas": { shortcut: "C", args: ["chess-board", { paddingTop: '1px' }]},
            "HTML": { shortcut: "H", args: ["code", { paddingTop: '1px' }]},            
          }
        }
      }
    }
  }

  beforePublish() {
    this.activeSideBars = [];
    this.currentShapeMode = "Rectangle";
    this.setEditMode("Halo");
  }

  async onLoad() {
    if (this.isComponent) return; // only with instances
    await this.whenRendered();
    document.body.style.background = 'black';
    this.setEditMode("Halo");
    this.getSubmorphNamed('fast load toggler').refresh();
  }

  // this.relayout()

  relayout() {
    if (this.respondsToVisibleWindow) {
      this.width = this.world().visibleBounds().width;
      this.position = pt(0, 0);
    }
    const flap = this.getSubmorphNamed('user flap');
    const statusBar = this.get('ipad status bar');
    statusBar.width = this.width;
    flap.right = this.width - 10;
  }

  getShapeMenuItems() {
    return Object.entries(this.shapeToIcon).map(([shapeName, {shortcut, args}]) => {
      return [
        [
         ...this.currentShapeMode === shapeName ? [
           ...Icon.textAttribute("check", {
             fontSize: 11,
             paddingTop: '2px',
           }), "   ", {}
         ] : ["     ", {}], 
         ...Icon.textAttribute(...args), `   ${shapeName} `, { float: "none"},
          "      " + shortcut, { float: "right", fontSize: 11, opacity: .5, fontFamily: "IBM Plex Mono" }
        ], () => {
          this.currentShapeMode = shapeName;
          this.setEditMode("Shape");
        }
      ]
    });
  }

  getSideBarMenuItems() {
    return Object.entries(this.sideBarToIcon).map(([sideBarName, {args}]) => {
      return [
        [
         ...this.activeSideBars.includes(sideBarName) ? [
           ...Icon.textAttribute("check", {
             fontSize: 11,
             paddingTop: '2px',
           }), "   ", {}
         ] : ["     ", {}], 
         ...Icon.textAttribute(...args), `   ${sideBarName} `, { float: "none"},
        ], () => this.openSideBar(sideBarName)
      ]
    });
  }

  reloadSidebar() {
    this.sideBar.remove();
    this.sideBar = null;
    this.stylingPalette.remove();
    this.stylingPalette = null
    this.openSideBar('Scene Graph');
    this.openSideBar('Styling Palette');
  }

  onKeyUp(evt) {
    if (this._currentEditMode == "Hand") {
      this.setEditMode("Hand", true);
      return;        
    }
  }

  onKeyDown(evt) {
    super.onKeyDown(evt);

    if (evt.isCommandKey()) {
      // temporary toggle halo mode
      if (this._currentEditMode == "Hand") {
        this.setEditMode("Halo", true);
        return;        
      }
    }

    if (evt.isAltDown()) {
      return;
    }

    const keyToClass = {
      R: "Rectangle",
      E: "Ellipse",
      I: "Image",
      P: "Path",
      Q: "Polygon",
      L: "Label",
      C: "Canvas",
      H: "HTML",
    }

    switch (evt.key) {
      case "Escape":
        this.setEditMode("Halo");
        break;
      case "R":
      case "E":
      case "I":
      case "P":
      case "Q":
      case "L":
      case "C":
      case "H":
        this.currentShapeMode = keyToClass[evt.key];
        this.setEditMode("Shape");
        break;
      case "T":
        this.setEditMode("Text");
        break;
    }
  }

  // this.reloadSidebar()

  async openSideBar(name) {
    if (this.activeSideBars.includes(name))
      arr.remove(this.activeSideBars, name);
    else
      this.activeSideBars.push(name);

    if (name == 'Scene Graph') {
      if (!this.sideBar) {
        let li = LoadingIndicator.open('loading side bar');
        this.sideBar = await resource('part://SystemIDE/scene graph side bar master').read();
        this.sideBar.hasFixedPosition = true;
        this.sideBar.openInWorld();
        this.sideBar.right = 0;
        await this.sideBar.whenRendered();
        li.remove();
      }
      this.sideBar.toggle(this.activeSideBars.includes('Scene Graph'));
    }

    if (name == 'Styling Palette') {
      if (!this.stylingPalette) {
        let li = LoadingIndicator.open('loading side bar');
        this.stylingPalette = await resource('part://SystemIDE/styling side bar master').read();
        this.stylingPalette.hasFixedPosition = true;
        this.stylingPalette.openInWorld()
        this.stylingPalette.left = this.world().right;
        await this.stylingPalette.whenRendered();
        li.remove();
      }
      this.stylingPalette.toggle(this.activeSideBars.includes('Styling Palette'));
    }

  }

  onMouseDown(evt) {
    const selector = this.get('select shape type');
    const shapeModeButton = this.get('shape mode button');
    const sideBarSelector = this.get('side bar selector');
    if (evt.targetMorph == selector) {
      let menu = this.world().openWorldMenu(evt, this.getShapeMenuItems());
      menu.position = shapeModeButton.globalBounds().bottomLeft().subPt(this.world().scroll);
    }

    if (evt.targetMorph == shapeModeButton) {
      this.setEditMode("Shape");
    }

    if (evt.targetMorph.name == 'text mode button') {
      this.setEditMode("Text");
    }

    if (evt.targetMorph.name == 'hand mode button') {
      this.setEditMode("Hand")
    }

    if (evt.targetMorph.name == 'halo mode button') {
      this.setEditMode("Halo");
    }

    if (evt.targetMorph.name == 'open component browser') {
      this.interactivelyLoadComponent();
    }

    if (evt.targetMorph == sideBarSelector) {
      let menu = this.world().openWorldMenu(evt, this.getSideBarMenuItems());
      menu.position = sideBarSelector.globalBounds().bottomLeft().subPt(this.world().scroll);
    }
  }

  async interactivelyLoadComponent() {
    const label = this.get('open component browser');
    const { haloShadow, haloColor } = this.get('user flap');
    const defaultColor = Color.rgb(102,102,102);
    label.fontColor = haloColor;
    label.dropShadow = haloShadow;
    await this.world().execCommand('browse and load component');
    label.dropShadow = false;
    label.fontColor = defaultColor;
  }

  setEditMode(mode, shallow = false) {
    const { haloShadow, haloColor } = this.get('user flap');
    const defaultColor = Color.rgb(102,102,102);
    if (!shallow) this._currentEditMode = mode;

    const shapeModeButton = this.get('shape mode button');
    const textModeButton = this.get('text mode button');
    const handModeButton = this.get('hand mode button');
    const haloModeButton = this.get('halo mode button');

    [
      ["Shape", shapeModeButton.submorphs],
      ["Text", [textModeButton]],
      ["Hand", [handModeButton]],
      ["Halo", [haloModeButton]],
    ].forEach(([modeName, morphsToUpdate]) => {
      if (mode == "Shape")
        this.world().toggleShapeMode(true, this.currentShapeMode);
      else if (mode == "Text")
        this.world().toggleShapeMode(true, "Text");
      else
        this.world().toggleShapeMode(false);

      this.world().showHaloPreviews(mode == "Halo");

      if (modeName == mode) {
        morphsToUpdate.forEach(m => {
          m.dropShadow = haloShadow;
          m.fontColor = haloColor;
        })
      } else {
        morphsToUpdate.forEach(m => {
          m.dropShadow = null;
          m.fontColor = defaultColor;
        })
      }
    });
  }
}

FastLoadToggler.index.js:
import { Morph, Icon } from "lively.morphic";
import { Color } from "lively.graphics/index.js";

export default class FastLoadToggler extends Morph {

  static get properties() {
    return {
      loadConfig: {
        derived: true,
        get() {
          return JSON.parse(localStorage.getItem('lively.load-config') || '{}');
        },
        set(data) {
          localStorage.setItem('lively.load-config', JSON.stringify(data));
        }
      }      
    }
  }

  onLoad() {
    // initialize on cold load
    if (!localStorage.getItem('lively.load-config')) {
      localStorage.setItem('lively.load-config', JSON.stringify({
        "lively.lang":"dynamic",
        "lively.ast":"dynamic",
        "lively.source-transform":"dynamic",
        "lively.classes":"dynamic",
        "lively.vm":"dynamic",
        "lively.modules":"dynamic",
        "lively.user":"dynamic",
        "lively.storage":"dynamic",
        "lively.morphic":"dynamic"
      }));
    }
  }

  onMouseDown(evt) {
    super.onMouseDown(evt);
    this.toggleFastLoad();
  }

  refresh() {
    const toggleIndicator = this.getSubmorphNamed('toggle indicator');
    const label = this.getSubmorphNamed('label');
    const bolt = this.getSubmorphNamed('bolt');
    let active = Object.values(this.loadConfig).every(v => v == 'frozen');
    const color = active ? this.owner.haloColor : this.owner.get('label').fontColor;
    toggleIndicator.fontColor = label.fontColor = bolt.fontColor = color;
    toggleIndicator.textAndAttributes = Icon.textAttribute(active ? 'toggle-on' : 'toggle-off');
  }

  toggleFastLoad() {
    const { loadConfig } = this;
    let active = Object.values(loadConfig).every(v => v == 'frozen');
    for (let key in loadConfig) loadConfig[key] = active ? 'dynamic' : 'frozen';
    this.loadConfig = loadConfig;
    this.refresh();
  }

}

