import { Morph, touchInputDevice, morph, Icon, easings } from "lively.morphic";
import { pt, Color } from "lively.graphics/index.js";
import { fun } from "lively.lang/index.js";
import { connect } from "lively.bindings/index.js";
import { TreeData } from "lively.components";

export class SettingsTree extends TreeData {
  static default () {
    return new this({
      isCollapsed: false,
      children: [
        {
          isCollapsed: true,
          name: [' ', {}, ...Icon.textAttribute('font'), ' Rich Text', { fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' ', {}, ...Icon.textAttribute('image'), ' Shape', { fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' ', {}, ...Icon.textAttribute('border-style'), ' Border', { paddingLeft: '2px', fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' ', {}, ...Icon.textAttribute('grip-vertical'), ' Layout', { paddingLeft: '4px', fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        }
      ]
    });
  }

  static border () {
    return new this({
      isCollapsed: false,
      children: [
        {
          isCollapsed: true,
          name: [' Type    ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Color   ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Width  ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Radius ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        }
      ]
    });
  }

  display (node) {
    if (node.panel) return [node.panel, {}];
    else return node.name;
  }

  collapse (node, bool) {
    node.isCollapsed = bool;
  }

  isCollapsed (node) {
    return node.isCollapsed;
  }

  isLeaf (node) {
    return !node.children;
  }

  getChildren (node) {
    if (node === this.root) {
      return node.children.filter(n => {
        if (this.target && n.applicabilityCheck) return eval(n.applicabilityCheck)(this.target);
        return true;
      });
    }
    return node.children;
  }
}



export default class StylingSideBar extends Morph {
  static get properties () {
    return {
      controls: {},
      richTextControl: {},
      isHaloItem: {
        get () {
          return true;
        }
      }
    };
  }

  relayout () {
    this.onWorldResize();
  }

  collapseAll () {
    this.getSubmorphsByStyleClassName('Tree').forEach(t => {
      t.nodes.forEach(n => t.treeData.root == n ? t.uncollapse(n) : t.collapse(n));
    });
  }

  onWorldResize (align = true) {
    if (!this.respondsToVisibleWindow) return;
    const bounds = $world.visibleBounds();
    const offsetTop = navigator.standalone && touchInputDevice ? 25 : 0;
    const hr = this.getSubmorphNamed('horizontal resizer');

    this.height = bounds.height - offsetTop;
    this.top = offsetTop + bounds.top();
    hr.height = this.height;
    hr.left = 0;
    hr.top = 0;
    if (!align) return;
    if (this.visible) {
      this.topRight = bounds.topRight();
    } else this.topLeft = bounds.topRight();
  }

  async toggle (active) {
    const bounds = $world.visibleBounds();
    const offsetTop = navigator.standalone && touchInputDevice ? 25 : 0;
    this.height = bounds.height - offsetTop;
    this.top = offsetTop + bounds.top();
    if (active) {
      $world.addMorph(this, $world.get('lively top bar'));
      this.topLeft = bounds.topRight();
      this.visible = true;
      await this.whenRendered();
      this.onWorldResize(false);
      await this.animate({
        opacity: 1,
        easing: easings.outCirc,
        topRight: bounds.topRight(),
        duration: 300
      });
      this.attachToWorld($world);
    } else {
      this.detachFromWorld($world);
      await this.animate({
        opacity: 0,
        topLeft: bounds.topRight(),
        duration: 300
      });
      this.visible = false;
      this.remove();
    }
  }

  cleanupAnchors () {
    const morphsWithEmptyAnchors = this.withAllSubmorphsSelect(m => m.anchors && m.anchors.filter(a => !a.id).length);
    for (const m of morphsWithEmptyAnchors) {
      m.anchors.forEach(a => !a.id && m.removeAnchor(a));
    }
    return morphsWithEmptyAnchors.length;
  }

  // fixme: connect

  attachToWorld (world) {
    connect(world, 'showHaloFor', this, 'focusMorph', {
      garbageCollect: true
    });
  }

  detachFromWorld (world) {
    world.attributeConnections.forEach(conn => {
      if (conn.targetObj === this) conn.disconnect();
    });
  }

  focusMorph (target) {
    if (target.isMorph && target.ownerChain().includes(this)) return;
    if (target.isLabel || target.isText || target.isButton) {
      this.richTextControl.focusOn(target, false);
    } else {
      // temporary deactivate the rich text interface not to confuse the user
    }
    const tree = this.getSubmorphNamed('style settings tree');
    tree.treeData.target = target;
    tree.update(true);
    Object.values(this.controls).forEach(control => control && control.focusOn(target));
  }

  onHierarchyChange () {
    Object.values(this.controls).forEach(control => control && control.update());
  }
}

ShadowWidget.index.js:
import { Morph } from "lively.morphic/morph.js";
import { ShadowPopover } from "lively.ide/styling/style-popover.js";
import { connect, signal } from "lively.bindings/index.js";

export default class ShadowWidget extends Morph {

  static get properties() {
    return {
      shadowValue: {
        set(v) {
          this.setProperty('shadowValue', v);
          this.update(v);
        }
      }
    }
  }

  onHoverIn() {
    this.getSubmorphNamed('drop down indicator').animate({
      opacity: 1, duration: 200
    });
  }

  onHoverOut() {
    this.getSubmorphNamed('drop down indicator').animate({
      opacity: 0, duration: 200
    });
  }

  onMouseDown(evt) {
    this.openPopover();
  }

  update(shadow) {
    let preview = this.submorphs[0];
    if (preview) preview.dropShadow = shadow;
  }

  async openPopover() {
    let shadowEditor = new ShadowPopover({
      hasFixedPosition: !!this.ownerChain().find(m => m.hasFixedPosition),
      shadowValue: this.shadowValue
    });
    await shadowEditor.fadeIntoWorld(this.globalBounds().center());
    connect(shadowEditor, "shadowValue", this, "shadowValue");
    connect(this, "shadowValue", this, "update");
    signal(this, "openWidget", shadowEditor);
  }

}

MullerColumnView.index.js:
import { Morph } from 'lively.morphic/morph.js';
import { TreeData, List } from 'lively.components/index.js';
import { HTTPFileBrowserNode } from 'lively.ide/http-file-browser.js';
import { resource } from 'lively.resources/index.js';
import { arr, promise } from 'lively.lang/index.js';
import { connect, signal, noUpdate } from 'lively.bindings/index.js';
import { pt } from 'lively.graphics/index.js';
import { PackageTreeData } from 'lively.ide/js/browser/index.js';

export default class MullerColumnView extends Morph {
  static get properties () {
    return {
      treeData: {
        // this.treeData
      },
      listMaster: {
        defaultValue: 'styleguide://SystemWidgets/list/light',
        set (url) {
          this.setProperty('listMaster', url);
          this.refresh(false);
        }
      }
      // muller columns are essentially a different style of tree view
      // the main different is that only one chain of collapsed nodes
      // is displayed at once. This in turn requires that of all the nodes
      // children, at most one can be collapsed at a time.
    };
  }

  truncateNameIfNeeded (displayedName) {
    // based on the muller column it is going to be rendered in, adjust the truncation
    return displayedName;
  }

  isSelected (node) {
    return !node.isCollapsed;
  }

  async example () {
    this.setTreeData(new PackageTreeData({
      browser: this.owner
    }));
  }

  async setTreeData (treeData) {
    this.treeData = treeData;
    await treeData.collapse(treeData.root, false);
    await this.refresh(false);
  }

  reset () {
    this.submorphs = [];
  }

  async selectNode (aNode, animated = true) {
    this._selectedNode = aNode;
    const parent = this.treeData.parentNode(aNode);
    await Promise.all(this.treeData.getChildren(parent).map(child => {
      if (child !== aNode && !this.treeData.isCollapsed(child)) {
        return this.treeData.collapse(child, true);
      }
      if (child === aNode) return this.treeData.collapse(child, false);
    }));
    await this.refresh(animated);
    signal(this, 'selectionChange', this.getExpandedPath());
  }

  getExpandedPath () {
    const td = this.treeData;
    if (!td) return [];
    let currentChildren = td.getChildren(td.root);
    let nextNode;
    const expandedPath = [td.root];
    while (nextNode = currentChildren && currentChildren.find(aChild => !td.isCollapsed(aChild))) {
      expandedPath.push(nextNode);
      currentChildren = td.getChildren(nextNode);
    }
    return expandedPath;
  }

  /*
    Often we want to expand a path that has never been expanded before.
    Depending on the implementation of the treeData the nodes that need to
    be visited may not even be present yet (lazy initialization).
    This is why we expand each node with a custom iterator.
  */
  async setExpandedPath (
    matchingNodes = (n) => n == this.treeData.root,
    until = this.treeData.root,
    animated = true
  ) {
    const td = this.treeData;
    const p = this.getExpandedPath();
    const currentPath = p.slice(p.indexOf(until));
    await Promise.all(currentPath.map(node => td.collapse(node, true))); // dont collapse root
    await td.uncollapseAll(matchingNodes, 0, until);
    await this.refresh(animated);
    this.submorphs.forEach(list => list.scrollSelectionIntoView());
  }

  newList () {
    const list = new List({
      master: this.listMaster,
      width: 200
    });
    connect(list, 'selection', this, 'selectNode');
    return list;
  }

  async ensureLists (numberOfLists, animated = true) {
    let newSubmorphs = this.submorphs; let scroll;
    const lenDiff = numberOfLists - this.submorphs.length;
    if (lenDiff > 0) {
      newSubmorphs = [...newSubmorphs, ...arr.genN(lenDiff, () => this.newList())];
      this.submorphs = newSubmorphs;
      await this.whenRendered(); // ensure layout has placed all morphs accordingly
      if (this.layout.renderViaCSS) // thank you virtual-dom....
      { newSubmorphs.map(m => this.layout.expressMeasure(m)); }
      scroll = pt(this.scrollExtent.x - this.width);
      if (animated) {
        this.animate({
          scroll,
          duration: 200
        });
      } else this.scroll = scroll;
    } else if (lenDiff < 0) {
      newSubmorphs = newSubmorphs.slice(0, lenDiff);
      scroll = pt(arr.last(newSubmorphs).right - this.width);
      if (animated) {
        await this.animate({
          scroll,
          duration: 200
        });
      } else this.scroll = scroll;
      this.submorphs = newSubmorphs;
    }
    return newSubmorphs;
  }

  relayout () {
    let scrollBarHeight = 0;
    if (this.scrollExtent.x > this.width + this.scrollbarOffset.x) {
      scrollBarHeight = 5;
    }
    this.submorphs.forEach(list => {
      list.height = this.height - scrollBarHeight;
      const control = list._managedNode && list._managedNode.listControl;
      if (control) {
        control.bottomRight = list.innerBounds().bottomRight().subXY(5, 0);
      }
    });
  }

  onHoverOut (evt) {
    super.onHoverOut(evt);
    this.submorphs.forEach(list => {
      const control = list._managedNode && list._managedNode.listControl;
      if (control) control.visible = false;
    });
  }

  onMouseMove (evt) {
    super.onMouseMove(evt);
    const hoveredList = this.submorphs.find(list => list.fullContainsWorldPoint(evt.position));
    if (hoveredList) {
      this.submorphs.forEach(list => {
        const control = list._managedNode && list._managedNode.listControl;
        if (control) {
          control.visible = list == hoveredList;
        }
      });
    }
  }

  get keybindings () {
    return [
      { keys: 'Left', command: 'select previous entry of last list' },
      { keys: 'Right', command: 'select first entry of next list' }
    ].concat(super.keybindings);
  }

  get commands () {
    return [
      {
        name: 'select previous entry of last list',
        exec: () => {
          const nextNode = this.treeData.parentNode(this._selectedNode);
          this.selectNode(nextNode);
        }
      },
      {
        name: 'select first entry of next list',
        exec: () => {
          const current = this._selectedNode;
          const nextNode = current.subNodes && current.subNodes[0];
          if (nextNode) {
            this.selectNode(nextNode);
          }
        }
      }
    ];
  }

  async refresh (animated = true) {
    const td = this.treeData;
    if (!td) return;
    let expandedPath = this.getExpandedPath(); let selectedFile;
    const last = arr.last(expandedPath);
    if (td.isLeaf(last) && last != td.root) {
      selectedFile = arr.last(expandedPath);
      expandedPath = expandedPath.slice(0, -1);
    }
    const lists = await this.ensureLists(expandedPath.length, animated); // always ensure the root list exists
    let currentList;
    expandedPath.forEach(node => {
      if ((!td.isCollapsed(node)) && currentList) {
        noUpdate(() => {
          currentList.selection = node;
          currentList.focus();
        });
      }
      currentList = lists.shift();
      noUpdate(() => currentList.selection = false);
      const originalScroll = currentList.itemScroll;
      if (currentList._managedNode == node) {
        currentList.itemScroll = originalScroll;
        const currentItems = currentList.items;
        const newItems = td.getChildren(node);
        if (newItems.length == currentItems.length) {
          arr.zip(currentItems, newItems).forEach(([item, value]) => {
            if (!value) return;
            item.value = value;
            if (item.value.isDirty) {
              item.value.isDirty = false;
              item.label = td.display(item.value);
            }
          });
        } else currentList._managedNode = null;
      }
      if (currentList._managedNode != node) {
        currentList.items = td.getChildren(node).map(each => {
          return { isListItem: true, label: td.display(each), value: each, tooltip: each.tooltip || false };
        });
        currentList._managedNode = node;
      }
      if (node.listControl) { currentList.submorphs = currentList.submorphs.slice(0, 2).concat([node.listControl]); }
    });
    if (selectedFile) {
      noUpdate(() => {
        currentList.selection = selectedFile;
        currentList.focus();
      });
    }
    this.relayout();
  }
}

