    return this.lower(string.camelCaseString(fieldName + (sub ? ` ${sub}` : '')))
  }

  unwrap(mainFieldName, prop, targetProp) {
    let fields = {
      [this.lower(string.camelCaseString(mainFieldName))]: [mainFieldName, prop, targetProp]
    }
    for (let side of ['top', 'left', 'bottom', 'right']) {
      let subFieldName = mainFieldName + ' ' + side;
      fields[this.lower(string.camelCaseString(subFieldName))] = [subFieldName, prop, this.join(targetProp, side)];
    }
    return fields;
  }

  attachToWorld() {
    connect($world, 'showHaloFor', this, 'focusOn', {
      garbageCollect: true
    });
  }

  async focusOn(target) {
    if (target && !this.isAncestorOf(target) 
        && this.target !== target
        && !obj.isArray(target))
      this.target = target;

    let visible = this.visible;
    this.visible = true;
    await this.whenRendered();
    this.update();
    await this.whenRendered();
    this.getSubmorphsByStyleClassName('NumberWidget').map(m => m.relayout());
    this.visible = visible;
  }

  onHoverIn(evt) {
    this.watchForTarget = false;
  }

  onHoverOut(evt) {
    this.watchForTarget = true;
  }

}

ShapeLayoutControl.index.js:
import { Morph, morph } from "lively.morphic";
import * as layouts from 'lively.morphic/layout.js';
import { string } from "lively.lang/index.js";
import { connect, noUpdate } from "lively.bindings/index.js";
import { InteractiveMorphSelector } from "lively.halos/morph.js";
import { Color, pt } from "lively.graphics/index.js";

export default class ShapeLayoutControl extends Morph {

  static get properties() {
    return {
      managedProps: {
        get() {
          return [
            ['type', 'selection', true], 
            ['align', 'selection'],
            ['direction', 'selection'],
            ['axis', 'selection'],
            ['spacing', 'number'],
            ['auto resize', 'checked'], 
            ['resize submorphs', 'checked'],
            ['x axis policy', 'selection', true],
            ['y axis policy', 'selection', true],
            ['react to submorph animations', 'checked'],
            ['submorph settings', false, true],
            ['order by index', 'checked'],
            ['dragme', false, true],
            ['configure grid layout', false, true]
          ];
        }
      },
      ui: {
        readOnly: true,
        get() {
          let ui = {
            labelContainer: this.getSubmorphNamed('label container'),
            controlContainer: this.getSubmorphNamed('control container')
          };
          for (let [prop] of this.managedProps) {
            let labelName = prop + ' label';
            let controlName = prop + ' control';
            ui[this.join(labelName)] = this.getSubmorphNamed(labelName);
            ui[this.join(controlName)] = this.getSubmorphNamed(controlName);
          }
          return ui;
        }
      }
    }
  }

  // this.focusOn(this.get('tester'))

  focusOn(target) {
    this.target = target;
    this.update(false);
  }

  setupConnections() {
    let ui = this.ui;
    connect(ui.submorphSettingsControl, 'fire', this, 'chooseSubmorphToChangeLayoutSettings');
    connect(ui.dragmeControl, 'onDragStart', this, 'onSubmorphSettingsDragStart');
    connect(ui.xAxisPolicyControl, 'selection', this, 'updateSubmorphProportionalLayoutSettings', {
      converter: `policy => ({
        policy,
        axis: "x",
        submorph: self.selectedSubmorph,
      })`,
      varMapping: {
        self: this
      }
    });
    connect(ui.yAxisPolicyControl, 'selection', this, 'updateSubmorphProportionalLayoutSettings', {
      converter: `policy => ({
        policy,
        axis: "y",
        submorph: self.selectedSubmorph,
      })`,
      varMapping: {
        self: this
      }
    });

    for (let [propName, accessor, customConnect] of this.managedProps) {
      if (customConnect)
        continue;
      let prop = this.join(propName),
          control = ui[this.join(propName, 'control')];
      connect(control, accessor, this, 'updateLayout', {
        updater: ($upd, val) => {
          $upd(prop, val);
        }, varMapping: { prop }
      });
    }
  }

  lower(s) { return s.charAt(0).toLowerCase() + s.slice(1); }

  join(fieldName, sub) {
    return this.lower(string.camelCaseString(fieldName + (sub ? ` ${sub}` : '')))
  }

  updateLayoutOfTarget(layout) {
    let layoutClass = layouts[string.camelCaseString(layout)];
    this.target.layout = layoutClass ? new layoutClass({ autoResize: false }) : null;
    this.update();
  }

  updateLayout(prop, value) {
    this.target.layout[prop] = value;
  }

  update(refresh = true) {
    if (!this.target) return;
    noUpdate(() => {
      let ui = this.ui;
      let layout = this.target.layout || { name: () => 'No'};
      let i = 0;
      for (let [prop, accessor, updater] of this.managedProps) {
        i += 5;
        let control = ui[this.join(prop, 'control')];
        let label = ui[this.join(prop, 'label')];

        if (!accessor) continue;
        if (prop === 'type') {
          control.selection = layout.name() + " Layout";
          continue;
        }

        let value = layout[this.join(prop)];
        let propApplicable = typeof value !== 'undefined';
        if (propApplicable) {
          switch (prop) {
            case 'align':
              control.items = layout.possibleAlignValues; break;
            case 'direction':
              control.items = layout.possibleDirectionValues; break;
            case 'axis':
              control.items = layout.possibleAxisValues; break;            
          }
          control[accessor] = value;
        }
        label.isLayoutable = label.visible = propApplicable;
      }
      this.showGridLayoutControl(layout.name() === 'Grid');
      this.showProportionalControl(layout.name() === 'Proportional');
    });
  }

  // proportional layout stuff

  updateSubmorphProportionalLayoutSettings({ policy, axis, submorph }) {
    this.target.layout.changeSettingsFor(submorph, {
      [axis]: policy
    }, true)
  }

  onSubmorphSettingsDragStart(evt) {
    evt.stop();
    let layout = this.target.layout;
    let settings = layout.settingsFor(this.selectedSubmorph), descr = [];
    for (let name in settings) descr = [...descr, name + ': ', { fontWeight: 'bold'}, settings[name] + ' ', {}];
    let grabme = morph({
      type: "label", fontColor: Color.white,
      value: descr, fill: Color.black.withA(.7),
      padding: 5, borderRadius: 10,
      isLayoutable: false
    });
    grabme.wantsToBeDroppedOn = (dropTarget) => layout.layoutableSubmorphs.includes(dropTarget);
    grabme.onBeingDroppedOn = (hand, dropTarget) => {
      grabme.remove();
      let target = layout.layoutableSubmorphs.includes(dropTarget) ? dropTarget :
        evt.world.morphsContainingPoint(evt.hand.position).find(ea =>
          layout.layoutableSubmorphs.includes(ea));
      if (target) {
        this.updateSubmorphProportionalLayoutSettings({policy: settings.x, axis: "x", submorph: target});
        this.updateSubmorphProportionalLayoutSettings({policy: settings.y, axis: "y", submorph: target});
        target.show();
        $world.setStatusMessage("layout settings applied");
      }
    };
    evt.hand.grab(grabme);
    grabme.moveBy(pt(10,10));
  }

  async chooseSubmorphToChangeLayoutSettings() {
    let morphs = this.target.layout.layoutableSubmorphs, submorph;
    // prevent drawer from loosing focus
    this.manageFocus = true;
    if (this.env.eventDispatcher.isKeyPressed("Shift")) {
      let items = morphs.map(m => ({isListItem: true, string: String(m), value: m}));
      ({selected: [submorph]} = await $world.listPrompt(
        "Select morph", items, {onSelection: ea => ea.show()}));
    } else {
      submorph = await InteractiveMorphSelector.selectMorph(
        this.world(), this, target => morphs.includes(target));
    }
    this.manageFocus = false;
    // stop prevention
    this.selectedSubmorph = submorph;
    if (!submorph) return;

    let { xAxisPolicyControl, yAxisPolicyControl } = this.ui;
    let submorphSettings = this.target.layout.settingsFor(submorph);
    noUpdate(() => {
      xAxisPolicyControl.selection = submorphSettings.x;
      yAxisPolicyControl.selection = submorphSettings.y;
    });
  }

  showProportionalControl(active) {
    let { submorphSettingsControl, dragmeControl,
          xAxisPolicyControl, xAxisPolicyLabel,
          yAxisPolicyControl, yAxisPolicyLabel
        } = this.ui;
    [
     submorphSettingsControl, dragmeControl,
     xAxisPolicyControl, xAxisPolicyLabel,
     yAxisPolicyControl, yAxisPolicyLabel
    ].forEach(elem => {
        elem.isLayoutable = active;
        elem.visible = active;
    });
  }

  showGridLayoutHalo() {
    $world.showLayoutHaloFor(this.target);
  }

  showGridLayoutControl(active) {
    let button = this.ui.configureGridLayoutControl;
    button.visible = button.isLayoutable = active;
  }
}

MasterComponentControl.index.js:
import { Morph } from "lively.morphic/morph.js";
import { resource } from "lively.resources/index.js";
import { arr, obj, string, Path } from "lively.lang/index.js";
import { connect } from "lively.bindings/index.js";
import { getMastersMenu, resolvedMasters } from "lively.morphic/style-guide.js";

export default class MasterComponentControl extends Morph {
  focusOn (target) {
    this.target = target;
    this.update();
  }

  async onMouseUp (evt) {
    let stateToModify;
    if (evt.targetMorph.name == 'auto master selection') {
      stateToModify = 'auto';
    }
    if (evt.targetMorph.name == 'hover master selection') {
      stateToModify = 'hover';
    }
    if (evt.targetMorph.name == 'click master selection') {
      stateToModify = 'click';
    }

    this.refreshButtons();

    if (!this.target) return;

    for (const state of ['auto', 'hover', 'click']) {
      if (this.target.master &&
          this.target.master[state] &&
          !this.get(state + ' master checkbox').checked) {
        this.adjustMasterComponent(state, null);
      }
    }

    if (stateToModify) {
      const menu = this.openMenu(await this.getMastersMenu(stateToModify), evt);
    }
  }

  async getMastersMenu (stateToModify) {
    return await Promise.all([...Object.keys(resolvedMasters), this.world().name].map(async worldName => {
      const isLocal = worldName == this.world().name;
      return [
        isLocal ? 'This World' : worldName,
        this.componentsToMenu(await resource(`styleguide://${worldName}/`).dirList(), stateToModify)
      ];
    }));
  }

  componentsToMenu (components, state, items = null, depth = 1) {
    if (!items) {
      items = components.map(component => ({
        name: arr.last(component.name.split('/')),
        path: component.name.split('/'),
        value: component
      }));
    }

    if (items.length == 0) return [];
    if (depth > 100) return [];
    const self = this;
    return arr.flatten(Object.entries(arr.groupBy(items, c => c.path[depth - 1])).map(([name, entries]) => {
      const [components, categories] = arr.partition(entries, entry => entry.path.length <= depth);
      const subMenu = [
        name,
        this.componentsToMenu(null, state, categories, depth + 1)
      ];
      return [
        ...components.map(entry => {
          return [entry.name, () => { self.adjustMasterComponent(state, entry.value); }];
        }),
        ...subMenu[1].length ? [subMenu] : []
      ];
    }), 1);
  }

  async getComponentSelectionMenu (state) {
    const worldName = this.world().metadata.commit.name;
    const res = resource(`styleguide://${worldName}/`);
    const components = await res.dirList();
    const systemComponents = await resource('styleguide://System/').dirList();
    const localMenu = this.componentsToMenu(components, state);
    const remoteMenu = this.componentsToMenu(systemComponents, state);
    return [
      ...localMenu.length > 0
        ? [
            ...localMenu,
            { isDivider: true }
          ]
        : [],
      ...remoteMenu
    ];
  }

  adjustMasterComponent (state, component) {
    if (this.target.master) { this.target.master[state] = component; } else if (component) {
      this.target.master = {
        [state]: component
      };
    }
    const { auto, hover, click } = this.target.master;
    if (!auto && !hover && !click) this.target.master = null;
    this.update();
    this.target.withAllSubmorphsDo(m => delete m._parametrizedProps); // clear them all
    this.target.requestMasterStyling();
  }

  componentsToMenu (components, state, items = null, depth = 1) {
    if (!items) {
      items = components.map(component => ({
        name: arr.last(component.name.split('/')),
        path: component.name.split('/'),
        value: component
      }));
    }

    if (items.length == 0) return [];
    if (depth > 100) return [];
    const self = this;
    return arr.flatten(Object.entries(arr.groupBy(items, c => c.path[depth - 1])).map(([name, entries]) => {
      const [components, categories] = arr.partition(entries, entry => entry.path.length <= depth);
      const subMenu = [
        name,
        this.componentsToMenu(null, state, categories, depth + 1)
      ];
      return [
        ...components.map(entry => {
          return [entry.name, () => { self.adjustMasterComponent(state, entry.value); }];
        }),
        ...subMenu[1].length ? [subMenu] : []
      ];
    }), 1);
  }

  update () {
    if (!this.target) return;
    for (const state of ['auto', 'hover', 'click']) {
      const btn = this.getSubmorphNamed(state + ' master selection');
      const checkBox = this.getSubmorphNamed(state + ' master checkbox');
      if (Path(['master', state]).get(this.target)) checkBox.checked = true;
      else checkBox.checked = false;
      btn.label = string.truncateLeft(Path(['master', state, 'name']).get(this.target) || 'select master', 15, '...');
    }
    this.refreshButtons();
  }

  refreshButtons () {
    for (const state of ['auto', 'hover', 'click']) {
      const btn = this.getSubmorphNamed(state + ' master selection');
      const checkBox = this.getSubmorphNamed(state + ' master checkbox');
      const disabled = btn.deactivated = !checkBox.checked;

      this.getSubmorphNamed(state + ' master selection').opacity = disabled ? 0.5 : 1;
    }
    // display overridden properties
    const overriddenPropsList = this.getSubmorphNamed('overridden props list');
    overriddenPropsList.items = this.getOverriddenProps();
    this.getSubmorphNamed('clear overridden prop button').deactivated = !overriddenPropsList.selection;
  }

  getMasterForTarget () {
    if (!this.target.isMorph) return;
    return [this.target, ...this.target.ownerChain()].map(m => m.master).find(Boolean);
  }

  clearSelectedProperty () {
    const prop = this.getSubmorphNamed('overridden props list').selection;
    this.getMasterForTarget().clearOverriddenPropertiesFor(this.target, [prop]);
    this.refreshButtons();
  }

  getOverriddenProps () {
    const master = this.getMasterForTarget();
    if (!master || !master._overriddenProps) return [];
    const props = master._overriddenProps.get(this.target);
    if (!props) return [];
    return Object.keys(props);
  }
}

MullerColumnView.index.js:
import { Morph } from 'lively.morphic/morph.js';
import { TreeData, List } from 'lively.components/index.js';
import { HTTPFileBrowserNode } from 'lively.ide/http-file-browser.js';
import { resource } from 'lively.resources/index.js';
import { arr, promise } from 'lively.lang/index.js';
import { connect, signal, noUpdate } from 'lively.bindings/index.js';
import { pt } from 'lively.graphics/index.js';
import { PackageTreeData } from 'lively.ide/js/browser/index.js';

export default class MullerColumnView extends Morph {
  static get properties () {
    return {
      treeData: {
        // this.treeData
      },
      listMaster: {
        defaultValue: 'styleguide://SystemWidgets/list/light',
        set (url) {
          this.setProperty('listMaster', url);
          this.refresh(false);
        }
      }
      // muller columns are essentially a different style of tree view
      // the main different is that only one chain of collapsed nodes
      // is displayed at once. This in turn requires that of all the nodes
      // children, at most one can be collapsed at a time.
    };
  }

  truncateNameIfNeeded (displayedName) {
    // based on the muller column it is going to be rendered in, adjust the truncation
    return displayedName;
  }

  isSelected (node) {
    return !node.isCollapsed;
  }

  async example () {
    this.setTreeData(new PackageTreeData({
      browser: this.owner
    }));
  }

  async setTreeData (treeData) {
    this.treeData = treeData;
    await treeData.collapse(treeData.root, false);
    await this.refresh(false);
  }

  reset () {
    this.submorphs = [];
  }

  async selectNode (aNode, animated = true) {
    this._selectedNode = aNode;
    const parent = this.treeData.parentNode(aNode);
    await Promise.all(this.treeData.getChildren(parent).map(child => {
      if (child !== aNode && !this.treeData.isCollapsed(child)) {
        return this.treeData.collapse(child, true);
      }
      if (child === aNode) return this.treeData.collapse(child, false);
    }));
    await this.refresh(animated);
    signal(this, 'selectionChange', this.getExpandedPath());
  }

  getExpandedPath () {
    const td = this.treeData;
    if (!td) return [];
    let currentChildren = td.getChildren(td.root);
    let nextNode;
    const expandedPath = [td.root];
    while (nextNode = currentChildren && currentChildren.find(aChild => !td.isCollapsed(aChild))) {
      expandedPath.push(nextNode);
      currentChildren = td.getChildren(nextNode);
    }
    return expandedPath;
  }

  /*
    Often we want to expand a path that has never been expanded before.
    Depending on the implementation of the treeData the nodes that need to
    be visited may not even be present yet (lazy initialization).
    This is why we expand each node with a custom iterator.
  */
  async setExpandedPath (
    matchingNodes = (n) => n == this.treeData.root,
    until = this.treeData.root,
    animated = true
  ) {
    const td = this.treeData;
    const p = this.getExpandedPath();
    const currentPath = p.slice(p.indexOf(until));
    await Promise.all(currentPath.map(node => td.collapse(node, true))); // dont collapse root
    await td.uncollapseAll(matchingNodes, 0, until);
    await this.refresh(animated);
    this.submorphs.forEach(list => list.scrollSelectionIntoView());
  }

  newList () {
    const list = new List({
      master: this.listMaster,
      width: 200
    });
    connect(list, 'selection', this, 'selectNode');
    return list;
  }

  async ensureLists (numberOfLists, animated = true) {
    let newSubmorphs = this.submorphs; let scroll;
    const lenDiff = numberOfLists - this.submorphs.length;
    if (lenDiff > 0) {
      newSubmorphs = [...newSubmorphs, ...arr.genN(lenDiff, () => this.newList())];
      this.submorphs = newSubmorphs;
      await this.whenRendered();
      scroll = pt(this.scrollExtent.x - this.width);
      if (animated) {
        this.animate({
          scroll,
          duration: 200
        });
      } else this.scroll = scroll;
    } else if (lenDiff < 0) {
      newSubmorphs = newSubmorphs.slice(0, lenDiff);
      scroll = pt(arr.last(newSubmorphs).right - this.width);
      if (animated) {
        await this.animate({
          scroll,
          duration: 200
        });
      } else this.scroll = scroll;
      this.submorphs = newSubmorphs;
    }
    return newSubmorphs;
  }

  relayout () {
    let scrollBarHeight = 0;
    if (this.scrollExtent.x > this.width + this.scrollbarOffset.x) {
      scrollBarHeight = 5;
    }
    this.submorphs.forEach(list => {
      list.height = this.height - scrollBarHeight;
      const control = list._managedNode && list._managedNode.listControl;
      if (control) {
        control.bottomRight = list.innerBounds().bottomRight().subXY(5, 0);
      }
    });
  }

  onHoverOut (evt) {
    super.onHoverOut(evt);
    this.submorphs.forEach(list => {
      const control = list._managedNode && list._managedNode.listControl;
      if (control) control.visible = false;
    });
  }

  onMouseMove (evt) {
    super.onMouseMove(evt);
    const hoveredList = this.submorphs.find(list => list.fullContainsWorldPoint(evt.position));
    if (hoveredList) {
      this.submorphs.forEach(list => {
        const control = list._managedNode && list._managedNode.listControl;
        if (control) {
          control.visible = list == hoveredList;
        }
      });
    }
  }

  get keybindings () {
    return [
      { keys: 'Left', command: 'select previous entry of last list' },
      { keys: 'Right', command: 'select first entry of next list' }
    ].concat(super.keybindings);
  }

  get commands () {
    return [
      {
        name: 'select previous entry of last list',
        exec: () => {
          const nextNode = this.treeData.parentNode(this._selectedNode);
          this.selectNode(nextNode);
        }
      },
      {
        name: 'select first entry of next list',
        exec: () => {
          const current = this._selectedNode;
          const nextNode = current.subNodes && current.subNodes[0];
          if (nextNode) {
            this.selectNode(nextNode);
          }
        }
      }
    ];
  }

  async refresh (animated = true) {
    const td = this.treeData;
    if (!td) return;
    let expandedPath = this.getExpandedPath(); let selectedFile;
    const last = arr.last(expandedPath);
    if (td.isLeaf(last) && last != td.root) {
      selectedFile = arr.last(expandedPath);
      expandedPath = expandedPath.slice(0, -1);
    }
    const lists = await this.ensureLists(expandedPath.length, animated); // always ensure the root list exists
    let currentList;
    expandedPath.forEach(node => {
      if ((!td.isCollapsed(node)) && currentList) {
        noUpdate(() => {
          currentList.selection = node;
          currentList.focus();
        });
      }
      currentList = lists.shift();
      noUpdate(() => currentList.selection = false);
      const originalScroll = currentList.itemScroll;
      if (currentList._managedNode == node) {
        currentList.itemScroll = originalScroll;
        arr.zip(currentList.items, td.getChildren(node)).forEach(([item, value]) => {
          if (!value) return;
          item.value = value;
          if (item.value.isDirty) {
            item.value.isDirty = false;
            item.label = td.display(item.value);
          }
        });
      }
      if (currentList._managedNode != node) {
        currentList.items = td.getChildren(node).map(each => {
          return { isListItem: true, label: td.display(each), value: each, tooltip: each.tooltip || false };
        });
        currentList._managedNode = node;
      }
      if (node.listControl) { currentList.submorphs = currentList.submorphs.slice(0, 2).concat([node.listControl]); }
    });
    if (selectedFile) {
      noUpdate(() => {
        currentList.selection = selectedFile;
        currentList.focus();
      });
    }
    this.relayout();
  }
}

PathIndicator.index.js:
import { Morph, Icon } from "lively.morphic";
import { promise } from "lively.lang/index.js";

export default class PathIndicator extends Morph {
  static get properties () {
    return {
      ui: {
        get () {
          return {
            filePath: this.getSubmorphNamed('file path'),
            pathContainer: this.getSubmorphNamed('path container'),
            clipboardControls: this.getSubmorphNamed('clipboard controls'),
            statusBox: this.getSubmorphNamed('status box'),
            statusLabel: this.getSubmorphNamed('status label'),
            errorControls: this.getSubmorphNamed('error controls'),
            exportToHtml: this.getSubmorphNamed('export to html'),
            runTestsButton: this.getSubmorphNamed('run tests in module')
          };
        }
      }
    };
  }

  showInfoInWorkspace () {
    const content = this.ui.statusBox.textString;
    const title = content.split('\n')[0];
    this.world().execCommand('open workspace',
      { title, content, language: 'text' });
  }

  relayout () {
    const { filePath, clipboardControls, statusBox, errorControls } = this.ui;
    let pad = 10 + filePath.left;
    filePath.width = this.width - clipboardControls.width - pad;
    clipboardControls.right = this.width;
    statusBox.width = this.width - 30;
    errorControls.topRight = statusBox.bottomRight.withX(this.width);
  }

  adjustHeight () {
    const { statusBox, clipboardControls, pathContainer, errorControls } = this.ui;
    if (this.ui.statusBox.opacity > 0) {
      this.height = errorControls.bottom;
    } else {
      this.height = 50;
    }
  }

  setPath (path) {
    const { filePath, clipboardControls, exportToHtml } = this.ui;
    clipboardControls.opacity = 1;
    filePath.value = path;
    exportToHtml.isLayoutable = exportToHtml.visible = filePath.textString.includes('.md');
  }

  toggleTestButton (active) {
    this.ui.runTestsButton.visible = this.ui.runTestsButton.isLayoutable = active;
  }

  // this.showInactive(300)

  showInactive (duration = 300) {
    this.requestTransition(async () => {
      this.ui.filePath.value = 'No file selected';
      this.master = 'styleguide://$world/file status/inactive';
      this.master.applyAnimated({ duration });
      await this.withAnimationDo(() => {
        this.ui.statusLabel.isLayoutable = false;
        this.ui.statusLabel.opacity = 0;
        this.ui.statusBox.opacity = 0;
        this.ui.clipboardControls.opacity = 0.5;
        this.adjustHeight();
      }, { duration });
    });
  }

  showDefault (duration = 300) {
    this.requestTransition(async () => {
      this.master = 'styleguide://$world/file status/default';
      this.master.applyAnimated({ duration });
      await this.withAnimationDo(() => {
        this.ui.statusLabel.isLayoutable = false;
        this.ui.statusLabel.opacity = 0;
        this.ui.statusBox.opacity = 0;
        this.adjustHeight();
      }, { duration });
    });
  }

  // this.showSaved(100)
  // this.showDefault()
  // this.showFrozen()
  // this.showError('hellloo')

  async showError (err, duration = 300) {
    this.requestTransition(async () => {
      const { statusBox, statusLabel } = this.ui;
      statusBox.textString = err;
      statusLabel.value = ['Error ', null, ...Icon.textAttribute('exclamation-triangle', { paddingTop: '3px' })];
      await statusLabel.whenRendered();
      this.master = 'styleguide://$world/file status/error';
      this.master.applyAnimated({ duration });
      await this.withAnimationDo(() => {
        statusBox.opacity = 1;
        statusLabel.isLayoutable = true;
        statusLabel.opacity = 1;
        this.adjustHeight();
      }, { duration });
    });
  }

  async showFrozen (frozenMessage, duration = 300) {
    this.requestTransition(async () => {
      const { statusBox, statusLabel } = this.ui;
      statusBox.textString = frozenMessage;
      statusLabel.value = ['Frozen ', null, ...Icon.textAttribute('snowflake', { paddingTop: '3px' })];
      await statusLabel.whenRendered();
      this.master = 'styleguide://$world/file status/frozen';
      this.master.applyAnimated({ duration });
      await this.withAnimationDo(() => {
        statusBox.opacity = 1;
        statusLabel.isLayoutable = true;
        statusLabel.opacity = 1;
        this.adjustHeight();
      }, { duration });
    });
  }

  async showSaved (duration = 300, timeout = 5000) {
    if (this._animating) return;
    this._animating = true;

    this.requestTransition(async () => {
      const { statusBox, statusLabel } = this.ui;
      statusLabel.opacity = 0;
      statusLabel.value = ['Saved ', null, ...Icon.textAttribute('check', { paddingTop: '3px' })];
      await statusLabel.whenRendered();
      this.master = 'styleguide://$world/file status/saved';
      this.master.applyAnimated({ duration });
      await this.withAnimationDo(() => {
        statusBox.opacity = 0;
        statusLabel.isLayoutable = true;
        statusLabel.opacity = 1;
        this.adjustHeight();
      }, { duration });
    });

    await promise.delay(timeout);
    this._animating = false;
    // cancel if another saved was triggered in the meantime
    this.showDefault(duration);
  }

  async requestTransition (transition) {
    if (this._currentTransition) {
      this._nextTransition = transition;
      await this._currentTransition; // in the meantime multiple next transitions may pour in
      this.requestTransition(this._nextTransition); // just animate the last transition that poured in
      this._nextTransition = null;
    } else {
      this._currentTransition = transition();
      await this._currentTransition;
      this._currentTransition = null;
    }
  }
}
