StatsWidget.index.js:
/*global Stats*/
import { HTMLMorph } from "lively.morphic/html-morph.js";

export default class StatsWidget extends HTMLMorph {
  async onLoad () {
    await this.whenRendered();
    this.html = this.html; // force override to avoid issues with reset
    const script = document.createElement('script');
    script.onload = () => {
      const stats = this.stats = new Stats();
      this.domNode.appendChild(this.stats.dom);
      requestAnimationFrame(function loop () {
        stats.update();
        requestAnimationFrame(loop);
      });
    };
    script.src = '//mrdoob.github.io/stats.js/build/stats.min.js';
    this.domNode.appendChild(script);
  }

  onMouseUp (evt) {
    super.onMouseUp(evt);
  }
}

StylingSideBar.index.js:
import { Morph, touchInputDevice, morph, Icon, easings } from "lively.morphic";
import { pt, Color } from "lively.graphics/index.js";
import { fun } from "lively.lang/index.js";
import { connect } from "lively.bindings/index.js";
import { TreeData } from "lively.components";

export class SettingsTree extends TreeData {
  static default () {
    return new this({
      isCollapsed: false,
      children: [
        {
          isCollapsed: true,
          name: [' ', {}, ...Icon.textAttribute('font'), ' Rich Text', { fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' ', {}, ...Icon.textAttribute('image'), ' Shape', { fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' ', {}, ...Icon.textAttribute('border-style'), ' Border', { paddingLeft: '2px', fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' ', {}, ...Icon.textAttribute('grip-vertical'), ' Layout', { paddingLeft: '4px', fontWeight: 'bold' }],
          children: [{
            panel: morph({})
          }]
        }
      ]
    });
  }

  static border () {
    return new this({
      isCollapsed: false,
      children: [
        {
          isCollapsed: true,
          name: [' Type    ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Color   ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Width  ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        },
        {
          isCollapsed: false,
          name: [' Radius ', {}, morph({ height: 20 }), {}],
          children: [{
            panel: morph({})
          }]
        }
      ]
    });
  }

  display (node) {
    if (node.panel) return [node.panel, {}];
    else return node.name;
  }

  collapse (node, bool) {
    node.isCollapsed = bool;
  }

  isCollapsed (node) {
    return node.isCollapsed;
  }

  isLeaf (node) {
    return !node.children;
  }

  getChildren (node) {
    if (node === this.root) {
      return node.children.filter(n => {
        if (this.target && n.applicabilityCheck) return eval(n.applicabilityCheck)(this.target);
        return true;
      });
    }
    return node.children;
  }
}



export default class StylingSideBar extends Morph {
  static get properties () {
    return {
      controls: {},
      richTextControl: {},
      isHaloItem: {
        get () {
          return true;
        }
      }
    };
  }

  relayout () {
    this.onWorldResize();
  }

  collapseAll () {
    this.getSubmorphsByStyleClassName('Tree').forEach(t => {
      t.nodes.forEach(n => t.treeData.root == n ? t.uncollapse(n) : t.collapse(n));
    });
  }

  onWorldResize (align = true) {
    if (!this.respondsToVisibleWindow) return;
    const bounds = $world.visibleBounds();
    const offsetTop = navigator.standalone && touchInputDevice ? 25 : 0;
    const hr = this.getSubmorphNamed('horizontal resizer');

    this.height = bounds.height - offsetTop;
    this.top = offsetTop + bounds.top();
    hr.height = this.height;
    hr.left = 0;
    hr.top = 0;
    if (!align) return;
    if (this.visible) {
      this.topRight = bounds.topRight();
    } else this.topLeft = bounds.topRight();
  }

  async toggle (active) {
    const bounds = $world.visibleBounds().withTopLeft(pt(0, 0));
    const offsetTop = navigator.standalone && touchInputDevice ? 25 : 0;
    this.height = bounds.height - offsetTop;
    this.top = offsetTop + bounds.top();
    if (active) {
      $world.addMorph(this, $world.get('lively top bar'));
      this.topLeft = bounds.topRight();
      this.visible = true;
      await this.whenRendered();
      this.onWorldResize(false);
      await this.animate({
        opacity: 1,
        easing: easings.outCirc,
        topRight: bounds.topRight(),
        duration: 300
      });
      this.attachToWorld($world);
    } else {
      this.detachFromWorld($world);
      await this.animate({
        opacity: 0,
        topLeft: bounds.topRight(),
        duration: 300
      });
      this.visible = false;
      this.remove();
    }
  }

  cleanupAnchors () {
    const morphsWithEmptyAnchors = this.withAllSubmorphsSelect(m => m.anchors && m.anchors.filter(a => !a.id).length);
    for (const m of morphsWithEmptyAnchors) {
      m.anchors.forEach(a => !a.id && m.removeAnchor(a));
    }
    return morphsWithEmptyAnchors.length;
  }

  // fixme: connect

  attachToWorld (world) {
    connect(world, 'showHaloFor', this, 'focusMorph', {
      garbageCollect: true
    });
  }

  detachFromWorld (world) {
    world.attributeConnections.forEach(conn => {
      if (conn.targetObj === this) conn.disconnect();
    });
  }

  focusMorph (target) {
    if (target.isMorph && target.ownerChain().includes(this)) return;
    if (target.isLabel || target.isText || target.isButton) {
      this.richTextControl.focusOn(target, false);
    } else {
      // temporary deactivate the rich text interface not to confuse the user
    }
    const tree = this.getSubmorphNamed('style settings tree');
    tree.treeData.target = target;
    tree.update(true);
    Object.values(this.controls).forEach(control => control && control.focusOn(target));
  }

  onHierarchyChange () {
    Object.values(this.controls).forEach(control => control && control.update());
  }
}

ColorStop.index.js:
import { Polygon } from "lively.morphic";
import { signal } from "lively.bindings/index.js";
import { resource } from "lively.resources/index.js";

export default class ColorStop extends Polygon {
  static get properties () {
    return {
      isSelected: {
        derived: true,
        get () {
          return this.master.equals('styleguide://$world/gradient editor/color stop/selected');
        }
      }
    };
  }

  forStop (stop) {
    this.stop = stop;
    this.refresh();
    return this;
  }

  refresh () {
    this.getSubmorphNamed('transparent').fill = this.stop.color;
    this.getSubmorphNamed('opaque').fill = this.stop.color.withA(1);
  }

  positionIn (gradientEditor) {
    this.bottomCenter = gradientEditor.getPositionFor(this.stop);
  }

  onDrag (evt) {
    const delta = this.getGlobalTransform().inverse().transformDirection(evt.state.dragDelta);
    signal(this, 'moveColorStop', [this.stop, delta.x]);
  }

  onMouseDown (evt) {
    super.onMouseDown(evt);
    this.select(this);
  }

  select (self) {
    this.master = 'styleguide://$world/gradient editor/color stop/selected';
  }

  deselect () {
    this.master = 'styleguide://$world/gradient editor/color stop/default';
  }
}

