import { Morph } from 'lively.morphic';
import { arr } from 'lively.lang/index.js';
import { Color } from 'lively.graphics/index.js';
import { signal } from 'lively.bindings/index.js';

export default // 21-06-15 13:07
class ColorPicker extends Morph {
  static get properties () {
    return {
      colorMode: {},
      hsb: {
        initialize () {
          this.hsb = [...Color.red.toHSB(), 1];
        }
      },
      gradientValue: {
        derived: true,
        get () {
          return this.ui.gradientControl.gradientValue;
        },
        set (v) {
          this.ui.gradientControl.gradientValue = v;
        }
      },
      color: {
        derived: true,
        get () {
          return Color.hsb(...this.hsb.slice(0, 3)).withA(this.hsb[3]);
        },
        set (c) {
          this.hsb = [...c.toHSB(), c.a];
        }
      },
      solidOnly: {
        derived: true,
        set (active) {
          const { colorTypeSelector } = this.ui;
          if (active) colorTypeSelector.selection = 'Solid';
          colorTypeSelector.visible = !active;
        }
      },
      ui: {
        derived: true,
        get () {
          return {
            colorTypeSelector: this.getSubmorphNamed('color type selector'),
            colorEncoder: this.getSubmorphNamed('color encoder'),
            opacityPicker: this.getSubmorphNamed('opacity slider'),
            shadePicker: this.getSubmorphNamed('field picker'),
            huePicker: this.getSubmorphNamed('hue picker'),
            gradientControl: this.getSubmorphNamed('gradient control')
          };
        }
      }
    };
  }

  toggleHalos (active) {
    this.withAllSubmorphsDo(m => m.halosEnabled = active);
    this.ui.gradientControl.gradientHalo.withAllSubmorphsDo(m => m.halosEnabled = active);
  }

  focusOnMorph (aMorph, color) {
    const world = aMorph && aMorph.world();
    if (world) {
      world.halos().forEach(m => m.remove());
      world.withTopBarDo(topBar => topBar.setEditMode('Hand', true));
    }
    this._target = aMorph;
    if (color.isGradient) {
      this.ui.gradientControl.setGradient(color, this);
      this.ui.colorTypeSelector.selection = color.type;
    } else {
      this.color = color;
      this.ui.colorTypeSelector.selection = 'Solid'; // triggers a confirm...
    }
    this.update();
  }

  close () {
    this.remove();
    const world = this._target && this._target.world();
    if (world) world.withTopBarDo(topBar => topBar.setEditMode('Halo', true));
    this.ui.gradientControl.toggle(false, this);
  }

  confirm () {
    switch (this.colorMode) {
      case 'linearGradient':
      case 'radialGradient':
        signal(this, 'value', this.gradientValue);
        break;
      case 'Solid':
        signal(this, 'value', this.color);
    }
  }

  switchMode (newMode) {
    const isGradient = ['linearGradient', 'radialGradient'].includes(newMode);
    this.ui.gradientControl.toggle(isGradient, this);
    this.colorMode = newMode;
    this.update();
    this.confirm();
  }

  enterColor (c) {
    // store color as hue sat brt alpha
    this.color = c;
    this.update(this.ui.colorEncoder);
    this.confirm();
  }

  adjustColor (c) {
    this.color = c;
    this.update();
    this.confirm();
  }

  adjustOpacity (a) {
    this.hsb = [...this.hsb.slice(0, 3), a];
    // this.color = this.color.withA(a);
    this.update(this.ui.opacityPicker);
    this.confirm();
  }

  adjustHue (hue) {
    // const [_, sat, brt] = this.color.toHSB();
    // this.color = Color.hsb(hue, sat, brt).withA(this.color.a);
    this.hsb = [hue, ...this.hsb.slice(1)];
    this.update(this.ui.huePicker);
    this.confirm();
  }

  adjustShade ([brt, sat]) {
    // const [hue] = this.color.toHSB();
    // this.color = Color.hsb(hue, sat, brt).withA(this.color.a);
    this.hsb = [this.hsb[0], sat, brt, this.hsb[3]];
    this.update(this.ui.shadePicker);
    this.confirm();
  }

  adjustGradient (gradientValue) {
    this.confirm();
  }

  update (...toSkip) {
    arr.withoutAll(Object.values(this.ui), [...toSkip, this.ui.colorTypeSelector]).forEach(m => m.update(this));
  }
}

GradientControl.index.js:
import { Morph } from 'lively.morphic';
import { LinearGradient, RadialGradient, Color, rect } from 'lively.graphics/index.js';
import { resource } from 'lively.resources/index.js';
import { connect, noUpdate, signal } from 'lively.bindings/index.js';
import { num, arr } from 'lively.lang/index.js';

export default class GradientControl extends Morph {
  static get properties () {
    return {
      gradientValue: {},
      gradientHalo: {}, // if active, this is openend in the world
      isDisabled: {
        get () {
          return !this.visible;
        }
      },
      stopControls: {
        derived: true,
        get () {
          return this.getSubmorphsByStyleClassName('ColorStop');
        }
      },
      selectedStopControl: {
        derived: true,
        get () {
          return this.stopControls.find(each => each.isSelected);
        }
      }
    };
  }

  onKeyDown (evt) {
    super.onKeyDown(evt);
    if (evt.keyCombo == 'Delete') {
      this.removeStop(this.selectedStopControl);
    }
  }

  onMouseDown (evt) {
    super.onMouseDown(evt);
    if (evt.targetMorph.name == 'gradient preview') {
      const offset = evt.positionIn(evt.targetMorph).x / evt.targetMorph.width;
      this.addStopAt(offset);
    }
  }

  removeStop (aStopControl) {
    if (this.gradientValue.stops.length < 3) return;
    arr.remove(this.gradientValue.stops, aStopControl.stop);
    this.refresh();
    this.confirm();
  }

  addStopAt (offset) {
    const stops = [...this.gradientValue.stops];
    const [before, after] = arr.sortBy(arr.sortBy(stops, stop => Math.abs(stop.offset - offset)).slice(0, 2), stop => stop.offset);
    const p = (offset - before.offset) / (after.offset - before.offset);
    stops.push({
      color: before.color.interpolate(p, after.color),
      offset
    });
    this.gradientValue.stops = arr.sortBy(stops, stop => stop.offset);
    this.refresh();
    this.confirm();
  }

  toggle (active, picker) {
    this.isLayoutable = this.visible = active;
    if (active) {
      this.gradientHalo.openInWorld();
    } else this.gradientHalo.remove();
    if (!active) this.reset();
  }

  reset () {
    this.gradientValue = null;
    this.getSubmorphsByStyleClassName('ColorStop').forEach(m => m.remove());
    this.gradientHalo.reset();
  }

  initLinearGradient (color) {
    if (color.isGradient) this.gradientValue = new LinearGradient({ stops: color.stops });
    else {
      this.gradientValue = new LinearGradient({
        stops: [{ color, offset: 0 }, { color: Color.transparent, offset: 1 }]
      });
    }
  }

  initRadialGradient (color, target) {
    const bounds = target.innerBounds();
    if (color.isGradient) this.gradientValue = new RadialGradient({ bounds, stops: color.stops });
    else {
      this.gradientValue = new RadialGradient({
        bounds,
        stops: [{ color, offset: 0 }, { color: Color.transparent, offset: 1 }]
      });
    }
  }

  setGradient (color, colorPicker) {
    this.gradientValue = color;
    this.gradientHalo.initFromPicker(colorPicker);
  }

  update (colorPicker) {
    if (this.isDisabled) return;
    const { selectedStopControl } = this;
    if (!this.gradientValue || this.gradientValue.type != colorPicker.colorMode) {
      if (colorPicker.colorMode == 'linearGradient') this.initLinearGradient(this.gradientValue || colorPicker.color);
      if (colorPicker.colorMode == 'radialGradient') this.initRadialGradient(this.gradientValue || colorPicker.color, colorPicker._target);
      this.gradientHalo.initFromPicker(colorPicker);
      this.gradientHalo.confirm();
    }
    if (selectedStopControl) {
      const stopToChange = this.gradientValue.stops.find(aStop => selectedStopControl.stop == aStop);
      if (stopToChange) stopToChange.color = colorPicker.color;
      this.confirm();
    }
    this.refresh();
  }

  getPositionFor (aStop) {
    // returns the appropriate position for a stop control corresponding to aStop
    const { start, end } = this.getSubmorphNamed('gradient preview').bounds().topEdge();
    return start.interpolate(aStop.offset, end).subXY(0, 5);
  }

  confirm () {
    signal(this, 'gradientChanged', this.gradientValue);
  }

  refresh () {
    if (!this.gradientValue) return;
    // updates the UI to be in tune with the gradient value
    const gradient = this.getSubmorphNamed('gradient');
    const stops = this.gradientValue.stops.map(stop => {
      return { ...stop };
    });
    gradient.fill = new LinearGradient({
      vector: rect(0),
      stops // do not share stops!!
    });
    this.updateStopControls(this.gradientValue.stops);
    this.gradientHalo.refresh(this);
  }

  moveStop ([aStop, dx]) {
    const relativeDelta = dx / this.getSubmorphNamed('gradient preview').width;
    this.adjustStop(aStop, relativeDelta);
  }

  adjustStop (aStop, relativeDelta) {
    aStop.offset = num.clamp(aStop.offset + relativeDelta, 0, 1);
    this.refresh(); // update the stop position
    this.confirm();
  }

  placeStop (aStopControl) {
    this.addMorph(aStopControl);
  }

  async getControlFor (aStop, haloOrEditor = this) {
    const s = await resource('part://$world/gradient editor/color stop').read();
    s.withAllSubmorphsDo(m => m.halosEnabled = false);
    haloOrEditor.placeStop(s);
    connect(s, 'select', haloOrEditor, 'selectStop');
    connect(s, 'moveColorStop', haloOrEditor, 'moveStop');
    return s.forStop(aStop);
  }

  selectStop (aStopControl) {
    this.deselectAllStopsExcept(aStopControl, this);
    this.deselectAllStopsExcept(aStopControl, this.gradientHalo);
    // ask the color picker to manage the color of this stop
    signal(this, 'switchColor', aStopControl.stop.color);
  }

  deselectAllStopsExcept (aStopControl, haloOrEditor) {
    noUpdate(() => {
      haloOrEditor.getSubmorphsByStyleClassName('ColorStop')
        .forEach(each => {
          if (each.stop != aStopControl.stop) {
            each.deselect();
          } else {
            each.select();
          }
        });
    });
  }

  async updateStopControls (stops, haloOrEditor = this) {
    if (haloOrEditor._active) return;
    haloOrEditor._active = true;
    let stopControls = haloOrEditor.getSubmorphsByStyleClassName('ColorStop');
    for (const stop of stops) {
      let control = stopControls.shift();
      if (control) {
        // adjust the color of the stops if needed
        control.forStop(stop);
      } else { await this.getControlFor(stop, haloOrEditor); }
    }
    arr.invoke(stopControls, 'remove');
    // adjust the position of the stops if needed
    haloOrEditor.getSubmorphsByStyleClassName('ColorStop').forEach(stopControl => {
      stopControl.positionIn(haloOrEditor);
    });
    haloOrEditor._active = false;
  }
}

GradientHalo.index.js:
import { Morph } from "lively.morphic";
import { arr } from "lively.lang/index.js";
import { resource } from "lively.resources/index.js";
import { connect, signal } from "lively.bindings/index.js";
import { pt, RadialGradient, LinearGradient, Point } from "lively.graphics/index.js";

export default class GradientHalo extends Morph {
  static get properties () {
    return {
      picker: {}, // ref to picker
      targetMorph: {
        derived: true,
        get () {
          return this.picker._target;
        }
      },
      ui: {
        derived: true,
        get () {
          return {
            originHandle: this.getSubmorphNamed('origin handle'),
            directionHandle: this.getSubmorphNamed('direction handle'),
            orthogonalHandle: this.getSubmorphNamed('orthogonal handle'),
            colorStopWrapper: this.getSubmorphNamed('color stop wrapper')
          };
        }
      },
      angle: {
        derived: true,
        get () {
          return this.directionPoint.subPt(this.originPoint).theta();
        }
      }
    };
  }

  reset () {
    this.getSubmorphsByStyleClassName('ColorStop').forEach(m => m.remove());
  }

  initFromPicker (picker) {
    const gradient = picker.gradientValue;
    this.picker = picker;
    if (gradient.type == 'linearGradient') { this.initFromLinearGradient(this.targetMorph, gradient); }
    if (gradient.type == 'radialGradient') { this.initFromRadialGradient(this.targetMorph, gradient); }
    this.alignWithTarget();
  }

  initFromLinearGradient (target, linearGradient) {
    // based on the angle
    const targetBounds = target.innerBounds();
    const theta = linearGradient.vectorAsAngle();
    const c = targetBounds.center();
    const d = Math.max(targetBounds.height, targetBounds.width);
    // from the angle, compute the intersections
    const br = c.addPt(Point.polar(d, theta));
    const tl = c.subPt(Point.polar(d, theta));
    const [p1, p2] = targetBounds.lineIntersection(tl.lineTo(br));
    // the direction point the the one closes to br
    let directionPoint = arr.min([p1, p2], p => p.dist(br));
    this.originPoint = directionPoint == p1 ? p2 : p1;
    this.directionPoint = directionPoint;
    this.orthogonalDist = arr.min(targetBounds.edges(), edge => edge.distanceFromLine(p2)).length() / 2;

    this.alignWithTarget();
  }

  initFromRadialGradient (target, radialGradient) {
    const targetBounds = target.innerBounds();
    const originPoint = targetBounds.relativeToAbsPoint(radialGradient.focus);
    const { height: gradientHeight, width: gradientWidth } = radialGradient.bounds;
    this.originPoint = originPoint;
    this.directionPoint = originPoint.addXY(0, gradientHeight / 2);
    this.orthogonalDist = gradientWidth / 2;
    this.alignWithTarget();
  }

  alignWithTarget () {
    const {
      originHandle,
      directionHandle,
      orthogonalHandle,
      colorStopWrapper
    } = this.ui;
    originHandle.center = pt(0);
    directionHandle.center = pt(this.width, 0);
    this.width = this.originPoint.dist(this.directionPoint);
    colorStopWrapper.width = this.width;
    directionHandle.center = pt(this.width, 0);
    orthogonalHandle.center = pt(0, this.orthogonalDist);

    if (this.orthogonalDist > 0) {
      colorStopWrapper.tilted = 1;
      colorStopWrapper.position = pt(this.width / 2, this.height / 4 - 1);
    } else {
      colorStopWrapper.tilted = 0;
      colorStopWrapper.position = pt(this.width / 2, -this.height / 4 + 1);
    }

    this.position = this.targetMorph.worldPoint(this.originPoint);
    this.rotation = this.angle;
  }

  confirm () {
    const { gradientControl } = this.picker.ui;
    if (gradientControl.gradientValue.type == 'linearGradient') {
      gradientControl.gradientValue.vector = this.angle;
    }
    if (gradientControl.gradientValue.type == 'radialGradient') {
      const v = gradientControl.gradientValue;
      const width = Math.abs(this.orthogonalDist) * 2;
      const height = this.originPoint.dist(this.directionPoint) * 2;
      v.focus = this.originPoint.scaleByPt(this.targetMorph.extent.inverted());
      v.bounds = pt(0, 0).extent(pt(width, height));
      v.angle = this.angle;
    }
    gradientControl.confirm();
    this.refresh(gradientControl, this.targetMorph);
    this.alignWithTarget();
  }

  normalize (delta) {
    return this.getGlobalTransform().transformDirection(delta);
  }

  moveOriginHandle (evt) {
    const delta = this.normalize(evt.state.dragDelta);
    this.originPoint = this.originPoint.addPt(delta);
    this.confirm();
  }

  moveDirectionHandle (evt) {
    const delta = this.normalize(evt.state.dragDelta);
    this.directionPoint = this.directionPoint.addPt(delta);
    this.confirm();
  }

  moveOrthogonalHandle (evt) {
    const delta = this.normalize(evt.state.dragDelta);
    this.orthogonalDist -= delta.dotProduct(Point.polar(1, this.angle - Math.PI / 2));
    this.confirm();
  }

  moveStop ([aStop, dx]) {
    this.picker.ui.gradientControl.adjustStop(aStop, dx / this.getSubmorphNamed('color stop wrapper').width);
  }

  refresh (gradientControl, target) {
    const {
      originHandle,
      directionHandle,
      orthogonalHandle,
      colorStopWrapper
    } = this.ui;

    const gradientValue = gradientControl.gradientValue;
    this.alignWithTarget();
    gradientControl.updateStopControls(gradientValue.stops, this);
  }

  placeStop (aStopControl) {
    this.get('color stop wrapper').addMorph(aStopControl);
  }

  selectStop (aStopControl) {
    this.picker.ui.gradientControl.selectStop(aStopControl);
  }

  getPositionFor (aStop) {
    const wrapper = this.getSubmorphNamed('color stop wrapper');
    const { start, end } = wrapper.innerBounds().bottomEdge();
    return start.interpolate(aStop.offset, end).subXY(0, 5).subPt(wrapper.origin);
  }
}

OpacityPicker.index.js:
import { Morph } from "lively.morphic";
import { pt, Color, LinearGradient } from "lively.graphics/index.js";
import { signal } from "lively.bindings/index.js";
import { num } from "lively.lang/index.js";

export default class OpacityPicker extends Morph {
  static get properties () {
    return {
      sliderPosition: {
        derived: true,
        after: ['submorphs'],
        get () {
          const h = this.height - 10;
          return pt(this.width / 2, 5 + h * this.alpha);
        },
        set (pos) {
          const h = this.height - 10;
          this.alpha = num.clamp(pos.y / h, 0, 1);
          this.refresh();
        }
      }
    };
  }

  confirm () {
    signal(this, 'opacityChanged', this.alpha);
  }

  onMouseDown (evt) {
    const sliderWidth = this.get('slider').width;
    this.sliderPosition = pt(0, evt.positionIn(this).y - sliderWidth / 2);
    this.confirm();
  }

  onDrag (evt) {
    const sliderWidth = this.get('slider').width;
    this.sliderPosition = pt(0, evt.positionIn(this).y - sliderWidth / 2);
    this.confirm();
  }

  update (colorPicker) {
    this.alpha = colorPicker.color.a;
    // properly set the gradient...
    this.getSubmorphNamed('opacity gradient').fill = new LinearGradient({
      stops: [{ offset: 0, color: colorPicker.color.withA(0) }, { offset: 1, color: colorPicker.color.withA(1) }]
    });
    this.refresh();
  }

  refresh () {
    this.get('slider').center = this.sliderPosition;
  }
}

ColorEncoder.index.js:
import { Morph } from "lively.morphic";
import { Color } from "lively.graphics/index.js";
import { signal, noUpdate } from "lively.bindings/index.js";
import { num } from "lively.lang/index.js";

export default class ColorEncoder extends Morph {
  static get properties () {
    return {
      encodingMode: {
        type: 'Enum',
        values: ['Hex', 'RGB', 'HSL', 'HSB', 'CSS']
      },
      currentColor: {
        defaultValue: Color.white
      }
    };
  }

  update (colorPicker) {
    this.currentColor = colorPicker.hsb;
    this.refresh();
  }

  refresh () {
    const color = this.currentColor;
    noUpdate(() => {
      ({
        Hex: () => this.updateHex(color),
        RGB: () => this.updateRGB(color),
        HSL: () => this.updateHSL(color),
        HSB: () => this.updateHSB(color),
        CSS: () => this.updateCSS(color)
      })[this.encodingMode]();
    });
  }

  updateHex (color) {
    color = Color.hsb(...color).withA(num.roundTo(color[3], 0.01));
    const hexInput = this.getSubmorphNamed('hex input');
    const hexOpacity = this.getSubmorphNamed('hex opacity control');
    hexInput.input = color.toHexString().toUpperCase();
    hexOpacity.number = color.a;
  }

  updateRGB (color) {
    const rInput = this.getSubmorphNamed('first value');
    const gInput = this.getSubmorphNamed('second value');
    const bInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const [r, g, b] = Color.hsb(...color).toTuple();
    const a = color[3];
    rInput.number = r * 255;
    gInput.number = g * 255;
    bInput.number = b * 255;
    aInput.number = a;
  }

  updateHSL (color, hsl = true) {
    const hInput = this.getSubmorphNamed('first value');
    const sInput = this.getSubmorphNamed('second value');
    const lInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const [h, s, b] = color;
    if (hsl) lInput.number = 1 - b;
    else lInput.number = b;
    hInput.number = h;
    sInput.number = s;
    aInput.number = color[3];
  }

  updateHSB (color) {
    this.updateHSL(color, false);
  }

  updateCSS (color) {
    const cssInput = this.getSubmorphNamed('css input');
    color = Color.hsb(...color).withA(num.roundTo(color[3], 0.01));
    cssInput.input = color.toCSSString();
  }

  confirm () {
    ({
      Hex: () => this.confirmHex(),
      RGB: () => this.confirmRGB(),
      HSL: () => this.confirmHSL(),
      HSB: () => this.confirmHSB(),
      CSS: () => this.confirmCSS()
    })[this.encodingMode]();
  }

  confirmHex () {
    const hexInput = this.getSubmorphNamed('hex input');
    const hexOpacity = this.getSubmorphNamed('hex opacity control');
    const c = Color.rgbHex(hexInput.input).withA(hexOpacity.number);
    signal(this, 'colorEntered', c);
  }

  confirmRGB () {
    const rInput = this.getSubmorphNamed('first value');
    const gInput = this.getSubmorphNamed('second value');
    const bInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const c = Color.rgba(rInput.number, gInput.number, bInput.number, aInput.number);
    signal(this, 'colorEntered', c);
  }

  confirmHSL () {
    const hInput = this.getSubmorphNamed('first value');
    const sInput = this.getSubmorphNamed('second value');
    const lInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const c = Color.hsb(hInput.number, sInput.number, 1 - lInput.number).withA(aInput.number);
    signal(this, 'colorEntered', c);
  }

  confirmHSB () {
    const hInput = this.getSubmorphNamed('first value');
    const sInput = this.getSubmorphNamed('second value');
    const bInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const c = Color.hsb(hInput.number, sInput.number, bInput.number).withA(aInput.number);
    signal(this, 'colorEntered', c);
  }

  confirmCSS () {
    const cssInput = this.getSubmorphNamed('css input');
    const c = Color.fromString(cssInput.input);
    signal(this, 'colorEntered', c);
  }

  selectEncoding (encodingName) {
    const nameToUI = {
      Hex: 'hex encoding',
      RGB: '3 val encoding',
      HSL: '3 val encoding',
      HSB: '3 val encoding',
      CSS: 'css encoding'
    };
    this.encodingMode = encodingName;
    this.submorphs.map(m => m.visible = m.isLayoutable = false);
    const control = this.getSubmorphNamed(nameToUI[encodingName]);
    control.visible = control.isLayoutable = true;
    if (encodingName == 'RGB') {
      [
        control.getSubmorphNamed('first value'),
        control.getSubmorphNamed('second value'),
        control.getSubmorphNamed('third value')
      ].forEach(aNumberInput => {
        Object.assign(aNumberInput, {
          min: 0, max: 255, scaleFactor: 1
        });
      });
    }
    if (['HSL', 'HSB'].includes(encodingName)) {
      Object.assign(control.getSubmorphNamed('first value'), {
        min: 0, max: 360, scaleFactor: 1
      });
      [
        control.getSubmorphNamed('second value'),
        control.getSubmorphNamed('third value')
      ].forEach(aNumberInput => {
        Object.assign(aNumberInput, {
          min: 0, max: 1, scaleFactor: 100
        });
      });
    }
    this.refresh();
  }
}

