StatsWidget.index.js:
/*global Stats*/
import { HTMLMorph } from "lively.morphic/html-morph.js";

export default class StatsWidget extends HTMLMorph {
  async onLoad () {
    await this.whenRendered();
    this.html = this.html; // force override to avoid issues with reset
    const script = document.createElement('script');
    script.onload = () => {
      const stats = this.stats = new Stats();
      this.domNode.appendChild(this.stats.dom);
      requestAnimationFrame(function loop () {
        stats.update();
        requestAnimationFrame(loop);
      });
    };
    script.src = '//mrdoob.github.io/stats.js/build/stats.min.js';
    this.domNode.appendChild(script);
  }

  onMouseUp (evt) {
    super.onMouseUp(evt);
  }
}

