import { Morph } from 'lively.morphic';
import { arr } from 'lively.lang/index.js';
import { Color } from 'lively.graphics/index.js';
import { signal } from 'lively.bindings/index.js';

export default // 21-06-15 13:07
class ColorPicker extends Morph {
  static get properties () {
    return {
      colorMode: {},
      hsb: {
        initialize () {
          this.hsb = [...Color.red.toHSB(), 1];
        }
      },
      gradientValue: {
        derived: true,
        get () {
          return this.ui.gradientControl.gradientValue;
        },
        set (v) {
          this.ui.gradientControl.gradientValue = v;
        }
      },
      color: {
        derived: true,
        get () {
          return Color.hsb(...this.hsb.slice(0, 3)).withA(this.hsb[3]);
        },
        set (c) {
          this.hsb = [...c.toHSB(), c.a];
        }
      },
      solidOnly: {
        derived: true,
        set (active) {
          const { colorTypeSelector } = this.ui;
          if (active) colorTypeSelector.selection = 'Solid';
          colorTypeSelector.visible = !active;
        }
      },
      ui: {
        derived: true,
        get () {
          return {
            colorTypeSelector: this.getSubmorphNamed('color type selector'),
            colorEncoder: this.getSubmorphNamed('color encoder'),
            opacityPicker: this.getSubmorphNamed('opacity slider'),
            shadePicker: this.getSubmorphNamed('field picker'),
            huePicker: this.getSubmorphNamed('hue picker'),
            gradientControl: this.getSubmorphNamed('gradient control')
          };
        }
      }
    };
  }

  toggleHalos (active) {
    this.withAllSubmorphsDo(m => m.halosEnabled = active);
    this.ui.gradientControl.gradientHalo.withAllSubmorphsDo(m => m.halosEnabled = active);
  }

  focusOnMorph (aMorph, color) {
    const world = aMorph && aMorph.world();
    if (world) {
      world.halos().forEach(m => m.remove());
      world.withTopBarDo(topBar => topBar.setEditMode('Hand', true));
    }
    this._target = aMorph;
    if (color.isGradient) {
      this.ui.gradientControl.setGradient(color, this);
      this.ui.colorTypeSelector.selection = color.type;
    } else {
      this.color = color;
      this.ui.colorTypeSelector.selection = 'Solid'; // triggers a confirm...
    }
    this.update();
  }

  close () {
    this.remove();
    const world = this._target && this._target.world();
    if (world) world.withTopBarDo(topBar => topBar.setEditMode('Halo', true));
    this.ui.gradientControl.toggle(false, this);
  }

  confirm () {
    switch (this.colorMode) {
      case 'linearGradient':
      case 'radialGradient':
        signal(this, 'value', this.gradientValue);
        break;
      case 'Solid':
        signal(this, 'value', this.color);
    }
  }

  switchMode (newMode) {
    const isGradient = ['linearGradient', 'radialGradient'].includes(newMode);
    this.ui.gradientControl.toggle(isGradient, this);
    this.colorMode = newMode;
    this.update();
    this.confirm();
  }

  enterColor (c) {
    // store color as hue sat brt alpha
    this.color = c;
    this.update(this.ui.colorEncoder);
    this.confirm();
  }

  adjustColor (c) {
    this.color = c;
    this.update();
    this.confirm();
  }

  adjustOpacity (a) {
    this.hsb = [...this.hsb.slice(0, 3), a];
    // this.color = this.color.withA(a);
    this.update(this.ui.opacityPicker);
    this.confirm();
  }

  adjustHue (hue) {
    // const [_, sat, brt] = this.color.toHSB();
    // this.color = Color.hsb(hue, sat, brt).withA(this.color.a);
    this.hsb = [hue, ...this.hsb.slice(1)];
    this.update(this.ui.huePicker);
    this.confirm();
  }

  adjustShade ([brt, sat]) {
    // const [hue] = this.color.toHSB();
    // this.color = Color.hsb(hue, sat, brt).withA(this.color.a);
    this.hsb = [this.hsb[0], sat, brt, this.hsb[3]];
    this.update(this.ui.shadePicker);
    this.confirm();
  }

  adjustGradient (gradientValue) {
    this.confirm();
  }

  update (...toSkip) {
    arr.withoutAll(Object.values(this.ui), [...toSkip, this.ui.colorTypeSelector]).forEach(m => m.update(this));
  }
}

GradientControl.index.js:
import { Morph } from 'lively.morphic';
import { LinearGradient, RadialGradient, Color, rect } from 'lively.graphics/index.js';
import { resource } from 'lively.resources/index.js';
import { connect, noUpdate, signal } from 'lively.bindings/index.js';
import { num, arr } from 'lively.lang/index.js';

export default class GradientControl extends Morph {
  static get properties () {
    return {
      gradientValue: {},
      gradientHalo: {}, // if active, this is openend in the world
      isDisabled: {
        get () {
          return !this.visible;
        }
      },
      stopControls: {
        derived: true,
        get () {
          return this.getSubmorphsByStyleClassName('ColorStop');
        }
      },
      selectedStopControl: {
        derived: true,
        get () {
          return this.stopControls.find(each => each.isSelected);
        }
      }
    };
  }

  onKeyDown (evt) {
    super.onKeyDown(evt);
    if (evt.keyCombo == 'Delete') {
      this.removeStop(this.selectedStopControl);
    }
  }

  onMouseDown (evt) {
    super.onMouseDown(evt);
    if (evt.targetMorph.name == 'gradient preview') {
      const offset = evt.positionIn(evt.targetMorph).x / evt.targetMorph.width;
      this.addStopAt(offset);
    }
  }

  removeStop (aStopControl) {
    if (this.gradientValue.stops.length < 3) return;
    arr.remove(this.gradientValue.stops, aStopControl.stop);
    this.refresh();
    this.confirm();
  }

  addStopAt (offset) {
    const stops = [...this.gradientValue.stops];
    const [before, after] = arr.sortBy(arr.sortBy(stops, stop => Math.abs(stop.offset - offset)).slice(0, 2), stop => stop.offset);
    const p = (offset - before.offset) / (after.offset - before.offset);
    stops.push({
      color: before.color.interpolate(p, after.color),
      offset
    });
    this.gradientValue.stops = arr.sortBy(stops, stop => stop.offset);
    this.refresh();
    this.confirm();
  }

  toggle (active, picker) {
    this.isLayoutable = this.visible = active;
    if (active) {
      this.gradientHalo.openInWorld();
    } else this.gradientHalo.remove();
    if (!active) this.reset();
  }

  reset () {
    this.gradientValue = null;
    this.getSubmorphsByStyleClassName('ColorStop').forEach(m => m.remove());
    this.gradientHalo.reset();
  }

  initLinearGradient (color) {
    if (color.isGradient) this.gradientValue = new LinearGradient({ stops: color.stops });
    else {
      this.gradientValue = new LinearGradient({
        stops: [{ color, offset: 0 }, { color: Color.transparent, offset: 1 }]
      });
    }
  }

  initRadialGradient (color, target) {
    const bounds = target.innerBounds();
    if (color.isGradient) this.gradientValue = new RadialGradient({ bounds, stops: color.stops });
    else {
      this.gradientValue = new RadialGradient({
        bounds,
        stops: [{ color, offset: 0 }, { color: Color.transparent, offset: 1 }]
      });
    }
  }

  setGradient (color, colorPicker) {
    this.gradientValue = color;
    this.gradientHalo.initFromPicker(colorPicker);
  }

  update (colorPicker) {
    if (this.isDisabled) return;
    const { selectedStopControl } = this;
    if (!this.gradientValue || this.gradientValue.type != colorPicker.colorMode) {
      if (colorPicker.colorMode == 'linearGradient') this.initLinearGradient(this.gradientValue || colorPicker.color);
      if (colorPicker.colorMode == 'radialGradient') this.initRadialGradient(this.gradientValue || colorPicker.color, colorPicker._target);
      this.gradientHalo.initFromPicker(colorPicker);
      this.gradientHalo.confirm();
    }
    if (selectedStopControl) {
      const stopToChange = this.gradientValue.stops.find(aStop => selectedStopControl.stop == aStop);
      if (stopToChange) stopToChange.color = colorPicker.color;
      this.confirm();
    }
    this.refresh();
  }

  getPositionFor (aStop) {
    // returns the appropriate position for a stop control corresponding to aStop
    const { start, end } = this.getSubmorphNamed('gradient preview').bounds().topEdge();
    return start.interpolate(aStop.offset, end).subXY(0, 5);
  }

  confirm () {
    signal(this, 'gradientChanged', this.gradientValue);
  }

  refresh () {
    if (!this.gradientValue) return;
    // updates the UI to be in tune with the gradient value
    const gradient = this.getSubmorphNamed('gradient');
    const stops = this.gradientValue.stops.map(stop => {
      return { ...stop };
    });
    gradient.fill = new LinearGradient({
      vector: rect(0),
      stops // do not share stops!!
    });
    this.updateStopControls(this.gradientValue.stops);
    this.gradientHalo.refresh(this);
  }

  moveStop ([aStop, dx]) {
    const relativeDelta = dx / this.getSubmorphNamed('gradient preview').width;
    this.adjustStop(aStop, relativeDelta);
  }

  adjustStop (aStop, relativeDelta) {
    aStop.offset = num.clamp(aStop.offset + relativeDelta, 0, 1);
    this.refresh(); // update the stop position
    this.confirm();
  }

  placeStop (aStopControl) {
    this.addMorph(aStopControl);
  }

  async getControlFor (aStop, haloOrEditor = this) {
    const s = await resource('part://$world/gradient editor/color stop').read();
    s.withAllSubmorphsDo(m => m.halosEnabled = false);
    haloOrEditor.placeStop(s);
    connect(s, 'select', haloOrEditor, 'selectStop');
    connect(s, 'moveColorStop', haloOrEditor, 'moveStop');
    return s.forStop(aStop);
  }

  selectStop (aStopControl) {
    this.deselectAllStopsExcept(aStopControl, this);
    this.deselectAllStopsExcept(aStopControl, this.gradientHalo);
    // ask the color picker to manage the color of this stop
    signal(this, 'switchColor', aStopControl.stop.color);
  }

  deselectAllStopsExcept (aStopControl, haloOrEditor) {
    noUpdate(() => {
      haloOrEditor.getSubmorphsByStyleClassName('ColorStop')
        .forEach(each => {
          if (each.stop != aStopControl.stop) {
            each.deselect();
          } else {
            each.select();
          }
        });
    });
  }

  async updateStopControls (stops, haloOrEditor = this) {
    if (haloOrEditor._active) return;
    haloOrEditor._active = true;
    let stopControls = haloOrEditor.getSubmorphsByStyleClassName('ColorStop');
    for (const stop of stops) {
      let control = stopControls.shift();
      if (control) {
        // adjust the color of the stops if needed
        control.forStop(stop);
      } else { await this.getControlFor(stop, haloOrEditor); }
    }
    arr.invoke(stopControls, 'remove');
    // adjust the position of the stops if needed
    haloOrEditor.getSubmorphsByStyleClassName('ColorStop').forEach(stopControl => {
      stopControl.positionIn(haloOrEditor);
    });
    haloOrEditor._active = false;
  }
}

GradientHalo.index.js:
import { Morph } from 'lively.morphic';
import { arr } from 'lively.lang/index.js';
import { resource } from 'lively.resources/index.js';
import { connect, signal } from 'lively.bindings/index.js';
import { pt, RadialGradient, LinearGradient, Point } from 'lively.graphics/index.js';

export default class GradientHalo extends Morph {
  static get properties () {
    return {
      picker: {}, // ref to picker
      targetMorph: {
        derived: true,
        get () {
          return this.picker._target;
        }
      },
      ui: {
        derived: true,
        get () {
          return {
            originHandle: this.getSubmorphNamed('origin handle'),
            directionHandle: this.getSubmorphNamed('direction handle'),
            orthogonalHandle: this.getSubmorphNamed('orthogonal handle'),
            colorStopWrapper: this.getSubmorphNamed('color stop wrapper')
          };
        }
      },
      angle: {
        derived: true,
        get () {
          return this.directionPoint.subPt(this.originPoint).theta();
        }
      }
    };
  }

  reset () {
    this.getSubmorphsByStyleClassName('ColorStop').forEach(m => m.remove());
  }

  initFromPicker (picker) {
    const gradient = picker.gradientValue;
    this.picker = picker;
    if (gradient.type == 'linearGradient') { this.initFromLinearGradient(this.targetMorph, gradient); }
    if (gradient.type == 'radialGradient') { this.initFromRadialGradient(this.targetMorph, gradient); }
    this.alignWithTarget();
  }

  initFromLinearGradient (target, linearGradient) {
    // based on the angle
    const targetBounds = target.innerBounds();
    const theta = linearGradient.vectorAsAngle();
    const c = targetBounds.center();
    const d = Math.max(targetBounds.height, targetBounds.width);
    // from the angle, compute the intersections
    const br = c.addPt(Point.polar(d, theta));
    const tl = c.subPt(Point.polar(d, theta));
    const [p1, p2] = targetBounds.lineIntersection(tl.lineTo(br));
    // the direction point the the one closes to br
    let directionPoint = arr.min([p1, p2], p => p.dist(br));
    this.originPoint = directionPoint == p1 ? p2 : p1;
    this.directionPoint = directionPoint;
    this.orthogonalDist = arr.min(targetBounds.edges(), edge => edge.distanceFromLine(p2)).length() / 2;

    this.alignWithTarget();
  }

  initFromRadialGradient (target, radialGradient) {
    const targetBounds = target.innerBounds();
    const originPoint = targetBounds.relativeToAbsPoint(radialGradient.focus);
    const { height: gradientHeight, width: gradientWidth } = radialGradient.bounds;
    this.originPoint = originPoint;
    this.directionPoint = originPoint.addXY(0, gradientHeight / 2);
    this.orthogonalDist = gradientWidth / 2;
    this.alignWithTarget();
  }

  alignWithTarget () {
    const {
      originHandle,
      directionHandle,
      orthogonalHandle,
      colorStopWrapper
    } = this.ui;
    originHandle.center = pt(0);
    directionHandle.center = pt(this.width, 0);
    this.width = this.originPoint.dist(this.directionPoint);
    colorStopWrapper.width = this.width;
    directionHandle.center = pt(this.width, 0);
    orthogonalHandle.center = pt(0, this.orthogonalDist);

    if (this.orthogonalDist > 0) {
      colorStopWrapper.tilted = 1;
      colorStopWrapper.position = pt(this.width / 2, this.height / 4 - 1);
    } else {
      colorStopWrapper.tilted = 0;
      colorStopWrapper.position = pt(this.width / 2, -this.height / 4 + 1);
    }

    this.globalPosition = this.targetMorph.worldPoint(this.originPoint).subPt($world.scroll); // ignores world scroll
    this.rotation = this.angle;
  }

  relayout () {
    this.alignWithTarget();
  }

  confirm () {
    const { gradientControl } = this.picker.ui;
    if (gradientControl.gradientValue.type == 'linearGradient') {
      gradientControl.gradientValue.vector = this.angle;
    }
    if (gradientControl.gradientValue.type == 'radialGradient') {
      const v = gradientControl.gradientValue;
      const width = Math.abs(this.orthogonalDist) * 2;
      const height = this.originPoint.dist(this.directionPoint) * 2;
      v.focus = this.originPoint.scaleByPt(this.targetMorph.extent.inverted());
      v.bounds = pt(0, 0).extent(pt(width, height));
      v.angle = this.angle;
    }
    gradientControl.confirm();
    this.refresh(gradientControl, this.targetMorph);
    this.alignWithTarget();
  }

  normalize (delta) {
    return this.getGlobalTransform().transformDirection(delta);
  }

  moveOriginHandle (evt) {
    const delta = this.normalize(evt.state.dragDelta);
    this.originPoint = this.originPoint.addPt(delta);
    this.confirm();
  }

  moveDirectionHandle (evt) {
    const delta = this.normalize(evt.state.dragDelta);
    this.directionPoint = this.directionPoint.addPt(delta);
    this.confirm();
  }

  moveOrthogonalHandle (evt) {
    const delta = this.normalize(evt.state.dragDelta);
    this.orthogonalDist -= delta.dotProduct(Point.polar(1, this.angle - Math.PI / 2));
    this.confirm();
  }

  moveStop ([aStop, dx]) {
    this.picker.ui.gradientControl.adjustStop(aStop, dx / this.getSubmorphNamed('color stop wrapper').width);
  }

  refresh (gradientControl, target) {
    const {
      originHandle,
      directionHandle,
      orthogonalHandle,
      colorStopWrapper
    } = this.ui;

    const gradientValue = gradientControl.gradientValue;
    this.alignWithTarget();
    gradientControl.updateStopControls(gradientValue.stops, this);
  }

  placeStop (aStopControl) {
    this.get('color stop wrapper').addMorph(aStopControl);
  }

  selectStop (aStopControl) {
    this.picker.ui.gradientControl.selectStop(aStopControl);
  }

  getPositionFor (aStop) {
    const wrapper = this.getSubmorphNamed('color stop wrapper');
    const { start, end } = wrapper.innerBounds().bottomEdge();
    return start.interpolate(aStop.offset, end).subXY(0, 5).subPt(wrapper.origin);
  }
}

OpacityPicker.index.js:
import { Morph } from "lively.morphic";
import { pt, Color, LinearGradient } from "lively.graphics/index.js";
import { signal } from "lively.bindings/index.js";
import { num } from "lively.lang/index.js";

export default class OpacityPicker extends Morph {
  static get properties () {
    return {
      sliderPosition: {
        derived: true,
        after: ['submorphs'],
        get () {
          const h = this.height - 10;
          return pt(this.width / 2, 5 + h * this.alpha);
        },
        set (pos) {
          const h = this.height - 10;
          this.alpha = num.clamp(pos.y / h, 0, 1);
          this.refresh();
        }
      }
    };
  }

  confirm () {
    signal(this, 'opacityChanged', this.alpha);
  }

  onMouseDown (evt) {
    const sliderWidth = this.get('slider').width;
    this.sliderPosition = pt(0, evt.positionIn(this).y - sliderWidth / 2);
    this.confirm();
  }

  onDrag (evt) {
    const sliderWidth = this.get('slider').width;
    this.sliderPosition = pt(0, evt.positionIn(this).y - sliderWidth / 2);
    this.confirm();
  }

  update (colorPicker) {
    this.alpha = colorPicker.color.a;
    // properly set the gradient...
    this.getSubmorphNamed('opacity gradient').fill = new LinearGradient({
      stops: [{ offset: 0, color: colorPicker.color.withA(0) }, { offset: 1, color: colorPicker.color.withA(1) }]
    });
    this.refresh();
  }

  refresh () {
    this.get('slider').center = this.sliderPosition;
  }
}

ColorEncoder.index.js:
import { Morph } from "lively.morphic";
import { Color } from "lively.graphics/index.js";
import { signal, noUpdate } from "lively.bindings/index.js";
import { num } from "lively.lang/index.js";

export default class ColorEncoder extends Morph {
  static get properties () {
    return {
      encodingMode: {
        type: 'Enum',
        values: ['Hex', 'RGB', 'HSL', 'HSB', 'CSS']
      },
      currentColor: {
        defaultValue: Color.white
      }
    };
  }

  update (colorPicker) {
    this.currentColor = colorPicker.hsb;
    this.refresh();
  }

  refresh () {
    const color = this.currentColor;
    noUpdate(() => {
      ({
        Hex: () => this.updateHex(color),
        RGB: () => this.updateRGB(color),
        HSL: () => this.updateHSL(color),
        HSB: () => this.updateHSB(color),
        CSS: () => this.updateCSS(color)
      })[this.encodingMode]();
    });
  }

  updateHex (color) {
    color = Color.hsb(...color).withA(num.roundTo(color[3], 0.01));
    const hexInput = this.getSubmorphNamed('hex input');
    const hexOpacity = this.getSubmorphNamed('hex opacity control');
    hexInput.input = color.toHexString().toUpperCase();
    hexOpacity.number = color.a;
  }

  updateRGB (color) {
    const rInput = this.getSubmorphNamed('first value');
    const gInput = this.getSubmorphNamed('second value');
    const bInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const [r, g, b] = Color.hsb(...color).toTuple();
    const a = color[3];
    rInput.number = r * 255;
    gInput.number = g * 255;
    bInput.number = b * 255;
    aInput.number = a;
  }

  updateHSL (color, hsl = true) {
    const hInput = this.getSubmorphNamed('first value');
    const sInput = this.getSubmorphNamed('second value');
    const lInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const [h, s, b] = color;
    if (hsl) lInput.number = 1 - b;
    else lInput.number = b;
    hInput.number = h;
    sInput.number = s;
    aInput.number = color[3];
  }

  updateHSB (color) {
    this.updateHSL(color, false);
  }

  updateCSS (color) {
    const cssInput = this.getSubmorphNamed('css input');
    color = Color.hsb(...color).withA(num.roundTo(color[3], 0.01));
    cssInput.input = color.toCSSString();
  }

  confirm () {
    ({
      Hex: () => this.confirmHex(),
      RGB: () => this.confirmRGB(),
      HSL: () => this.confirmHSL(),
      HSB: () => this.confirmHSB(),
      CSS: () => this.confirmCSS()
    })[this.encodingMode]();
  }

  confirmHex () {
    const hexInput = this.getSubmorphNamed('hex input');
    const hexOpacity = this.getSubmorphNamed('hex opacity control');
    const c = Color.rgbHex(hexInput.input).withA(hexOpacity.number);
    this.currentColor = [...c.toHSB(), c.a];
    signal(this, 'colorEntered', c);
  }

  confirmRGB () {
    const rInput = this.getSubmorphNamed('first value');
    const gInput = this.getSubmorphNamed('second value');
    const bInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const c = Color.rgba(rInput.number, gInput.number, bInput.number, aInput.number);
    this.currentColor = [...c.toHSB(), c.a];
    signal(this, 'colorEntered', c);
  }

  confirmHSL () {
    const hInput = this.getSubmorphNamed('first value');
    const sInput = this.getSubmorphNamed('second value');
    const lInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const c = Color.hsb(hInput.number, sInput.number, 1 - lInput.number).withA(aInput.number);
    this.currentColor = [...c.toHSB(), c.a];
    signal(this, 'colorEntered', c);
  }

  confirmHSB () {
    const hInput = this.getSubmorphNamed('first value');
    const sInput = this.getSubmorphNamed('second value');
    const bInput = this.getSubmorphNamed('third value');
    const aInput = this.getSubmorphNamed('opacity control');
    const c = Color.hsb(hInput.number, sInput.number, bInput.number).withA(aInput.number);
    this.currentColor = [...c.toHSB(), c.a];
    signal(this, 'colorEntered', c);
  }

  confirmCSS () {
    const cssInput = this.getSubmorphNamed('css input');
    const c = Color.fromString(cssInput.input);
    this.currentColor = [...c.toHSB(), c.a];
    signal(this, 'colorEntered', c);
  }

  selectEncoding (encodingName) {
    const nameToUI = {
      Hex: 'hex encoding',
      RGB: '3 val encoding',
      HSL: '3 val encoding',
      HSB: '3 val encoding',
      CSS: 'css encoding'
    };
    this.encodingMode = encodingName;
    this.submorphs.map(m => m.visible = m.isLayoutable = false);
    const control = this.getSubmorphNamed(nameToUI[encodingName]);
    control.visible = control.isLayoutable = true;
    if (encodingName == 'RGB') {
      [
        control.getSubmorphNamed('first value'),
        control.getSubmorphNamed('second value'),
        control.getSubmorphNamed('third value')
      ].forEach(aNumberInput => {
        Object.assign(aNumberInput, {
          min: 0, max: 255, scaleFactor: 1
        });
      });
    }
    if (['HSL', 'HSB'].includes(encodingName)) {
      Object.assign(control.getSubmorphNamed('first value'), {
        min: 0, max: 360, scaleFactor: 1
      });
      [
        control.getSubmorphNamed('second value'),
        control.getSubmorphNamed('third value')
      ].forEach(aNumberInput => {
        Object.assign(aNumberInput, {
          min: 0, max: 1, scaleFactor: 100
        });
      });
    }
    this.refresh();
  }
}

ColorPaletteView.index.js:
import { HTMLMorph } from "lively.morphic";
import { arr } from "lively.lang/index.js";
import { materialDesignColors, webSafeColors, flatDesignColors, Color } from "lively.graphics/index.js";
import { signal } from "lively.bindings/index.js";

export default class ColorPaletteView extends HTMLMorph {
  test () {
    this.showPalette(materialDesignColors.map(c => {
      return Color.rgbHex(c);
    }));
  }

  selectPalette (paletteName) {
    const nameToPalette = {
      'Flat Design': flatDesignColors,
      Material: materialDesignColors,
      'Web Safe': webSafeColors
    };
    nameToPalette[paletteName] && this.showPalette(nameToPalette[paletteName].map(c => {
      return Color.rgbHex(c);
    }));
  }

  onMouseDown (evt) {
    super.onMouseDown(evt);
    const { color } = evt.domEvt.target.dataset;
    if (!color) return;
    signal(this, 'colorSelected', Color.fromString(color));
  }

  showPalette (colors) {
    this.html = `<div class="palette-container">
      ${
        colors.map(c => {
            return `<div class="color-cell" data-color="${c}" style="background: ${c}"></div>`;
          }).join('\n')
      }
    </div>`;
  }
}

MullerColumnView.index.js:
import { Morph } from 'lively.morphic/morph.js';
import { TreeData, List } from 'lively.components/index.js';
import { HTTPFileBrowserNode } from 'lively.ide/http-file-browser.js';
import { resource } from 'lively.resources/index.js';
import { arr, promise } from 'lively.lang/index.js';
import { connect, signal, noUpdate } from 'lively.bindings/index.js';
import { pt } from 'lively.graphics/index.js';
import { PackageTreeData } from 'lively.ide/js/browser/index.js';

export default class MullerColumnView extends Morph {
  static get properties () {
    return {
      treeData: {
        // this.treeData
      },
      listMaster: {
        defaultValue: 'styleguide://SystemWidgets/list/light',
        set (url) {
          this.setProperty('listMaster', url);
          this.refresh(false);
        }
      }
      // muller columns are essentially a different style of tree view
      // the main different is that only one chain of collapsed nodes
      // is displayed at once. This in turn requires that of all the nodes
      // children, at most one can be collapsed at a time.
    };
  }

  truncateNameIfNeeded (displayedName) {
    // based on the muller column it is going to be rendered in, adjust the truncation
    return displayedName;
  }

  isSelected (node) {
    return !node.isCollapsed;
  }

  async example () {
    this.setTreeData(new PackageTreeData({
      browser: this.owner
    }));
  }

  async setTreeData (treeData) {
    this.treeData = treeData;
    await treeData.collapse(treeData.root, false);
    await this.refresh(false);
  }

  reset () {
    this.submorphs = [];
  }

  async selectNode (aNode, animated = true) {
    this._selectedNode = aNode;
    const parent = this.treeData.parentNode(aNode);
    await Promise.all(this.treeData.getChildren(parent).map(child => {
      if (child !== aNode && !this.treeData.isCollapsed(child)) {
        return this.treeData.collapse(child, true);
      }
      if (child === aNode) return this.treeData.collapse(child, false);
    }));
    await this.refresh(animated);
    signal(this, 'selectionChange', this.getExpandedPath());
  }

  getExpandedPath () {
    const td = this.treeData;
    if (!td) return [];
    let currentChildren = td.getChildren(td.root);
    let nextNode;
    const expandedPath = [td.root];
    while (nextNode = currentChildren && currentChildren.find(aChild => !td.isCollapsed(aChild))) {
      expandedPath.push(nextNode);
      currentChildren = td.getChildren(nextNode);
    }
    return expandedPath;
  }

  /*
    Often we want to expand a path that has never been expanded before.
    Depending on the implementation of the treeData the nodes that need to
    be visited may not even be present yet (lazy initialization).
    This is why we expand each node with a custom iterator.
  */
  async setExpandedPath (
    matchingNodes = (n) => n == this.treeData.root,
    until = this.treeData.root,
    animated = true
  ) {
    const td = this.treeData;
    const p = this.getExpandedPath();
    const currentPath = p.slice(p.indexOf(until));
    await Promise.all(currentPath.map(node => td.collapse(node, true))); // dont collapse root
    await td.uncollapseAll(matchingNodes, 0, until);
    await this.refresh(animated);
    this.submorphs.forEach(list => list.scrollSelectionIntoView());
  }

  newList () {
    const list = new List({
      master: this.listMaster,
      width: 200
    });
    connect(list, 'selection', this, 'selectNode');
    return list;
  }

  async ensureLists (numberOfLists, animated = true) {
    let newSubmorphs = this.submorphs; let scroll;
    const lenDiff = numberOfLists - this.submorphs.length;
    if (lenDiff > 0) {
      newSubmorphs = [...newSubmorphs, ...arr.genN(lenDiff, () => this.newList())];
      this.submorphs = newSubmorphs;
      await this.whenRendered(); // ensure layout has placed all morphs accordingly
      if (this.layout.renderViaCSS) // thank you virtual-dom....
      { newSubmorphs.map(m => this.layout.expressMeasure(m)); }
      scroll = pt(this.scrollExtent.x - this.width);
      if (animated) {
        this.animate({
          scroll,
          duration: 200
        });
      } else this.scroll = scroll;
    } else if (lenDiff < 0) {
      newSubmorphs = newSubmorphs.slice(0, lenDiff);
      scroll = pt(arr.last(newSubmorphs).right - this.width);
      if (animated) {
        await this.animate({
          scroll,
          duration: 200
        });
      } else this.scroll = scroll;
      this.submorphs = newSubmorphs;
    }
    return newSubmorphs;
  }

  relayout () {
    let scrollBarHeight = 0;
    if (this.scrollExtent.x > this.width + this.scrollbarOffset.x) {
      scrollBarHeight = 5;
    }
    this.submorphs.forEach(list => {
      list.height = this.height - scrollBarHeight;
      const control = list._managedNode && list._managedNode.listControl;
      if (control) {
        control.bottomRight = list.innerBounds().bottomRight().subXY(5, 0);
      }
    });
  }

  onHoverOut (evt) {
    super.onHoverOut(evt);
    this.submorphs.forEach(list => {
      const control = list._managedNode && list._managedNode.listControl;
      if (control) control.visible = false;
    });
  }

  onMouseMove (evt) {
    super.onMouseMove(evt);
    const hoveredList = this.submorphs.find(list => list.fullContainsWorldPoint(evt.position));
    if (hoveredList) {
      this.submorphs.forEach(list => {
        const control = list._managedNode && list._managedNode.listControl;
        if (control) {
          control.visible = list == hoveredList;
        }
      });
    }
  }

  get keybindings () {
    return [
      { keys: 'Left', command: 'select previous entry of last list' },
      { keys: 'Right', command: 'select first entry of next list' }
    ].concat(super.keybindings);
  }

  get commands () {
    return [
      {
        name: 'select previous entry of last list',
        exec: () => {
          const nextNode = this.treeData.parentNode(this._selectedNode);
          this.selectNode(nextNode);
        }
      },
      {
        name: 'select first entry of next list',
        exec: () => {
          const current = this._selectedNode;
          const nextNode = current.subNodes && current.subNodes[0];
          if (nextNode) {
            this.selectNode(nextNode);
          }
        }
      }
    ];
  }

  async refresh (animated = true) {
    const td = this.treeData;
    if (!td) return;
    let expandedPath = this.getExpandedPath(); let selectedFile;
    const last = arr.last(expandedPath);
    if (td.isLeaf(last) && last != td.root) {
      selectedFile = arr.last(expandedPath);
      expandedPath = expandedPath.slice(0, -1);
    }
    const lists = await this.ensureLists(expandedPath.length, animated); // always ensure the root list exists
    let currentList;
    expandedPath.forEach(node => {
      if ((!td.isCollapsed(node)) && currentList) {
        noUpdate(() => {
          currentList.selection = node;
          currentList.focus();
        });
      }
      currentList = lists.shift();
      noUpdate(() => currentList.selection = false);
      const originalScroll = currentList.itemScroll;
      if (currentList._managedNode == node) {
        currentList.itemScroll = originalScroll;
        const currentItems = currentList.items;
        const newItems = td.getChildren(node);
        if (newItems.length == currentItems.length) {
          arr.zip(currentItems, newItems).forEach(([item, value]) => {
            if (!value) return;
            item.value = value;
            if (item.value.isDirty) {
              item.value.isDirty = false;
              item.label = td.display(item.value);
            }
          });
        } else currentList._managedNode = null;
      }
      if (currentList._managedNode != node) {
        currentList.items = td.getChildren(node).map(each => {
          return { isListItem: true, label: td.display(each), value: each, tooltip: each.tooltip || false };
        });
        currentList._managedNode = node;
      }
      if (node.listControl) { currentList.submorphs = currentList.submorphs.slice(0, 2).concat([node.listControl]); }
    });
    if (selectedFile) {
      noUpdate(() => {
        currentList.selection = selectedFile;
        currentList.focus();
      });
    }
    this.relayout();
  }
}

ResponsiveLayoutMorph.index.js:
import { Morph, Icon } from "lively.morphic";
import { obj, arr } from "lively.lang/index.js";

export default class ResponsiveLayoutMorph extends Morph {

  static get properties() {
    return {
      verticalBreakPoints: {
        defaultValue: []
      },
      horizontalBreakPoints: {
        defaultValue: []
      },
      watchableProperties: {
        get() {
          return ['master'];
        }
      },
      watchedProperties: {
        after: ['breakPointPropertyMapping'],
        set(propertyNames) {
          this.setProperty('watchedProperties', propertyNames);
          this.clearUnwatchedProperties();
          this.capturePropertiesForBreakPoint(this.getCurrentBreakPoints());
        }
      },
      configurationHalo: {},
      breakPointPropertyMapping: {
        after: ['submorphs'],
        set(mappingSpec) {
          let map = new WeakMap();
          map.__serialize__ = (pool, serializedObjMap, path) => 
            this.breakPointPropertyMappingAsSpec(pool, serializedObjMap, path);
          this.setProperty('breakPointPropertyMapping', map);
          // convert the spec to a weak map based mapping
          for (let [m, spec] of mappingSpec) {
            if (typeof m === 'string') m = this.get(m);
            map.set(m, spec);
          }
        }
      },
      debug: {
        // toggle the halo that allows to modify breakpoints and the set of watched properties
        after: ['configurationHalo'],
        type: 'Boolean',
        set(active) {
           this.setProperty('debug', active);
           this.toggleHalo(active);
        }
      }
    }
  }

  onOwnerChanged(newOwner) {
    if (newOwner && !newOwner.isHand) this.toggleHalo(this.debug)
  }

  remove() {
    super.remove();
    this.configurationHalo.remove();
  }

  menuItems() {
    let checked = Icon.textAttribute('check-square'),
        unchecked = Icon.textAttribute('square');
    Object.assign(unchecked[1], {paddingRight: "5px", float: 'none', display: 'inline'});
    Object.assign(checked[1], {paddingRight: "5px", float: 'none', display: 'inline'});

    return [
      [
        [...this.debug ? checked : unchecked, 'Responsive Controls ', {float: 'none'}], 
        () => {
          this.debug = !this.debug;
        },
      ],
      ['Watched Properties', 
        this.watchableProperties.map(prop => {
          let active = this.watchedProperties.includes(prop);
          return [[
            ...active ? checked : unchecked,
            prop, {float: 'none'}
          ], () => {
            if (active)
              this.watchedProperties = arr.without(this.watchedProperties, prop);
            else
              this.watchedProperties = [...this.watchedProperties, prop];
          }]
        })
      ],
      { isDivider: true },
      ...super.menuItems()
    ]
  }

  // this.copy()

  clearUnwatchedProperties() {
    this.withAllSubmorphsDo(m => {
      let breakPointsToProps = this.breakPointPropertyMapping.get(m) || {};
      for (let bp in breakPointsToProps) {
        // do not assign undefined props
        breakPointsToProps[bp] = obj.select({...m._morphicState, ...breakPointsToProps[bp]}, this.watchedProperties); 
      }
    });
  }

  breakPointPropertyMappingAsSpec(pool, serializedObjMap, path) {
    let spec = [];
    this.withAllSubmorphsDo(m => { 
      spec.push(pool ? m : m.name);
      spec.push(this.breakPointPropertyMapping.get(m) || [[{}, {}], [{}, {}]]);
    });
    spec = arr.toTuples(spec, 2);
    // this does not yet play nicely with masters...
    return pool ? pool.ref(this).snapshotProperty(this.id, spec, path, serializedObjMap, pool) : spec;
  }

  onChange(change) {
    super.onChange(change);
    if (change.prop === 'extent') {
       this.update()
    }
    if (change.prop === 'position') {
      this.configurationHalo.relayout();
    }
  }

  update() {
    let [h, v] = this.getCurrentBreakPoints();
    if (!this.lastBreakPoints) this.lastBreakPoints = [h, v];
    let [oldH, oldV] = this.lastBreakPoints;
    if (oldH != h || oldV != v) {
      this.capturePropertiesForBreakPoint(this.lastBreakPoints);
      this.updateSubmorphHierarchy([h,v]);
      this.lastBreakPoints = [h,v];   
    }
    this.configurationHalo.relayout();
  }

  updateSubmorphHierarchy(breakPoint) {
    this.withAllSubmorphsDo(m => {
       let breakPointsToProps = this.breakPointPropertyMapping.get(m);
       Object.assign(m, breakPointsToProps[breakPoint]);
    })
  }

  getCurrentBreakPoints() {
    let h = arr.findIndex([...this.horizontalBreakPoints, this.width], (b) => this.width <= b);
    let v = arr.findIndex([...this.verticalBreakPoints, this.height], (b) => this.height <= b);
    return [h, v];
  }

  capturePropertiesForBreakPoint(breakPoint) {
    this.withAllSubmorphsDo(m => {
      let breakPointsToProps = this.breakPointPropertyMapping.get(m) || {};
      // fixme: maybe we need to be more careful when capturing properties? what about layouts?
      breakPointsToProps[breakPoint] = obj.select(m, this.watchedProperties);
      this.breakPointPropertyMapping.set(m, breakPointsToProps);
    })
  }

  toggleHalo(active) {
    let halo = this.configurationHalo;
    if (active) {
      halo.openInWorld();
      halo.relayout();
    } else {
      halo.remove();
    }
  }
}

ResponsiveLayoutMorphHalo.index.js:
import { Morph, HorizontalLayout, morph, Icon } from "lively.morphic";
import { pt, Color, materialDesignColors } from "lively.graphics/index.js";
import { num, arr } from "lively.lang/index.js";


export default class ResponsiveLayoutMorphHalo extends Morph {
  static get properties() {
    return {
       target: {},
       sliderProto: {},
       removeBreakpointButton: {
         readOnly: true,
         get() {
           return Icon.makeLabel('times', {
             fontSize: 17, name: 'remove breakpoint', tooltip: 'remove breakpoint'
           });
         }
       },
       ui: {
         derived: true,
         get() {
           return {
             sliders: this.getSubmorphsByStyleClassName('BreakpointSlider'),
             addVerticalBreakPointButton: this.getSubmorphNamed('add vertical breakpoint'),
             addHorizontalBreakpointButton: this.getSubmorphNamed('add horizontal breakpoint'),
             addVerticalBreakpointButton: this.getSubmorphNamed('add vertical breakpoint'),
             verticalBreakpointControl: this.getSubmorphNamed('vertical breakpoint control'),
             horizontalBreakpointControl: this.getSubmorphNamed('horizontal breakpoint control'),
           }
         }
       }
    }
  }

  getColorForBreakpoint(idx, align) {
    // generate from different color cycles
    let colorCycles = {
      horizontal: [0, 14, 12, 13].map(i => Color.rgbHex(materialDesignColors[i * 15])),
      vertical: [9, 2, 5, 2].map(i => Color.rgbHex(materialDesignColors[i * 15]))
    };
    return colorCycles[align][idx % 4];
  }

  relayout() {
    if (!this.owner) return;
    let {
      sliders,
      horizontalBreakpointControl,
      verticalBreakpointControl,
      addHorizontalBreakpointButton,
      addVerticalBreakpointButton
    } = this.ui;
    let {
      verticalBreakPoints,
      horizontalBreakPoints
    } = this.target;

    this.position = this.target.globalPosition.subPt(pt(35,35));

    for (let slider of sliders) {
      slider.layout.forceLayout();
      slider.alignInHalo(this);
    }

    // adjust the last breakpoint marker
    let lastV = verticalBreakpointControl.submorphs[verticalBreakPoints.length];
    lastV.width = Math.max(200, this.target.height - (arr.last(verticalBreakPoints) || 0) + 10);
    let lastH = horizontalBreakpointControl.submorphs[horizontalBreakPoints.length];
    lastH.width = Math.max(200, this.target.width - (arr.last(horizontalBreakPoints) || 0) + 10);

    // avoid sluggish relayouting
    verticalBreakpointControl.layout.forceLayout();
    horizontalBreakpointControl.layout.forceLayout();

    verticalBreakpointControl.submorphs.forEach((v, i) => {
      if (num.between(this.target.height, verticalBreakPoints[i-1] || 0, verticalBreakPoints[i] || this.target.height + 10))
        v.opacity = 1;
      else v.opacity = .5;
    });
    horizontalBreakpointControl.submorphs.forEach((v, i) => {
      if (num.between(this.target.width, horizontalBreakPoints[i-1] || 0, horizontalBreakPoints[i] || this.target.width + 10))
        v.opacity = 1;
      else v.opacity = .5;
    });

    addVerticalBreakpointButton.top = verticalBreakpointControl.bottom + 10;
    addHorizontalBreakpointButton.left = horizontalBreakpointControl.right + 10;
  }

  // this.update()
  // this.relayout()

  update() {
    let { sliders, verticalBreakpointControl, horizontalBreakpointControl } = this.ui;
    let { verticalBreakPoints, horizontalBreakPoints } = this.target;
    let updateBreakPoints = (control, breakpoints, align) => {
      control.submorphs = arr.range(0, breakpoints.length).map(i => {
        if (i > 0) {
          let slider = this.sliderProto.copy();
          slider.breakPointIndex = i - 1;
          slider.orientation = align;
          this.addMorph(slider);
        }
        return morph({
          left: i * 100,
          name: align[0] + i, fill: this.getColorForBreakpoint(i, align), height: 20,
          submorphs: i > 0 ? [this.removeBreakpointButton] : [],  clipMode: "hidden",
          layout: i > 0 && new HorizontalLayout({
            spacing: 2, direction: 'rightToLeft', autoResize: false, align: 'center'
          })
        })
      });
    }

    sliders.forEach(slider => slider.remove());

    updateBreakPoints(verticalBreakpointControl, verticalBreakPoints, 'vertical');
    updateBreakPoints(horizontalBreakpointControl, horizontalBreakPoints, 'horizontal');

    this.relayout();
  }

  // this.target.horizontalBreakPoints

  onMouseDown(evt) {
    let target = evt.targetMorph;
    let { verticalBreakpointControl, horizontalBreakpointControl } = this.ui;
    switch(target.name) {
        case "add horizontal breakpoint":
          this.target.horizontalBreakPoints = [
            ...this.target.horizontalBreakPoints,
            horizontalBreakpointControl.width
          ]; 
          this.update();
          break;
        case "add vertical breakpoint":
          this.target.verticalBreakPoints = [
            ...this.target.verticalBreakPoints,
            verticalBreakpointControl.width
          ];
          this.update();
          break;
        case "remove breakpoint":
          let control = target.owner.owner;
          let idx = control.submorphs.indexOf(target.owner) - 1;
          if (control === verticalBreakpointControl)
            arr.removeAt(this.target.verticalBreakPoints, idx);
          if (control === horizontalBreakpointControl)
            arr.removeAt(this.target.horizontalBreakPoints, idx);
          this.update();
          break;
    }
  }

  onSliderDrag(slider, dragDelta) {
    let { orientation } = slider;
    let breakPointAccessor = orientation + 'BreakPoints';
    let delta = dragDelta[orientation == 'horizontal' ? 'x' : 'y'];
    let idx = slider.breakPointIndex;
    this.target[breakPointAccessor][idx] += delta; // in place to prevent update
    // shift all proceeding breakpoints by the same delta
    this.relayout()
  }

}

BreakpointSlider.index.js:
import { Morph, Icon } from "lively.morphic";
import { rect, Color, pt } from "lively.graphics/index.js";
import { arr } from "lively.lang/index.js";

export default class BreakpointSlider extends Morph {

  static get properties() {
    return {
      orientation: {
        type: 'Enum',
        values: ['vertical', 'horizontal'],
        after: ['submorphs', 'layout'],
        set(direction) {
          this.setProperty('orientation', direction);
          this.relayout();
        }
      },
      breakPointIndex: {}
    }
  }

  alignInHalo(halo) {
      let {
        horizontalBreakpointControl,
        verticalBreakpointControl
      } = halo.ui;
      let {
        verticalBreakPoints,
        horizontalBreakPoints
      } = halo.target; 
      let idx = this.breakPointIndex, offset;
      if (this.orientation === 'vertical') {
        let { top } = verticalBreakpointControl;
        offset = verticalBreakPoints[idx];
        this.rightCenter = pt(halo.width - 13, offset + top);
        verticalBreakpointControl.submorphs[idx].width = offset - (verticalBreakPoints[idx - 1] || 0);
      }
      if (this.orientation === 'horizontal') {
        let { left } = horizontalBreakpointControl;
        offset = horizontalBreakPoints[idx];
        this.bottomCenter = pt(offset + left, halo.height - 13);
        horizontalBreakpointControl.submorphs[idx].width = offset - (horizontalBreakPoints[idx - 1] || 0);
      }
      this.getSubmorphNamed('user agent icon').value = this.getIconForOffset(offset);
      this.getSubmorphNamed('pixel view').value = `${offset.toFixed()} px`
  }

  getIconForOffset(offset) {
    let sections = {
      horizontal: [
        0, 'mobile',
        768, 'tablet',
        1024, 'desktop'
      ],
      vertical: [
        0, 'mobile',
        1024, 'tablet',
        1440, 'desktop'
      ]
    }
    let ranges = arr.toTuples(sections[this.orientation], 2).reverse();
    return Icon.textAttribute(ranges.find(t => offset > t[0])[1]);
  }

  relayout() {
    let [pixelView, userAgent] = this.submorphs;
    switch (this.orientation) {
      case 'vertical':
         this.rotation = -Math.PI / 2
         pixelView.padding = rect(10,0,3,0);
         pixelView.fit();
         pixelView.rotation = Math.PI / 2;
         userAgent.rotation = Math.PI / 2;
         userAgent.top = -100;
         this.height = 80;
         break;
      case 'horizontal':
         this.rotation = 0;
         pixelView.padding = rect(0,0,0,9);
         pixelView.fit();
         pixelView.rotation = 0;
         userAgent.rotation = 0;
         userAgent.top = -100;
         this.height = 60;
         break;
    }
    this.layout.forceLayout();
  }

  onDrag(evt) {
     this.owner.onSliderDrag(this, evt.state.dragDelta)
  }

}

