import { Morph } from 'lively.morphic';
import { rect } from 'lively.graphics';

export default class Tab extends Morph {
  static get properties () {
    return {
      caption: {
        defaultValue: 'Unnamed Tab',
        set (caption) {
          if (!this.renamable) return;
          this.setProperty('caption', caption);
          const captionLabel = this.getSubmorphNamed('tab caption');
          if (captionLabel) captionLabel.textString = caption;
          this.name = caption + ' tab';
        }
      },
      selected: {
        defaultValue: false,
        set (selected) {
          const owner = this.getOwnerNamed('tabs');
          if (selected && owner) {
            owner.deselectAllTabs();
            owner.showContent(this.content);
          }
          this.setProperty('selected', selected);
          this.setAppearance(selected);
        }
      },
      closeable: {
        defaultValue: true,
        after: ['submorphs'],
        set (closeable) {
          this.setProperty('closeable', closeable);
          this.getSubmorphNamed('tab close').visible = closeable;
          this.getSubmorphNamed('tab close').isLayoutable = closeable;
          this.getSubmorphNamed('tab caption').padding = closeable ? rect(6, 3, -6, -2) : rect(6, 3, 0, -2);
        }
      },
      renamable: {
        defaultValue: true,
        set (renamable) {
          this.setProperty('renamable', renamable);
          if (this._enableRenaming && !renamable) this._enableRenaming = false;
        }
      },
      _enableRenaming: {
        defaultValue: false,
        set (enableRenaming) {
          this.setProperty('_enableRenaming', enableRenaming);
          const textbox = this.getSubmorphNamed('tab caption');
          if (!textbox) return;
          textbox.reactsToPointer = enableRenaming;
          textbox.readOnly = !enableRenaming;
          textbox.selection.range = enableRenaming ? { start: 0, end: textbox.textString.length } : { start: 0, end: 0 };
          textbox.focus();
        }
      }
    };
  }

  setAppearance (isSelected) {
    if (isSelected) {
      this.master = $world.getSubmorphNamed('tab/selected');
    } else {
      this.master = {
        auto: $world.getSubmorphNamed('tab/default'),
        click: $world.getSubmorphNamed('tab/active'),
        hover: $world.getSubmorphNamed('tab/hover')
      };
    }
  }

  isTab () {
    return true;
  }

  close () {
    this.getOwnerNamed('tabs').selectNearestTab(this);
    this.remove();
  }

  onMouseUp () {
    if (!this.selected) this.selected = true;
  }

  onDoubleMouseDown () {
    this._enableRenaming = this.renamable;
  }
}

