import { Morph } from "lively.morphic/morph.js";
import { pt, rect } from "lively.graphics/index.js";
import { resource } from "lively.resources/src/helpers.js";
import { arr } from "lively.lang/index.js";

export default class Tabs extends Morph {
  static get properties () {
    return {
      scrollStep: {
        defaultValue: 100
      },
      showNewTabButton: {
        defaultValue: true,
        after: ['submorphs'],
        set (showNewTabButton) {
          this.setProperty('showNewTabButton', showNewTabButton);
          this.getSubmorphNamed('button new tab').visible = showNewTabButton;
          this.setBounds(this.bounds());
        }
      }
    };
  }

  setBounds (bounds) {
    super.setBounds(bounds);
    const contentContainer = this.getSubmorphNamed('tab content container');
    const tabContainer = this.getSubmorphNamed('tab container');

    contentContainer.extent = pt(bounds.width, bounds.height - tabContainer.height);
    contentContainer.position = pt(0, tabContainer.top + tabContainer.height);
    tabContainer.extent = pt(bounds.width, tabContainer.height);
    tabContainer.resize(pt(bounds.width, this.height));
  }

  async addTab (caption, content = null) {
    const newTab = await resource('part://$world/tab').read();
    newTab.caption = caption;
    newTab.content = content;

    const tabFlapContainer = this.getSubmorphNamed('tab flap container');
    tabFlapContainer.addMorph(newTab);

    newTab.selected = true;
    return newTab;
  }

  get tabs () {
    return this.getSubmorphNamed('tab container').tabs;
  }

  deselectAllTabs () {
    this.tabs.forEach(tab => {
      tab.selected = false;
    });
  }

  selectNearestTab (otherTab) {
    if (this.tabs.length === 0) return;
    if (this.tabs.length === 1) {
      this.tabs[0].selected = true;
      return;
    }
    let tabIndex = this.tabs.indexOf(otherTab);
    const tab = tabIndex < this.tabs.length - 1 ? this.tabs[++tabIndex] : this.tabs[--tabIndex];
    tab.selected = true;
    return tab;
  }

  get selectedTab () {
    return this.tabs.find(tab => tab.selected);
  }

  addContentToActiveTab (content) {
    const tab = this.selectedTab;
    if (tab) {
      tab.content = content;
      this.showContent(content);
    }
  }

  showContent (content) {
    const container = this.getSubmorphNamed('tab content container');
    container.withAllSubmorphsDo(submorph => {
      if (submorph !== container) submorph.remove();
    });

    if (content) {
      container.addMorph(content);
      content.position = pt(0, 0);
      content.extent = container.extent;
    }
  }
}

