import { Morph } from "lively.morphic/morph.js";
import { arr } from "lively.lang/index.js";
import { pt } from "lively.graphics/index.js";

export default class TabContentContainer extends Morph {
  onDrop (evt) {
    const content = arr.first(evt.hand.grabbedMorphs);
    super.onDrop(evt);
    if (content) { this.owner.addContentToSelectedTab(content); }
  }

  resize () {
    const content = this.submorphs[0];
    if (!content) return;
    content.position = pt(0, 0);
    content.extent = this.extent;
  }
}

