import { Morph } from "lively.morphic";
import { pt } from "lively.graphics/index.js";

export default class TabContainer extends Morph {
  get tabs () {
    return this.getSubmorphNamed('tab flap container').submorphs.filter(submorph => submorph.isTab);
  }

  resize (size) {
    const buttonScrollRight = this.getSubmorphNamed('button scroll right');
    const buttonScrollLeft = this.getSubmorphNamed('button scroll left');
    const buttonNewTab = this.getSubmorphNamed('button new tab');
    const tabScrollContainer = this.getSubmorphNamed('tab flap scroll container');

    this.height = this.owner.tabHeight;

    buttonScrollRight.height = this.height;
    buttonScrollLeft.height = this.height;
    buttonNewTab.height = this.height;
    tabScrollContainer.height = this.height;
    this.tabs.forEach(tab => tab.extent = pt(tab.width, this.height));

    const positionButtonScrollRight = pt(size.x - buttonScrollRight.width, buttonScrollRight.top);
    const positionButtonScrollLeft = pt(positionButtonScrollRight.x - buttonScrollLeft.width, buttonScrollRight.top);
    const positionButtonMostLeft = pt(positionButtonScrollLeft.x - (buttonNewTab.visible ? buttonNewTab.width : 0), buttonScrollRight.top);

    buttonScrollRight.position = positionButtonScrollRight;
    buttonScrollLeft.position = positionButtonScrollLeft;
    if (buttonNewTab.visible) buttonNewTab.position = positionButtonMostLeft;

    tabScrollContainer.extent = pt(positionButtonMostLeft.x, tabScrollContainer.height);
  }
}

