import { Morph } from 'lively.morphic';
import { rect, pt } from 'lively.graphics';
import { signal, noUpdate, connect } from "lively.bindings/index.js";
import { arr } from "lively.lang/index.js";

export default class Tab extends Morph {
  static get properties () {
    return {
      caption: {
        defaultValue: 'Unnamed Tab',
        set (caption) {
          if (!this.renamable) return;
          this.setProperty('caption', caption);
          const captionLabel = this.getSubmorphNamed('tab caption');
          if (captionLabel) captionLabel.textString = caption;
          this.name = caption + ' tab';
          if (this.owner && this.owner.layout && this.owner.name == 'tab flap container') this.owner.layout.apply();
        }
      },
      content: {
        intialize () {
          this.content = {};
        }
      },
      selected: {
        defaultValue: false,
        set (selected) {
          this.setProperty('selected', selected);
          signal(this, 'onSelectionChange', selected);
          this.setAppearance(selected);
        }
      },
      closeable: {
        defaultValue: true,
        after: ['submorphs'],
        set (closeable) {
          this.setProperty('closeable', closeable);
          this.getSubmorphNamed('tab close').visible = closeable;
          this.getSubmorphNamed('tab close').isLayoutable = closeable;
          this.getSubmorphNamed('tab caption').padding = closeable ? rect(6, 3, -6, -2) : rect(6, 3, 0, -2);
        }
      },
      renamable: {
        defaultValue: true,
        set (renamable) {
          this.setProperty('renamable', renamable);
          if (this._enableRenaming && !renamable) this._enableRenaming = false;
        }
      },
      _enableRenaming: {
        defaultValue: false,
        set (enableRenaming) {
          this.setProperty('_enableRenaming', enableRenaming);
          const textbox = this.getSubmorphNamed('tab caption');
          if (!textbox) return;
          textbox.reactsToPointer = enableRenaming;
          textbox.readOnly = !enableRenaming;
          textbox.selection.range = enableRenaming ? { start: 0, end: textbox.textString.length } : { start: 0, end: 0 };
          textbox.focus();
        }
      }
    };
  }

  initialize () {
    connect(this, 'extent', this, 'relayout');
  }

  async relayout () {
    const content = this.getSubmorphNamed('horizontal container');
    const caption = this.getSubmorphNamed('tab caption');

    const space = (this.height - content.height) / 2;
    content.position = pt(space, space - 1);

    const padding = rect(5, 3, space - 5, -2);
    caption.padding = padding;
    await this.whenRendered();

    noUpdate(() => {
      this.width = content.width + 2 * space;
    });
  }

  setAppearance (isSelected) {
    if (this.isComponent) return;
    if (isSelected) {
      this.master = 'styleguide://tabs/tab/selected';
    } else {
      this.master = {
        auto: 'styleguide://tabs/tab/default',
        click: 'styleguide://tabs/tab/active',
        hover: 'styleguide://tabs/tab/hover'
      };
    }
  }

  isTab () {
    return true;
  }

  close () {
    // hook for connection to do cleanup
    signal(this, 'onClose');
    if (this.content) { this.content.remove(); }
    this.remove();
  }

  onMouseUp (evt) {
    if (arr.first(evt.targetMorphs) == this.getSubmorphNamed('tab close')) return;
    if (!this.selected) this.selected = true;
  }

  onDoubleMouseDown () {
    this._enableRenaming = this.renamable;
  }
}

TabCaption.index.js:
import { Text } from "lively.morphic";

export default class TabCaption extends Text {
  get tab () {
    return this.owner.owner;
  }

  onKeyDown (event) {
    if (event.key == 'Enter' && this.textString != '') {
      this.tab._enableRenaming = false;
      this.tab.caption = this.textString;
      $world.setStatusMessage('Tab renamed');
    } else if (event.key == 'Escape' || (event.key == 'Enter' && this.textString == '')) {
      this.tab._enableRenaming = false;
      this.textString = this.owner.owner.caption;
      $world.setStatusMessage('Renaming canceled');
    } else {
      super.onKeyDown(event);
    }
  }
}

