import { Morph, show, TilingLayout } from "lively.morphic";
import { arr, fun } from "lively.lang";
import { getAllPartResources, defaultPartsbinFolder, loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { resource } from "lively.resources/index.js";
import { connect } from "lively.bindings/index.js";
import { Rectangle, pt } from "lively.graphics/index.js";


export class Partsbin extends Morph {

  static get properties() {
    return {
      partsbinFolder: {
        get() {
          return this.getProperty("partsbinFolder") || defaultPartsbinFolder;
        },
        set(val) {
          if (this.partsbinFolder === val) return;
          this.setProperty("partsbinFolder", val);
        }
      },

      buttonBar: {
        readOnly: true, derived: true, after: ["submorphs"],
        get() { return this.getSubmorphNamed("button-bar"); }
      },

      partItemList: {
        readOnly: true, derived: true, after: ["submorphs"],
        get() { return this.getSubmorphNamed("partItemList"); }
      },

      previews: {
        after: ["partItemList"],
        set(previewMorphs) {
          this.setProperty("previews", previewMorphs);
          this.partItemList.submorphs = previewMorphs;
        }
      }
    }
  }

  get isPartsBin() { return true }

  reset() {
    this.layout = null;
    this.clipMode = "hidden";
    this.partItemList.layout = new TilingLayout({spacing: 10})
    this.partItemList.fill = null
    this.partItemList.clipMode = "auto";
    connect(this, 'partsbinFolder', this, 'update');

    this.getSubmorphNamed("reload-button");

    connect(this.getSubmorphNamed("search-input"), 'inputChanged', this, 'filterItemsDebounced', {
      converter: function() { return this.sourceObj.input; }
    });
  }

  beforePublish() {
    this.clear();
  }

  onLoad(snapshot, rev) {
    this.update();
  }

  clear() {
    this.previews = [];
  }

  async update() {
    // await this.update()

    this.clear();

    let {partsbinFolder} = this,
        partResorces = await getAllPartResources({partsbinFolder}),
        previewProto = await loadObjectFromPartsbinFolder("part-item-for-partsbin", {partsbinFolder});

    for (let partResource of partResorces) {
      let preview = previewProto.copy();
      this.previews = [...this.previews, preview];
      this.showPart(partResource.url, preview);
    }
  }

  async showPart(partURL, previewMorph) {
    // let partURL = "http://localhost:9011/lively.morphic/parts/ChatWidget.json"
    // await this.showPart(partURL)

    if (!previewMorph) {
      previewMorph = this.previews.find(ea =>
        ea.partResource && ea.partResource.url === partURL);
    }

    if (!previewMorph) {
      let {partsbinFolder} = this;
      previewMorph = await loadObjectFromPartsbinFolder("part-item-for-partsbin", {partsbinFolder});
      this.previews = [...this.previews, previewMorph];
    }

    return previewMorph.showPart(resource(partURL));
  }

  async onPartPublished({url, partName}) {
    await this.showPart(url);
    this.setStatusMessage("updating part " + url);
  }

  filterItemsDebounced(queryString) {
    fun.debounceNamed(this.id + "-filterItemsDebounced", 100,
      queryString => this.filterItems(queryString))(queryString);
  }

  filterItems(queryString = "") {
    queryString = queryString.toLowerCase();
    this.partItemList.layout.disable();
    let [matchingParts, fadingParts] = queryString ?
      arr.partition(this.previews, ea => ea.partName.toLowerCase().includes(queryString)) :
      [this.previews, []];
    matchingParts.forEach(p => {
       let reappearing = !p.owner;
       this.partItemList.addMorph(p);
       if (reappearing) { 
         p.animate({opacity: 1, duration: 300});
       }
    });
    fadingParts.forEach(p => { 
        p.owner && (async () => {
           let pos = p.globalPosition;
           this.addMorph(p);
           p.globalPosition = pos;
           await p.animate({opacity: 0, duration: 300});
           p.remove();
        })();
    })
    this.partItemList.layout.enable({duration: 300});
  }

}

