import { Morph, morph, Text } from "lively.morphic";
import { connect, disconnectAll, disconnect, signal } from "lively.bindings/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { pt, Color } from "lively.graphics/index.js";
import { hashCode } from "lively.lang/string.js";
import { serialize, deserialize } from "lively.serializer2/index.js";
import { date, arr } from "lively.lang/index.js";
import { select } from "lively.lang/object.js";
import { getUser } from "lively.user/index.js";
import { TopicData } from "lively.2lively/discussion.js";
import L2LClient from "lively.2lively/client.js";
import { zip } from "lively.lang/array.js";


export default class Topic extends Morph {

  static get properties() {
    return {
      comments: { defaultValue: [] },
      isExpanded: {defaultValue: true},
      date: {
        after: ['submorphs'],
        get() { return this.get('date').textString },
        set(t) {
          this.get('date').textString = date.format(t, 'h:MM TT, mmmm d yyyy');
        }
      },
      title: {
        after: ['submorphs'],
        set(t) {
          this._title = t;
          this.get('topic name').textString = t;
          this.get('hover bar').get('topic name').textString = t;
        },
        get() { return this._title },
        defaultValue: 'Topic Name'},
      author: {
        after: ['submorphs'],
        defaultValue: {
          name: 'Lively Kernel',
          avatar: morph({type: 'image', extent: pt(130,130)})
        },
        get() {
          return this._author;
        },
        set(author) {
          this._author = author;
          author && this.updateAuthorView()
        }
      },
      user: {
        after: ['author', 'submorphs'],
        defaultValue: {
          name: 'Lively Kernel',
          avatar: {type: 'image'}
        },
        set(u) {
          this._user = u;
          this.comments.forEach(c => c.user = u);
          this.ensureEditPrivileges();
        },
        get() {
          return this._user;
        }
      }
    }
  }

  get __only_serialize__() {
    return [...super.__only_serialize__, 'haloShadow']
  }

  ensureEditPrivileges() {
    this.user.name == this.author.name ? this.showButtons('edit button') : this.hideButtons('edit button');
    this.get('controls').layout.apply();
  }

  relayout() {
    if (!this.submorphs.length) return;
    this.get('reply hover bar').bottomLeft = this.innerBounds().bottomLeft().addXY(0, -15);
    this.get('spacer').width = this.width - 12;
    this.get('topic head').width = this.width;
    this.get('topic head').height = this.get('content').textBounds().height + 100;
    this.get('hover bar').width = this.width;
    this.get('reply hover bar').width = this.width;
    this.comments.forEach(c => c.width = this.width - 30);
  } 

  updateAuthorView() {
    const authorAvatar = this.get('author avatar'),
          authorName = this.get('author name'),
          titleBar = this.get('title bar');
    authorName.textString = this.author.name;
    authorAvatar.submorphs = [this.author.avatar.copy()]
    authorAvatar.submorphs[0].position = pt(0,0);
    titleBar.layout.col(3).width = this.get('author info').bounds().width;
    this.relayout();
  }

  async getUserObject(email) {
     let user = await getUser({email});
     user.avatar = deserialize(JSON.parse(user.avatar));
     return user;
  }

  async renderTopic(user, {hash, title, content, author, date, comments}, users = {}) {
    this.hash = hash;
    this.setContent(deserialize(content));
    this.title = title;
    this.author = users[author] || await this.getUserObject(author)
    this.date = date;
    await this.patchComments(comments, user, users);
    this.user = user;
    return this;
  }

  async patchComments(comments, user, users) {
    let comment = await loadObjectFromPartsbinFolder('Comment');
    if (comments) {
      for (var [commentData, commentMorph] of zip(comments, this.comments)) {
        if (commentMorph) {
          commentMorph.renderComment(user, commentData);
        } else {
          this.registerComment(await comment.copy().renderComment(user, commentData, users));
        }
      }
    }
  }  

  getData() {
    return new TopicData({
      tracker: L2LClient.default(),
      author: this.author.email,
      user: this.user,
      content: this.serializedContent || serialize(this.getContent()),
      title: this.title,
      date: this.date,
      hash: this.hash,
      comments: this.comments.map(r => r.getData())
    })
  }

  getContent() {
    disconnect(this.get('content'), 'extent', this, 'relayout');
    const content = this.get('content').copy()
    connect(this.get('content'), 'extent', this, 'relayout');
    return content;
  }

  setContent(textMorph) {
    var content = this.get('content');
    if (content) {
      content.remove();
      disconnect(content, 'extent', this, 'relayout');
    }
    this.serializedContent = serialize(textMorph);
    content = textMorph;
    content.name = 'content';
    this.get('topic head').addMorph(content);
    this.get('topic head').layout.col(0).row(1).group.morph = content;
    connect(content, 'extent', this, 'relayout');
  }

  showHoverBar() {
    const bar = this.get('hover bar'),
          replyBar = this.get('reply hover bar');
    if (!replyBar.visible)
      replyBar.animate({
        opacity: 1, visible: true, duration: 300
      });
    if (!bar.visible) 
      bar.animate({
        opacity: 1, visible: true,
        position: pt(0,0), duration: 300
      });
  }

  hideHoverBar() {
    const bar = this.get('hover bar'),
          replyBar = this.get('reply hover bar');
    if (replyBar.visible)
      replyBar.animate({
        opacity: 0, visible: false, duration: 300
      });
    if (bar.visible) {
      bar.animate({
        visible: false,
        opacity: 0, 
        bottomLeft: pt(0,0), 
        duration: 300
      });
    }
  }

  async open() {
    await this.pull();
    signal(this, 'expanding', this);
    const title = this.get('title bar'),
          backButton = this.get('back button');
    title.layout.disable()
    title.layout.col(0).width = 40;
    title.layout.col(0).row(0).group.morph = backButton;
    title.layout.enable({duration: 300})
    backButton.animate({opacity: 1, visible: true, duration: 300});
    await this.animate({
      width: this.owner.width + 15,
      borderRadius: 0,
      duration: 300
    })
    this.get('scroll pane').clipMode = 'scroll';
  }

  async collapse() {
    const title = this.get('title bar'),
          scrollPane = this.get('scroll pane');
    this.tmpContent && this.cancelEdit();
    this.get('hover bar').visible = false;
    this.get('reply hover bar').visible = false;
    this.hideHoverBar();
    scrollPane.scroll = pt(0,0);
    scrollPane.clipMode = 'hidden';
    scrollPane.animate({height: 42, duration: 300});
    this.animate({
      height: 42, borderRadius: 5,
      width: this.owner.width - 10,
      duration: 300
    })
    signal(this, 'collapsing', this);
    this.get('back button').animate({opacity: 0, visible: false, duration: 300});
    title.layout.disable()
    title.layout.col(0).width = 0;
    title.layout.col(0).row(0).group.morph = null;
    title.layout.enable({duration: 300})
  }

  registerComment(comment) {
    this.comments.push(comment.post(arr.last(this.comments) || this));
    if (comment.owner != this.get('comments container'))
      this.get('comments container').addMorph(comment, this.get('spacer'));
    this.relayout();
  }

  async addComment() {
    var comment = await loadObjectFromPartsbinFolder('Comment');
    this.get('comments container').addMorph(comment, this.get('spacer'));
    comment.date = new Date();
    comment.width = this.width - 30;
    comment.user = comment.author = this.user;
    if (!this.fullContainsWorldPoint(comment.globalBounds().bottomLeft())) 
      this.get('scroll pane').scroll = pt(0, this.height);
    try {
      await comment.prepareToPost();
      this.registerComment(comment);
      this.push();
    } catch(err) {
      console.log(err);
      comment.remove();
    }
  }

  enableEdit() {
    this.tmpContent = this.getContent();
    this.get('title bar').layout.col(1).width = 0;
    this.hideButtons('edit button', 'topic name');
    this.showButtons('cancel edit', 'save changes', 'topic name editor holder');
    this.get('topic name editor').textString = this.title;
    this.get('topic name editor holder').center = this.get('topic name').center.addXY(-1,-1);
    this.get('controls').layout.apply({duration: 300});
    this.get('content').animate({dropShadow: this.haloShadow, readOnly: false, duration: 300});
  }

  cancelEdit() {
    this.disableEdit();
    this.setContent(this.tmpContent);
    this.tmpContent = null;
  }

  saveChanges() {
    this.disableEdit();
    this.title = this.get('topic name editor').textString;
    this.serializedContent = serialize(this.getContent());
    this.tmpContent = null;
    this.push();
  }

  async disableEdit() {
    this.showButtons('edit button', 'topic name')
    this.hideButtons('cancel edit', 'save changes', 'topic name editor holder');
    this.get('controls').layout.apply({duration: 300});
    await this.get('content').animate({
       dropShadow: false, 
       readOnly: true, 
       duration: 300});
  }

  hideComments() {
    this.borderRadius = 5; 
    this.get('scroll pane').clipMode = 'hidden';
    connect(this.get('topic head'), 'extent', this, 'extent')
    this.height = this.get('topic head').height;
  }

  showComments() {
    this.borderRadius = 0;
    this.get('scroll pane').clipMode = 'scroll';
    disconnect(this.get('topic head'), 'extent', this, 'extent');
    this.height = 400;
  }

  prepareForCreation() {
    this.hideComments();
    return new Promise((resolve, reject) => {
      this.hideButtons('edit button', 'comment button', 'back button', 
                       'date', 'cancel edit', 'save changes',
                       'topic name');
      this.get('title bar').layout.col(0).width = 0;
      this.showButtons('create topic', 'discard draft', 'topic name editor holder');
      this.get('topic name editor holder').position = pt(0,0);
      this.get('create topic').action = resolve;
      this.get('discard draft').action = reject;
      this.get('controls').layout.apply();
      this.get('content').animate({dropShadow: this.haloShadow, readOnly: false, duration: 300});
    })
  }

  async push() {
    if (this.hash) {
      await this.getData().pushUpdate();
    } else {
      this.hash = await this.getData().create();
    }
  }

  async pull() {
    // fixme: This should alter the view in place
    let topic = this.getData();
    if (await topic.pullUpdate()) {
       this.renderTopic(this.user, topic);
    } else {
      throw Error('Could not fetch topic ', this.hash);
    }
  }

  async create() {
    this.showComments();
    this.get('create topic').action = null;
    this.get('discard draft').action = null;
    this.title = this.get('topic name editor').textString;
    this.hideButtons('create topic', 'discard draft', 'topic name editor holder');
    this.showButtons('edit button', 'comment button', 'date', 'topic name', 'back button');
    this.get('title bar').layout.disable()
    this.get('title bar').layout.col(0).width = 40;
    this.get('title bar').layout.enable({duration: 300})
    this.get('controls').layout.apply({duration: 300});
    (async () => { 
      await this.get('content').animate({
        dropShadow: false, readOnly: true, duration: 300
      })
      this.get('title bar').layout.col(1).width = 250;
    })()
    this.collapse()
    return this;
  }

  showButtons(...toBeShown) {
    toBeShown.forEach(name => {
      this.get(name).animate({opacity: 1, visible: true, isLayoutable: true, duration: 300});
    });
  }

  hideButtons(...toBeHidden) {
    toBeHidden.forEach(name => {
      this.get(name).animate({opacity: 0, visible: false, isLayoutable: false, duration: 300});
    })
  }
}

