class Pulser extends Morph {

  get __only_serialize__() {
    return [...super.__only_serialize__, 'high', 'low'];
  }

  async pulse() {
     await this.animate({fill: this.high, easing: Power4.easeOut})
     await this.animate({fill: this.low, easing: Power2.easeIn})
  }

  startPulsing() {
    this.visible = true;
    this.startStepping(2500, 'pulse')
  }

  stopPulsing() {
    this.visible = false;
    this.stopStepping();
  }
}
