import { Morph, Text } from "lively.morphic";
import { connect, signal } from "lively.bindings/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";
import { pt } from "lively.graphics/index.js";

export default class Topic extends Morph {

  static get properties() {
    return {
      comments: { defaultValue: [] }
    }
  }

  relayout() {
    this.get('reply hover bar').bottomLeft = this.innerBounds().bottomLeft().addXY(0, -15);
    this.get('spacer').width = this.width - 12;
    this.get('topic head').width = this.width;
    this.get('hover bar').width = this.width;
    this.get('reply hover bar').width = this.width;
  }

  showHoverBar() {
    const bar = this.get('hover bar'),
          replyBar = this.get('reply hover bar');
    if (!replyBar.visible)
      replyBar.animate({
        opacity: 1, visible: true, duration: 300
      });
    if (!bar.visible) 
      bar.animate({
        opacity: 1, visible: true,
        position: pt(0,0), duration: 300
      });
  }

  hideHoverBar() {
    const bar = this.get('hover bar'),
          replyBar = this.get('reply hover bar');
    if (replyBar.visible)
      replyBar.animate({
        opacity: 0, visible: false, duration: 300
      });
    if (bar.visible) {
      bar.animate({
        visible: false,
        opacity: 0, 
        bottomLeft: pt(0,0), 
        duration: 300
      });
    }
  }

  async open() {
    const title = this.get('title bar'),
          backButton = this.get('back button');
    title.layout.disable()
    title.layout.col(0).width = 40;
    title.layout.col(0).row(0).group.morph = backButton;
    title.layout.enable({duration: 300})
    backButton.animate({opacity: 1, visible: true, duration: 300});
    await this.animate({
      height: Math.min(this.owner.height, 500) + 30,
      borderRadius: 0,
      duration: 300
    })
    this.get('scroll pane').clipMode = 'scroll';
  }

  async collapse() {
    const title = this.get('title bar'),
          scrollPane = this.get('scroll pane');
    this.get('hover bar').visible = false;
    this.get('reply hover bar').visible = false;
    this.hideHoverBar();
    scrollPane.scroll = pt(0,0);
    scrollPane.clipMode = 'hidden';
    this.animate({
      height: 42, borderRadius: 5,
      duration: 300
    })
    this.get('back button').animate({opacity: 0, visible: false, duration: 300});
    title.layout.disable()
    title.layout.col(0).width = 0;
    title.layout.col(0).row(0).group.morph = null;
    title.layout.enable({duration: 300})
  }

  registerComment(comment) {
    this.comments.push(comment.post());
  }

  async addComment() {
    var comment = await loadObjectFromPartsbinFolder('Comment');
    this.get('comments container').addMorph(comment, this.get('spacer'));
    comment.setContent(new Text({textString: 'Write something...', padding: 5}));
    comment.date = new Date();
    this.get('scroll pane').scroll = pt(0, this.height);
    try {
      await comment.prepareToPost();
      this.registerComment(comment);
    } catch(err) {
      console.log(err);
      comment.remove();
    }
  }
}

