import { Morph } from "lively.morphic";
import { signal } from "lively.bindings/index.js";

export default class Scroll_pane extends Morph {
  onScroll() {
    if (this.scroll.y > 30) {
      signal(this, "show hover bar");
    } else {
      signal(this, "hide hover bar");
    }
  }
}
