import { Morph, Text } from "lively.morphic";
import { connect, disconnect } from "lively.bindings/index.js";
import { arr, date } from "lively.lang/index.js";
import { loadObjectFromPartsbinFolder } from "lively.morphic/partsbin.js";

export default class Comment extends Morph {

  static get properties() {
    return {
      replies: { defaultValue: [] },
      date: {
        after: ['submorphs'],
        get() { return this.get('date').textString },
        set(t) {
          this.get('date').textString = date.format(t, 'mmmm d yyyy');
        }
      }
    }
  }

  relayout() {
    const replies = this.get('reply container'),
          content = this.get('content');
    this.height = content.textBounds().height + 100
    replies.position = this.innerBounds().bottomLeft()
    replies.width = this.width;
  }

  setContent(textMorph) {
    var content = this.get('content');
    if (content) {
      content.remove();
      disconnect(content, 'extent', this, 'relayout');
    }
    content = textMorph;
    content.name = 'content';
    this.addMorph(content);
    this.layout.row(1).col(0).group.morph = content;
    connect(content, 'extent', this, 'relayout');
  }

  async addReply() {
    var reply;
    this.replies.push(
      this.get('reply container').addMorph(
        reply = await loadObjectFromPartsbinFolder('Reply')));
    reply.setContent(new Text({textString: "Enter text...", padding: 5}));
    reply.date = new Date();
    this.showReplies();
  }

  toggleReplies() {
    this.repliesVisible ? this.hideReplies() : this.showReplies();
  }

  showReplies() {
    const replies = this.get('reply container');
    this.repliesVisible = true;
    replies.animate({
      height: replies.submorphBounds().height + 10,
      opacity: 1, duration: 300
    });
  }

  hideReplies() {
    this.repliesVisible = false;
    this.get('reply container').animate({height: 0, opacity: 0, duration: 300});
  }

  enableEdit() {
    this.get('edit button').animate({opacity: 0, visible: false, isLayoutable: false, duration: 300});
    this.get('cancel edit').animate({opacity: 1, visible: true, isLayoutable: true, duration: 300});
    this.get('save changes').animate({opacity: 1, visible: true, isLayoutable: true, duration: 300});
    this.get('controls').layout.apply({duration: 300});
  }

  cancelEdit() {
    this.disableEdit();
    // TODO: also discard changes
  }

  disableEdit() {
    this.get('edit button').animate({opacity: 1, visible: true, isLayoutable: true, duration: 300});
    this.get('cancel edit').animate({opacity: 0, visible: false, isLayoutable: false, duration: 300});
    this.get('save changes').animate({opacity: 0, visible: false, isLayoutable: false, duration: 300});
    this.get('controls').layout.apply({duration: 300});
  }
}

