import { Morph } from "lively.morphic/morph.js";
import { connect } from "lively.bindings/index.js";
import { doGrep } from "lively.morphic/ide/shell/shell-interface.js";
import { show } from "lively.morphic/index.js";
import { fun, arr } from "lively.lang/index.js";
import Terminal from "lively.morphic/ide/shell/terminal.js";
import LoadingIndicator from "lively.morphic/components/loading-indicator.js";

export default class GrepSearch extends Morph {

  static get properties() {
    return {
      grepCommand: {
        serialize: false
      },

      lastGrepSearchTerm: {
        serialize: false
      },

      loadingIndicator: {},

      searchQueryInputLine: {
        derived: true, readOnly: true,
        get() { return this.getSubmorphNamed("grep-list").inputMorph; }
      },

      basedirInputLine: {
        derived: true, readOnly: true,
        get() { return this.getSubmorphNamed("basedir-input-line"); }
      },

      searchTerm: {
        derived: true,
        get() { return this.searchQueryInputLine.input; },
        set(x) { this.searchQueryInputLine.input = x; }
      },

      basedir: {
        derived: true,
        get() { return this.basedirInputLine.input; },
        set(x) { this.basedirInputLine.input = x; }
      }

    }
  }

  reset() {
    // this.get("grep-list").historyId = "grep-searcher-term-hist"
    // this.basedirInputLine.historyId = "grep-searcher-basedir-hist";
    // connect(this.searchQueryInputLine, 'inputChanged', this, 'onSearchQueryChanged');
    // this.get("grep-list").filterFunction = `(parsedInput, item) => this.owner.filterGrepResult(parsedInput, item)`;
    // connect(this.get("grep-list"), 'filterChanged', this, 'onSearchTermInputChanged');
    // connect(this.get("grep-list"), 'accepted', this, 'openGrepResult', {converter: acceptStatus => acceptStatus ? acceptStatus.selected[0] : null});

    // this.get("grep-list").fontFamily = "Monaco, monospace"
    // this.get("grep-list").fontSize = 10
    this.get("grep-list").items = [];
    this.basedir = "";
    this.searchTerm = "";
  }

  onLoad() {
    this.basedir = arr.last(this.basedirInputLine.inputHistory.items) || "";
  }

  filterGrepResult({lowercasedTokens}, item) {
    let tokens = lowercasedTokens.slice(1); // without grep term;
    return tokens.every(token => item.string.toLowerCase().includes(token));
  }

  onSearchTermInputChanged({parsedInput: {tokens, lowercasedTokens}, items}) {
    let grepSearchTerm = tokens[0] || "";
    if (grepSearchTerm.length <= 2) return;
    fun.debounceNamed(`${this.id}-+onSearchQueryChanged`, 600, grepSearchTerm => {
      this.onSearchQueryChanged(grepSearchTerm);
    })(grepSearchTerm);
  }

  async onSearchQueryChanged(grepSearchTerm) {
    let {grepCommand, basedir, lastGrepSearchTerm} = this;

    if (grepSearchTerm.length <= 2) return;

    if (grepCommand) {
      if (grepCommand.searchTerm === grepSearchTerm) return; // nothing changed
      if (grepCommand.isRunning()) {
        this.setStatusMessage("Stopping current grep command...");
        grepCommand.kill("KILL");
        this.grepCommand = null;
      }
    } else if (lastGrepSearchTerm === grepSearchTerm) {
      return;
    }

    if (!this.loadingIndicator || !this.loadingIndicator.world()) {
      let i = this.loadingIndicator = LoadingIndicator.open("searching");
      i.center = this.globalBounds().center();
    }

    let cmd;
    try {
      cmd = this.grepCommand = doGrep(grepSearchTerm, basedir);
    } catch (err) {
      this.showError(err);
      return;
    }

    this.lastGrepSearchTerm = cmd.grepSearchTerm = grepSearchTerm;

    let {exitCode, results, stderr} = await cmd.whenDone();

    if (cmd !== this.grepCommand) return; // old, stopped command
    this.grepCommand = null;

    if (this.loadingIndicator) {
      this.loadingIndicator.remove();
      this.loadingIndicator = null;
    }

    if (exitCode && exitCode !== 1) {
      if (stderr) this.showError(stderr);
      this.getSubmorphNamed("grep-list").items = []; // no results;
      return;
    }

    this.getSubmorphNamed("grep-list").items = results.map(ea => {
      let {filename, lineno, match} = ea;
      if (filename.startsWith(basedir)) filename = filename.slice(basedir.length);
      filename = filename.replace(/^\.?\//, "");

      let matched = [match, null],
          searchTermInMatch = match.toLowerCase().indexOf(grepSearchTerm.toLowerCase());
      if (searchTermInMatch > -1) {
        let from = searchTermInMatch, to = from + grepSearchTerm.length;
        matched = [
          match.slice(0, from), null,
          match.slice(from, to), {backgroundColor: "orange"},
          match.slice(to), null];
      }

      return {
        isListItem: true,
        label: [`${filename}:${lineno}`, {fontSize: 11}, `\t`, null, ...matched],
        value: ea
      }
    });

    this.getSubmorphNamed("grep-list").updateFilter();
  }

  openGrepResult(choice) {
    if (!choice) return;
    let {filename, lineno, match} = choice;
    return this.world().execCommand("open file", {
      url: filename,
      lineNumber: lineno,
      reuse: true
    });
  }

  focus() {
    this.get("grep-list").focus();
  }

  get commands() {
    return [
      {
        name: "[grep searcher] focus basedir input",
        exec: () => {
          this.basedirInputLine.show();
          return this.basedirInputLine.focus();
        }
      },

      {
        name: "[grep searcher] focus filter list",
        exec: () => {
          this.getSubmorphNamed("grep-list").show();
          return this.getSubmorphNamed("grep-list").focus();
        }
      },

      {
        name: "[grep searcher] stop current command",
        exec: () => {
          if (!this.grepCommand || !this.grepCommand.isRunning) {
            this.setStatusMessage("No command running");
            this.getSubmorphNamed("grep-list").items = [];
            return true;
          }
          this.setStatusMessage("Stopping grep command...");
          this.grepCommand.kill("KILL");
          return true;
        }
      },

      {
        name: "[grep searcher] open current grep command in terminal",
        exec: () => {
          if (!this.grepCommand || !this.grepCommand.isRunning) {
            this.setStatusMessage("No command running");
            return true;
          }
          return Terminal.forCommand(this.grepCommand)
        }
      }
    ]
  }

  get keybindings() {
    // delete this._cachedKeyhandlers
    return [
      {keys: "F1|Alt-Up", command: "[grep searcher] focus basedir input"},
      {keys: "F2|Alt-Down", command: "[grep searcher] focus filter list"},
      {keys: "Escape|Ctrl-G", command: "[grep searcher] stop current command"},
      {keys: "Meta-Shift-O", command: "[grep searcher] open current grep command in terminal"},
    ]
  }
}
