import { Morph, Image, morph, Icon, HTMLMorph } from "lively.morphic";
import { Color, pt } from "lively.graphics/index.js";
import { connect, once, disconnect } from "lively.bindings/index.js";
import { resource } from "lively.resources/index.js";
import { loadMorphFromSnapshot } from "lively.morphic/serialization.js";
import LoadingIndicator from "lively.morphic/components/loading-indicator.js";

export default class Part_dash_item_dash_for_dash_partsbin extends Morph {

  static get properties() {
    return {
      partName: {},
      partResource: {},
      snapshot: {}
    }
  }

  reset() {
    connect(this.getSubmorphNamed("remove-button"), 'fire', this, 'interactivelyRemovePart');
  }

  beforePublish() {
    this.clear()
  }

  clear() {
    this.snapshot = null;
    this.partResource = null;
    this.partName = "";
    let label = this.getSubmorphNamed('preview-label'),
        preview = this.getSubmorphNamed('html-preview'),
        imagePreview = this.getSubmorphNamed('image-preview');
    label && label.remove();
    imagePreview && imagePreview.remove();
    if (preview)
      preview.html = `
  <div style="display: flex;
              align-items: center;
              justify-content: center;
              height: 100%;
              background: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(0%, rgba(242,243,244,1)),color-stop(100%, rgba(229,231,233,1)))">
    <p style="font: bold 20pt Inconsolata, monospace; color: lightgray;">Loading</p>
  </div>`;
  }

  async showPart(partResource) {
    // partResource = resource(System.decanonicalize("lively.morphic/")).join("parts/star.json")
    // partResource = resource(System.decanonicalize("lively.morphic/")).join("parts/button.json")
    // await this.showPart(partResource)
    this.clear();
    this.partName = partResource.name().replace(/\.json$/, "");
    this.snapshot = JSON.parse(await partResource.read());
    this.partResource = partResource;
    this.addPreviewMorph();
    this.addLabel();
    this.getSubmorphNamed("remove-button").visible = false;
  }

  addPreviewMorph() {
    let {snapshot: {preview}} = this;
    if (typeof preview !== "string" || !preview) preview = "no preview";
    return preview.startsWith("data:image/") ?
      this.addImagePreviewMorph(preview) :
      this.addHTMLPreviewMorph(preview);
  }

  addImagePreviewMorph(dataURI) {
    let {extent, partName} = this,
        margin = 10,
        {x: w, y: h} = extent,
        edgeLength = Math.min(w-2*margin, h-2*margin),
        borderWidth = 1,
        previewSpec = {
          name: "image-preview",
          fill: null,
          reactsToPointer: false,
          imageUrl: dataURI,
          extent: pt(edgeLength, edgeLength),
          topCenter: pt(w/2, margin),
          nativeCursor: "-webkit-grab"
        },
        htmlPreview = this.getSubmorphNamed("html-preview"),
        preview = htmlPreview ? htmlPreview.replaceWith(new Image()) : this.addMorphBack(new Image());
    Object.assign(preview, previewSpec);
    return preview;
  }

  addHTMLPreviewMorph(html) {
    let {extent, partName} = this,
        margin = 10,
        previewWidth = 100+2*margin,
        previewHeight = 100+2*margin,
        {x: w, y: h} = extent,
        borderWidth = 1,
        previewHTML = `<div style="width: 100%;">`
                    + `<div style="position: absolute; top: ${margin}px;`
                    + `            left: ${margin + (w-previewWidth) / 2}px">`
                    + html
                    + `</div>`,
      previewSpec = {
        name: "html-preview",
        fill: null,
        reactsToPointer: false,
        html: previewHTML,
        extent: pt(w, h),
        // border: {width: 1, color: Color.gray, radius: 5},
        nativeCursor: "-webkit-grab"
      };
    var preview = this.getSubmorphNamed("html-preview") || this.addMorphBack(new HTMLMorph());
    Object.assign(preview, previewSpec);
    preview.domNode.style.pointerEvents = "none";
    return preview;
  }

  addLabel() {
    let {partName, width, height} = this
    if (this.getSubmorphNamed('preview-label'))
      this.getSubmorphNamed('preview-label').remove();
    this.addMorph({
      bottomCenter: pt(width/2, height-5),
      type: 'label', fontSize: 12, fontColor: Color.gray.darker(),
      fill: Color.gray.withA(.7), borderRadius: 10, padding: 4,
      value: partName, name: 'preview-label'
    });
  }

  onDragStart(evt) {
    this.createAndGrabNewInstance();
  }

  async createAndGrabNewInstance() {
    // FIXME
    var hand = this.world().firstHand;
    this.draggable = false;
    this.env.eventDispatcher.eventState.draggedMorph = null;
    try {
      let {snapshot} = this,
          preview = this.getSubmorphNamed('html-preview') || this.getSubmorphNamed('image-preview'),
          newMorph = preview.copy(),
          positionInHand = hand.localize(preview.globalPosition);
      hand.grab(newMorph);
      newMorph.dropShadow = false;
      newMorph.origin = newMorph.extent.scaleBy(.5);
      newMorph.topLeft = positionInHand;
      if (!preview.isImage) {
        var previewDiv = preview.domNode.childNodes[0].childNodes[0].childNodes[0],
            scale = 1 / (previewDiv.getBoundingClientRect().width / previewDiv.offsetWidth)
      } else var scale = 1;
      newMorph.animate({scale, duration: 400});
      newMorph.loadPart = async () => {
         newMorph.addMorph({
           fill: Color.black.withA(.7), scale: 1 / scale,
           extent: pt(100,100), borderRadius: 5,
           submorphs: [
             Object.assign(Icon.makeLabel('refresh'), {
              extent: pt(60,60),
              padding: 10,
              fontSize: 60,
              fill: Color.transparent,
              fontColor: Color.white,
             }),
             {
               type: 'label', value: 'Loading Part...',
               fontColor: Color.white, fontSize: 13,
               bottomCenter: pt(50,95)
             }
           ]
         }).center = pt(0,0);
         let part = await loadMorphFromSnapshot(snapshot);
         newMorph.owner.addMorph(part)
         part.center = newMorph.center;
         newMorph.remove();
      };
      once(newMorph, 'remove', newMorph, 'loadPart');
    } catch (e) {
      this.showError(e)
    } finally {
      this.draggable = true;
    }
  }

  async interactivelyRemovePart() {
    let {partName, partResource} = this,
        really = await this.world().confirm(`Do you really want to remove part ${partName}?`);
    if (!really) return;
    await partResource.remove();
    if (this.owner.isPartsBin) { this.remove(); }
  }

  onHoverIn(evt) {
    let btn = this.getSubmorphNamed("remove-button");
    btn.bringToFront();
    btn.visible = true;
  }

  onHoverOut(evt) {
    let btn = this.getSubmorphNamed("remove-button");
    btn.visible = false;
  }
}
